css:
@-moz-document url-prefix("https://play.google.com/books/reader"), url-prefix("https://books.googleusercontent.com/") {
body,
.gb-page-wrapper-body,
.gb-reader-container {
  background-color: #222;
  color: #eee;
}


/* Reader */
.gb-text-reader {
  margin-bottom: -20px;
  padding-top: 20px;
}
.gb-segment span.underline {
  border-bottom-color: #eee;
}





/* Selection Menu */
.gb-selection-menu.gb-reader-shadow {
  background-color: #222;
}
.gb-selection-menu-item {
  color: #aaa;
}
.gb-selection-menu-item-highlight {
  color: #fff;
}
.gb-selection-menu-separator {
  border-bottom: 1px solid #666;
  margin: 3px 0;
}





/* Top Bar */
.gb-topbar {
  background: #333;
  border-bottom: 1px solid #444;
  padding: 0;
}

.gb-topbar-table td {
  vertical-align: middle;
}
.gb-topbar-button {
  transition: color 0.1s ease-out;
}
.gb-topbar-button,
.gb-topbar-button:focus {
  color: #666;
}
.gb-topbar-button:hover {
  color: #fff;
}
.gb-topbar-button.gb-topbar-button-open {
  color: #fff;
}





/* Status Bar */
.gb-statusbar {
  background: #333;
  border-top: 1px solid #444;
}

.gb-pagecontrol-input {
  color: #999;
}

.gb-statusbar-page-input {
  color: #666;
}
.gb-statusbar-controls-table {
  display: none;
}
.gb-statusbar:hover .gb-slider-segment {
  background-color: #0277bd;
}

.gb-pagination-controls {
  color: #aaa;
}
.gb-pagination-controls-hover {
  background-color: #666;
  color: #fff;
}

.gb-slider-segment {
  background-color: #666;
}
.gb-slider-indicator.SPRITE_slider_indicator {
  background: #ddd;
  border-radius: 50%;
  border: 1px solid #000;
  margin-top: -1px;
}
.gb-slider-tooltip-body {
  background-color: #333;
  color: #ccc;
  border: 1px solid #666;
  border-radius: 6px;
  box-shadow: 0 1px 8px 1px #aaa;
}
.gb-slider-tooltip-pointer-outer {
  border-color: #666 transparent transparent transparent;
}
.gb-slider-tooltip-pointer-inner {
  border-color: #333 transparent transparent transparent;
}





/* Sidepanel */
.gb-reader-shadow {
  background-color: #222;
  box-shadow: 0 1px 8px 1px #aaaa;
  border-radius: 6px;
}

.gb-reader-shadow.gb-sidebar-popup {
  background-color: #333;
  color: #ccc;
}

.gb-sidepanel-close.gb-sidepanel-close-hover {
  filter: invert(1);
}

.gb-sidepanel-header {
  background-color: #333;
  margin: 0;
  padding: 1rem 1rem 0;
}

.gb-sidepanel-title {
  font-size: 1.25rem;
  font-weight: 400;
  margin: 0;
  padding: 0 0 0.5rem;
}

.gb-sidepanel-content {
  margin: 1rem 0;
  padding: 0 1rem;
}



/* Sidepanel - Content */

.gb-tab-bar {
  display: flex;
  flex-flow: row nowrap;
  justify-content: center;
}

.gb-tab {
  border-radius: 4px 4px 0 0;
  border-left: 1px solid #000;
  border-top: 1px solid #000;
  background-color: #444;
  color: #aaa;
  padding: 0.4rem 0.35rem;
  text-align: center;
  transition: all 0.2s ease-out;
}
.gb-tab:last-child {
  border-right: 1px solid #000;
}
.gb-tab:hover:not(.gb-tab-selected) {
  color: #fff;
  text-decoration: none;
  background-color: #666;
}

.gb-tab-separator {
  display: none;
}
.gb-tab-selected {
  background-color: #222;
  color: #fff;
}

.gb-result-snippet {
  color: #999;
}
.gb-result.gb-result-hover .gb-result-snippet {
  background-color: inherit;
  color: #eee;
}
.gb-result-hover {
  background-color: #666;
}
.gb-result.gb-result-hover .gb-result-page-cell {
  color: #fff;
}
.gb-result {
  border: 0 none;
  border-top: 1px solid #999;
  border-width: 1px 0;
  transition: all 0.2s ease-out;
}
.gb-result:first-child {
  border-top: 0 none;
}

/* Contents - Notes */
.gb-result-snippet span {
  color: #000;
}
.gb-result-note {
  color: #666;
}
.gb-result.gb-result-hover .gb-result-note {
  color: #fff;
}





/* Sidepanel - Display Options */

/* View and Typeface */
.goog-flat-menu-button,
.gb-reader-settings-menu-item-content {
  background-color: #666;
  color: #eee;
}
.gb-reader-settings-menu-item-disabled .gb-reader-settings-menu-item-content {
  background-color: #333;
  color: #666;
  cursor: not-allowed;
}

.gb-reader-settings-menu-item {
  border-top: 1px solid #aaa;
}
.gb-reader-settings-menu-item-highlight .gb-reader-settings-menu-item-content {
  background-color: #ddd;
  color: #000;
}

.gb-reader-settings-button {
  border: 1px solid #444;
}

.gb-sidepanel .gb-reader-settings-button-checked {
  border: 1px solid #fff;
}

.gb-reader-settings-button-hover {
  background-color: #eee;
  border: 1px solid #eee;
  transition: all 0.1s ease-out;
}


div[class^="SPRITE_"]:not([class^="SPRITE_icon_layout"]) {
  filter: invert(1);
}

.gb-reader-settings-button-hover div[class^="SPRITE_"]:not([class^="SPRITE_icon_layout"]) {
  filter: invert(0);
}



/* Sidepanel - Search */
/* View and Typeface */
.gb-search-input {
  background-color: #666;
  border: 1px solid #999;
  color: #eee;
}

.gb-search-controls {
  padding: 0.25rem 0 1rem;
}

.gb-search-input::placeholder {
  color: #999;
}
}
