css:
@-moz-document domain("dealabs.com") {
	/******** background-color: #263238 ********/
.nav, .navMenu-trigger:hover, .bg--main, .zIndex--fixed, .voteBar, .btn--mode-white--dark, .navMenu-trigger--active:focus, .cept-hottest-widget-position-none, .card, .threadItem, .overflow--hidden, .threadGrid, .vote-btn.vote-up, .vote-btn.vote-down, .bg--em, .navDropDown-link:focus, .notification-item--read:hover, .navDropDown-tabs-notifications-count, .notification-item:first-child, .subNavMenu--menu.subNavMenu--selected .subNavMenu-roundCounter-text
{background-color: #263238 !important;}

	/******** background-color: #37474F ********/
.tGrid-row, .popover-content, .btn--mode-boxSec, .subNavMenu-btn, .input--search, .input, .btn:disabled, .btn--disabled, .text--overlay, .vote-box, .navDropDown-head, .btn--mode-primary-inverted, .multiSelect-list
{background-color: #37474F !important;}

	/******** background-color: #455A64 ********/
.navMenu-trigger, .subNav, .bg--color-greyPanel, .btn--mode-header-login, .js-options, .navDropDown-link:hover, .notification-item--read, .notifications-content, .listingProfile, .bg--color-brandPrimaryPale
{background-color: #455A64 !important;}

	/******** background-color: #35373b ********/
.footer
{background-color: #35373b !important;}

	/******** background-color: #546E7A ********/
.subNavMenu--menu .subNavMenu-list
{background-color: #546E7A !important;}

	/******** background-color: #B0BEC5 ********/
.checkbox-box 
{background-color: #B0BEC5 !important;}
	
	
	
	
	
	/******** color: white ********/
.navMenu-label, .navMenu-link 
{color: white !important;}

	/******** color: #CFD8DC ********/
.aGrid, .card, .overflow--hidden, .thread-title, .btn--mode-boxSec, .subNavMenu, .thread-username, .border--color-borderGrey, .text--color-charcoalTint, .mute--text, .lbox--v-1, .subNavMenu--menu .subNavMenu-list, .text--color-subNavLayerText, .userProfile-title--sub, .page2-secTitle, .thread-userOptionLink, .text--color-charcoal, .text--color-greyShade, .mute--text2, .bg--color-brandPrimaryPale
{color: #CFD8DC !important;}

	/******** color: #EF5350 ********/
.text--color-price, .vote-temp--hot, .text--color-red, .bg--red 
{color: #EF5350 !important;}
	
	/******** color: #03a5c1 ********/
a.linkPlain, .userHtml a.linkPlain, .userHtml a.thread-link 
{color: #03a5cl !important;}
	
	/******** color: #263238 ********/
.text--color-white
{color: #263238 !important;}
	
	
	
	
	
	/******** border: none ********/
.subNav, .bg--main, .border--color-borderGrey, .navDropDown-link, .subNavMenu-btn, .input--search, .btn--mode-boxSec, .btn--mode-boxSec:hover, .vote-box, .input, .notification-item, .navDropDown-footer, .popover--border-navDropDownPrimary .popover-content, .boxSec-div
{border: none !important;}

	/******** border-color: #78909C ********/
.notification-item--read, .checkbox-box 
{border-color: #78909C !important;}

	/******** border-color: #455A64 ********/
.img
{border-color: #455A64 !important;}
	
	/******** border-color: #37474F ********/
.navDropDown-footer, .btn--mode-primary-inverted, .multiSelect-list, .bg--color-brandPrimaryPale
{border-color: #37474F !important;}





	/******** RANDOM ********/
.popover--menu .popover-arrow::after, .popover--border-navDropDownPrimary .popover-arrow::before
{border-bottom: 14px solid #37474F !important;}
	
.navMenu-div
{border-top-color: white !important;}

.text--overlay::before
{background-image: linear-gradient(90deg,hsla(200, 19%, 18%, 0),#37474F 90%) !important;}

.overflow--fade::after
{background: none !important;}

.vote-box
{box-shadow: none !important;}

.thread-title--list::after
{background-image: none !important;}

.popover-content
{background-color: #37474F !important; border: none !important;}

.logo--fromW2-vertical
{background-image: url(https://svgshare.com/i/99S.svg) !important; }

.subNavMenu-item--separator
{border-bottom-color: #78909C !important;}
}

@-moz-document url-prefix("https://www.dealabs.com/profile"), url-prefix("http://www.dealabs.com/profile") {
/******** background-color: #263238 ********/
.tabbedInterface-tab--selected
{background-color: #263238 !important;}
	
	/******** background-color: #455A64 ********/
.tabbedInterface-tab--selected
{background-color: #455A64 !important;}
	
	/******** background-color: #78909C ********/
.tabbedInterface-tab, .textBadge--white
{background-color: #78909C !important;}
	
	
	
	/******** color: white ********/
.tabbedInterface-tab, .textBadge--white, .tabbedInterface-tab--selected
{color: white !important;}
	
	
	
	/******** RANDOM ********/
.boxSec-div
{border-top-color: #455A64 !important;}
}

@-moz-document url-prefix("https://www.dealabs.com/black-friday"), url-prefix("http://www.dealabs.com/black-friday") {
	/******** background-color: #455A64 ********/
.listingProfile
{background-color: #455A64 !important;}
	
	
	
	/******** color: #ECEFF1 ********/
.text--b, .mute--text2
{color: #ECEFF1 !important;}
}
