css:
@-moz-document domain("coub.com") {
/* 

Main variables for the theme

*/

:root {
    --image-filter-pink: invert(100%) hue-rotate(250deg) saturate(1000%); /* equivalent to #ff00ff */
    --image-filter-hover: invert(100%) hue-rotate(200deg) saturate(1000%); /* equivalent to #f31ff3 */
    --primary-bg: #0a0a0a;
    --secondary-bg: #101010;
    --primary-color: #ffffff;
    --primary-color-hover: #525252;
    --primary-box-shadow-bg: #ff00ff;
    --primary-box-shadow-fg: #ffffff;
}

/* Load indicators */
.load-indicator.-blue svg circle {
    stroke: #545454;
}

.loadRotator>span {
    border: 2px solid #ff00ff;
}

.load-indicator.-small svg circle {
    stroke: #ff00ff;
}

/* Essentials */
body {
    background: var(--primary-bg);
    color: var(--primary-color);
}

a {
    color: var(--primary-color);
}

a:hover {
    color: #525252;
}

.-color--black {
    color: #f8f7f7;
}

.-color--white {
    color: #fff;
}

.-color--blue-ribbon {
    color: #ff00ff;
}

.-ribbon:hover, .-ribbon:hover:active {
    box-shadow: 0px 0px 5px 2px rgba(0,0,0,0.95);
    transition-duration: .2s;
    -webkit-transition-duration: .2s;
}

.header-channel__notify-badge__group .subgroup .subgroup__header .box--vertical.-right a {
    color: #5a5b5c;
}

.header-channel__notify-badge__group .subgroup .subgroup__header .box--vertical.-right a:hover {
    color: #ff00ff;
}

.header-channel__notify-badge__group .subgroup .subgroup__header h2 {
    color: var(--primary-color);
}

.dd-notification-friends .notification-list__text .friendship-notification-text__title a {
    color: var(--primary-color);
    font-weight: bold;
}

.dd-notification-friends .notification-list__text .friendship-notification-text__info.-gray {
    color: #ff00ff;
}

.-ribbon-hvr-text:hover, .-ribbon-hvr-text:hover:active {
    color: var(--primary-color-hover) !important;
}

.box-card__meta {
    background: var(--secondary-bg);
}

.channel.channel--box-card .channel__counters>a, .channel.channel--box-card .channel__counters>.middot, .channel.channel--box-card .channel__counters>span {
    color: var(--primary-color);
}

.box--banner {
    background-color: var(--secondary-bg);
}

.description__controls {
    color: var(--primary-color);   
}

.header__bg, .header.-scrolled>.header__bg {
    background: var(--secondary-bg);
}

.nav__item .nav__item__inner {
    color: var(--primary-color);
}

.coub.coub--normal-card .description__title {
    color: #ffffff;
}

.channel.channel--box-card .channel__avatar>a {
    border-radius: 0% !important;
}

.channel.channel--box-card .channel__avatar>a:hover {
    box-shadow: 0px 0px 5px 2px rgba(0,0,0,0.95);
    transition-duration: .2s;
    -webkit-transition-duration: .2s;
}

.object-media__img>a>img {
    border-radius: 0%;
}

.badge-counter {
    background: #f000;
    background: #ff00ff;
}

.header-channel__avatar:hover {
    -moz-box-shadow: 0px 0px 0px 3px #676767;
    -webkit-box-shadow: 0px 0px 0px 3px #676767;
    box-shadow: 0px 0px 0px 3px #676767;
}

.-bg-color--blue-ribbon {
    background-color: #ff00ff;
}

.wkly__header__nav.-scrolled .nav__inner {
    background-color: var(--primary-bg);
}

footer.footer .footer__nav>ul>.list__item, footer.footer .footer__nav>ul>.list__item>a {
    color: #ffffff;
}

footer.footer .footer__copyright {
    color: #989393;
}

footer.footer .footer__copyright div+div>a {
    color: #ff00ff;
}

h1 {
    color: var(--primary-color);
}

.related-tags__description {
    color: var(--primary-color);
}

.coub__recoub-block:hover .media-block__preview-image {
    box-shadow: 0px 0px 0px 3px #ff00ff;
}

.coub__recoub-block:hover .media-block__title {
    color: #545454;
}

.nav__profile {
    color: var(--primary-color);
}

.heading h1 {
    color: var(--primary-color);
}

.-color-rgba--gray {
    color: rgba(183, 180, 180, 0.6);
}

.cb-edtr__body p {
    color: var(--primary-color);
}

.-bg-color--white {
    background-color: var(--secondary-bg);
    color: var(--primary-color);
}

.back {
    color: rgba(255, 255, 255, 0.5);
}

.back:hover {
    color: #4e4e4e;
    cursor: pointer;
}

.back:hover:before {
    border-top-color: #4e4e4e;
}

.input-field:hover {
    border-color: #ff00ff;
    box-shadow: 0px 0px 0px 2px #ff00ff;
}

.input-field:focus {
    border-color: #ff00ff;
    box-shadow: 0px 0px 0px 2px #ff00ff;
}

.input-field::-webkit-input-placeholder, .input-field-group>input::-webkit-input-placeholder {
    color: rgba(255, 255, 255, 0.6);
}

input.input-field.-rn {
    background: #0a0a0a;
    color: white;
}

.coub__sharing__list.box--inline {
    display: none;
}

.coub__sharing__dropdown .sharing__dropdown__item>span, .story__sharing-dropdown .sharing__dropdown__item>span {
    color: rgb(255, 255, 255);
}

.suggest__item:hover .suggest__item__image-box {
    box-shadow: 0px 0px 0px 3px #4e4e4e;
}

.suggest__item:hover .suggest__item-stamp>.hbold {
    color: #4e4e4e;
}

.suggest__item-stamp>.-color--emperor {
    color: rgba(224, 224, 224, 0.4);
}

html:not(.mobile) .close-small:hover {
    color: #ff00ff;
}

.checkbox-field:hover>.checkbox {
    box-shadow: 0px 0px 0px 2px #ff00ff;
    border-color: #ff00ff;
}

/* Editor */

.cb-edtr__upload-type:active {
    background-color: #f0f0f00a;
}

.cb-edtr__process--initial {
    background-color: #0a0a0a;
    box-shadow: unset;
}

.cb-edtr__upload-process>p:first-of-type {
    color: #ffffff;
}

.cb-edtr__process--initial .corner:after {
    box-shadow: 0px 0px 0px 12px #101010;
}
.cb-edtr__process__upload {
    background-color: #ff00ff;
}

.cb-edtr__upload-process>p:last-of-type {
    color: rgba(204, 204, 204, 0.5);
}

.-segment-not-highlighted .cb-edtr__timeline__segment.load-state-loaded.current .segment__visible, .cb-edtr__timeline__segment.load-state-loaded.current .-segment-not-highlighted .segment__visible, .-segment-not-highlighted .cb-edtr__timeline__segment.load-state-loaded.-always-highlighted .segment__visible, .cb-edtr__timeline__segment.load-state-loaded.-always-highlighted .-segment-not-highlighted .segment__visible, .-segment-not-highlighted .cb-edtr__timeline__segment.load-state-preloaded.current .segment__visible, .cb-edtr__timeline__segment.load-state-preloaded.current .-segment-not-highlighted .segment__visible, .-segment-not-highlighted .cb-edtr__timeline__segment.load-state-loaded:hover .segment__visible, .cb-edtr__timeline__segment.load-state-loaded:hover .-segment-not-highlighted .segment__visible, .-segment-not-highlighted .cb-edtr__timeline__segment.load-state-loaded.active .segment__visible, .cb-edtr__timeline__segment.load-state-loaded.active .-segment-not-highlighted .segment__visible, .-segment-not-highlighted .cb-edtr__timeline__segment.load-state-preloaded:hover .segment__visible, .cb-edtr__timeline__segment.load-state-preloaded:hover .-segment-not-highlighted .segment__visible, .-segment-not-highlighted .cb-edtr__timeline__segment.load-state-preloaded.active .segment__visible, .cb-edtr__timeline__segment.load-state-preloaded.active .-segment-not-highlighted .segment__visible, .-segment-not-highlighted .segment-visible-hover-state {
    box-shadow: 0px 0px 0px 3px #545454;
}

.cutter__handle-dragger {
    background: #545454;
}

.sb.-blue {
    background-color: #000000;
    color: #ffffff;
    border: 1px solid #353738;
}

.cb-edtr__player-toggle-flash-html5 input[type='checkbox']:checked+label::before {
    background: #ff00ff;
}

.cb-edtr__player-embed .corner:after {
    box-shadow: 0px 0px 0px 2.5px #000000;
}

.cb-edtr__tips p {
    color: rgba(234, 230, 230, 0.5);
}

/* Dropdown */
.dropdown__content {
    background: var(--secondary-bg);
    border: 1px solid #54545426;
    border-radius: 0px;
}

.list.list--selectable .list__item:not(.disabled):hover {
    background-color: rgba(0,0,0,0.3);
    color: #ffffffa3 !important;
}

.dd-notification-list .important {
    background-color: #44414136;
    border: 1px solid #494949;
    margin-bottom: 5px;
}
.niceScroller .antiscroll-inner {
    border-radius: 0px;
}

.list {
    padding: 0;
}

.channel-menu-dropdown__content .list__item {
    background: var(--secondary-bg);
    color: var(--primary-color);
}

.channel-menu-dropdown__content .list__item:hover {
    background-color: #4e4e4e;
}

.channel-menu-dropdown__content .list__item a {
    color: var(--primary-color);
}

.page__content .featured__body h1, body.featured-stories [clientside-timeline] .page>h5 {
    color: var(--primary-color);
}

.list.list--selectable .list__item:not(.disabled):hover, .list.list--selectable .list__item:not(.disabled):hover>a {
    color: #ffffff;
}

/* Player */
.viewer.viewer--v2 .viewer__suggests {
    visibility: hidden;
}
.viewer__coubs-changer .button-prev-next.-prev {
    visibility: hidden;
}
.viewer__coubs-changer .button-prev-next.-next {
    visibility: hidden;
}
.viewer.viewer--v2.-fullscreen.-ui-state-playing:not(.-visible-controls) .viewer__fullscreen-logo,
.viewer.viewer--v2.-fullscreen.-changer-mode .viewer__fullscreen-logo {
    visibility: hidden;
}

/* Ads */

.coub.timeline-banner.active {
    display: none !important;
}

.timeline-banner {
    display: none !important;
}

/* 

All the hardcoded classes that uses the coub.com background image assets, I apply a filter on top of them that inverts, rotates the hue and saturates the image, which results in a completely new color, you can get any color you like with this, you are NOT limited to the blue coub.com has by default!

*/

.header__logo .img.white, .header__logo .img.blue, .header__nav .nav__item.explore>.nav__item__inner::before, .header__nav .nav__item.hot>.nav__item__inner::before, .header__nav .nav__item.create>.nav__item__inner::before, .header__channel .header__channel-chat-button .sb, .header-channel__notify-badge .sb.-notify-handler.-friends, .header-channel__notify-badge .sb.-notify-handler.-general, .description__controls .coub__like-button.-on i[handler], html:not(.mobile) .description__controls .coub__like-button:not([disabled]) i[handler]:hover, .description__controls .coub__recoub-button i[handler], .description__controls .coub__remix-button i[handler], .description__controls .coub__like-button i[handler], li[class~="nav__item"] > a.nav__item__inner:before, li[class~="category-item"] > i.box--vertical, .coub.coub--normal-card .description__recoub-icon, .sb.-follow.following i.follow-icon, .search-popup__recent__delete, .header__search .header-search__button, img.wkly__header__logo.image, img.image.box--vertical, .nav__menu .nav__item >.nav__item__inner:before, .coub__sharing__dropdown .dropdown__handler .sb:before, .story__sharing-dropdown .dropdown__handler .sb:before, .channel__relationships .sb.settings:before, .sharing__controls .dropdown__handler>.sb.-st.edit:after, .story__like-button i::before, .story__repost-button i::before, .cb-edtr__upload-type--external:before, .cb-edtr__upload-type--local:before, .cb-edtr__upload-type--webcam.-quarter:before {
    filter: var(--image-filter-pink);
}

/* Hover behaviour can be different */
li[class~="nav__item"]:hover a.nav__item__inner:before, .nav__menu .nav__item:hover >.nav__item__inner:before {
    filter: var(--image-filter-hover);
}

/* Buttons */

html:not(.mobile) .sb.-st:hover  {
    background-color: #ff00ff;
    color: #ffffff;
    box-shadow: 0px 0px 0px 1px #ff00ff;
}

.sb.-st.active, .sb.-st:active, html:not(.mobile) .sb.-st:hover:active, html:not(.mobile) .sb.-st:hover.active {
    background-color: #ff00ff;
    color: #ffffff;
    box-shadow: 0px 0px 0px 1px #ff00ff;
}

[blocks-stories-block] .more-stories-button {
    color: #ffffff;
}

[blocks-stories-block] .more-stories-button:hover {
    color: #545454;
}

.dropdown.dropdown--select.country-dropdown button.sb.-st.-toggle {
    background: var(--secondary-bg);
    color: var(--primary-color);
}

.sb-group.sb-group--join.-st .sb-join__btn:first-of-type .sb:hover, .sb-group.sb-group--join.-st .sb-join__btn:first-of-type .sb.active {
    box-shadow: unset;
}

.sb-group.sb-group--join.-st .sb-join__btn:last-of-type .sb {
    box-shadow: unset;
}

.sb-group.sb-group--join.-st .sb-join__btn:last-of-type .sb:hover, .sb-group.sb-group--join.-st .sb-join__btn:last-of-type .sb.active {
    box-shadow: unset;
}

.slideshow .slideshow__dots .dot.-active {
    background-color: #ff00ff;
}


.slideshow .slideshow__dots .dot {
    background-color: var(--secondary-bg);
    border: 4px solid var(--primary-bg);
}

.profile__message-button .sb {
    box-shadow: inset 0 0 0 20px var(--secondary-bg);
    border: 1px solid black;
}

.profile__message-button .sb:hover {
    box-shadow: unset;
    color: #ffffff;
    background: #353738;
    border: 1px solid black;
}

.sb.-st {
    color: var(--primary-color);
    background-color: var(--primary-bg);
    box-shadow: 0px 0px 0px 1px #353738;
}

.coub__sharing__list .sb.embed, .story__sharing-list .sb.embed {
    color: var(--primary-color);
}

/* Tags */

.coub__tags-tag, .story__tags-tag {
    color: #a08da0;
    background-color: #040404;
    border: 1px solid #494949;
    border-radius: 0px;
}

.coub__tags-tag:hover, .story__tags-tag:hover {
    color: #ffffff;
    background-color: rgba(0,0,0,0.95);
    border-color: #ff00ff;
    transition-duration: .2s;
    -webkit-transition-duration: .2s;
}

.related-tags__wrap>a {
    color: #a08da0;
    box-shadow: unset;
    background-color: #040404;
    border: 1px solid #ff00ff;
}

.related-tags__wrap>a:hover {
    background-color: rgba(0,0,0,0.95);
}

.related-tags__cropper.sb {
    color: #ff00ff;
}

/* Search */
.header__search .input-field, .input-field:focus{
    background: var(--primary-bg);
    color: var(--primary-color);
}

.header__search .input-field:focus {
    background: var(--primary-bg);
    color: white; 
}

.header__search .search-popup.-visible-popup .input-field {
    box-shadow: 0px 0px 0px 2px #4e4e4e;
}

.header__search .input-field:hover, .header__search .input-field:focus {
    box-shadow: 0px 0px 0px 2px #4e4e4e;
}

.search-popup__recent__head {
    background: #4e4e4e;
    color: rgb(255, 255, 255);
}

.search-popup__results__more {
    background: var(--primary-bg);
    color: rgb(255, 255, 255);
}

.search-popup__channel__followers {
    color: var(--primary-color);
}

.search-popup .dropdown__inner li.selected a, .search-popup .dropdown__inner li a:hover {
    background: #232323;
    color: #ffffff;
}

.search-popup__recent__clear-all:hover {
    color: #0e0d0e;
}

}
