css:
@-moz-document domain(kg.bezumn.ru) {
:root {
    /* default */
    --deepgray: #1a1818;
    --darkgray: #282B2F;
    --white: #E0E0E0;
    --blue: #8EC5FF;
    --lightblue: #c1dfff;
    --red: #ff8e9d;
    --middlegray: #656565;
    --orange: #d60;
    /* logo img */
    --logo: url('https://i.imgur.com/mWdARxU.png') no-repeat;
}

p,
#stats_last_update {
    color: var(--white) !important;
}

html {
    background: var(--darkgray) !important;
}

body,
#wrapper,
header,
section,
footer {
    border: none;
    background: var(--darkgray);
}
/* logo */
header img {
    display: block;
    box-sizing: border-box;
    width: 101px;
    height: 79px;
    background: var(--logo) no-repeat;
    padding-left: 101px;
    animation-name: logo;
    animation-duration: 9s;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
    filter: invert(0%);
    position: relative;
    margin: 10px auto;
}
/* logo animation */
@keyframes logo {
    0% {
        filter: invert(0%);
    }
    50% {
        filter: invert(85%);
    }
    100% {
        filter: invert(0%);
    }
}
/* table */
table th {
    color: var(--deepgray) !important;
    background: var(--blue) !important;
}

table td {
    color: var(--blue) !important;
}

table tr:nth-child(odd) {
    background: var(--darkgray);
}

table tr:nth-child(even) {
    background: var(--deepgray);
}
/* h */
h1,
h2,
h3,
h4,
h5,
h6 {
    color: var(--orange) !important;
}

.options,
.choose_voc {
    color: var(--white) !important;
    background: var(--deepgray) !important;
}
/* graphics */
canvas {
    filter: invert(95%);
}

nav span {
    color: var(--deepgray);
    background-color: var(--blue);
}

nav a,
div a,
.choose_voc a {
    color: var(--blue);
}

nav a:hover,
div a:hover {
    color: var(--lightblue);
}
/* rang color */
.rank1 {
    color: #cccccc !important;
}

.rank2 {
    color: #76e4e0 !important;
}

.rank3 {
    color: #2cd42c !important;
}

.rank4 {
    color: #ccbc00 !important;
}

.rank5 {
    color: #ff8b22 !important;
}

.rank6 {
    color: #ff1b6b !important;
}

.rank7 {
    color: #a03eec !important;
}

.rank8 {
    color: #575bff !important;
}

.rank9 {
    color: #2b59ec !important;
}

input[type=text] {
    background: var(--deepgray);
    color: var(--white);
}
}
