css:
@-moz-document url-prefix("https://www.novinky.cz/") {
.ogm-branding > div,
.ogm-ribbon--novinky,
.ogm-ribbon--novinky a,
.ogm-ribbon--menu-shown div,
.slb-menu,
p[data-dot="text"],
body,
main[aria-labelledby="accessibility-content"],
footer[role="contentinfo"],
div[data-dot-data],
div[data-dot-data] div,
.ogm-branding header[aria-labelledby="accessibility-header"],
main[aria-labelledby="accessibility-content"] header[data-e2e="tpl-royalty-mainHeader"],
input[type=text],
div[data-dot="hlasovani"] > div,
.ogm-ribbon--novinky ul,
.ogm-ribbon--novinky li,
article[role="article"] li span,
div.ogm-article-author-social .ogm-article-author__texts a {
    border-color: #525252;
    background: #000 !important;
    color: #7e7c7c !important;
}
.w-Ui-Tech,
.w-Ui-Tech div {
	background: transparent!important;
}
.tpl-king-top-content .szn-component-root {
    position: absolute;
    height: 1000vh;
    width: 100%;
    z-index: 100;
    background-color: rgba(0,0,0,0.7) !important;
    pointer-events: none;
}
div[data-dot="hlasovani"] .g_do {
    background: #222 !important;
}
div[data-dot="hlasovani"] .g_dp {
    background: #111 !important;
}
div[data-dot="vtip"] div.g_br {
	background: none !important;
}

#player-container {
	background: transparent!important;
}

#player-container div {
	background: transparent!important;
}

div.ad-content {
	background: transparent!important;
}

div[data-dot="poutak_v_textu"] div,
span[role="button"] {
	background-color: #111 !important;
    color: #7e7c7c;
    border-color: #525252;
}

div[data-dot="menu"] a span {
	color: #b00205;
}

div[data-dot="poutak_v_textu"] a > div span:first-of-type,
h3[data-dot-data],
h4[data-dot-data],
div[data-dot="boxiky_seo"] a,
article td,
article td a {
	color: #4b4b89 !important;
}

svg use,
svg defs {
	display: none
}

button[data-dot="lista_hledani_button"] {
	color: #000;
    background: #b00205;
}

.ogm-ribbon--menu-shown > div:after {
	opacity: 0;
}

.sn-button:hover {
 	color: #403939;
}

.ogm-ribbon--novinky.ogm-ribbon--menu-shown div:last-of-type {
    overflow-y: auto;
}


}
