css:
@-moz-document url-prefix("https://www.c-sharpcorner.com") 
{
  applies-to

  body
  {
      background-color: #101010;
  }

  p
  {
      color: #d5d5d5;
  }

  li
  {
      color: #d5d5d5;
  }

  h4
  {
      color: #d5d5d5;
  }

  h3.heading
  {
      color: #ffff;
  }

  h4.heading
  {
      color: #ffff;
  }

  a
  {
      color: #ffff;
  }

  body[id=MasterBody]
  {
      background-color: #101010;
  }

  .breaking-news-container > ul
  {
      background-color: #1e1e1e;
      border-color: #181818;
  }

  .breaking-news-container > ul > li
  {
      background-color: #1e1e1e;
  }

  .b-container.page-body
  {
      background-color: #101010;
  }

  .commentDescription
  {
      color: #d5d5d5;
  }

  .content-list a.title
  {
      font-size: 16px;
      color: #d5d5d5;
  }

  .featuredBox
  {
      background-color: #1e1e1e;
      border-color: #1e1e1e;
  }

  .featuredBox li .detail a
  {
      color: #d5d5d5;
  }

  .text-grey-900
  {
      color: #ffff;
  }

  .breaking-news-container > ul > li p
  {
      color: #d5d5d5;
  }

  .stacked-list > li > a
  {
      color: #d5d5d5;
  }

  h3.heading
  {
      color: #ffff;
  }

  .numbered-list .body
  {
      color: #d5d5d5;
  }

  .city-name
  {
      color: #888;
  }

  .main
  {
      background-color: #101010;
  }

  .category .articlesRow .HeadingAr a
  {
      color: #d5d5d5;
  }

  .detail
  {
      color: #d5d5d5;
  }
}
