css:
@-moz-document domain(avherald.com) {
    /* =================== BODY ============================== */
    .body_background,
    .body_mobile_background {
        background: #fffcf6 !important; 
        background-image: none !important;
        max-width: 100% !important;
        color:#B2965F !important;  
        margin-top: 15px !important;
        background-repeat: no-repeat; 
        background-position: top center; 
        background-attachment: scroll; 
        text-align: left; 
        font-family: Arial,sans-serif !important; 
        font-style: normal; 
        font-weight: normal; 
        font-size: initial !important;
    }

    /* ========================== GLOBAL ========================== */
    .header_avherald,
    .subheader_avherald,
    .copyright,
    .sitetext,
    .sitetitle,
    .sitesmalltext,
    .headline_avherald,
    .bheadline_avherald,
    .headline_faq,
    .bheadline_faq,
    .time_avherald,
    .article_avherald,
    .brief_avherald,
    .headline_article,
    .headline_user_comments,
    .headline_comment,
    .sitecomment,
    .site_sticky_comment {
        text-align:center; 
        font-style:normal; 
        font-weight: normal;
    }

    .header_avherald,
    .sitetitle,
    .bheadline_avherald,
    .bheadline_faq,
    .headline_article,
    .headline_user_comments,
    .headline_comment,
    a:link,
    a:visited,
    .site_sticky_comment {
        font-weight: bold; 
    }

    a:link,
    a:visited {
        text-decoration: none;
    }

    td { 
        text-align: left; 
    }
    
    p,
    .avhcookieconsent {
        display: block;
    }

    p {
        margin: 1em 0 0 0;
    }
    
    img, 
    embed, 
    object,
    video { 
        max-width: 100%;
        width: auto;
        height: auto;
    }

    hr {
        visibility: hidden !important;
    }

    /* =================== FONT FAMILIES & SIZES =================== */
    /* equivalent to h1 */
    .header_avherald {
        font-family: "Arial Black", Gadget, sans-serif !important;
        font-size: 2em !important;
        color: #667FB2 !important; 
    }

    /* equivalent to h2 */
    .subheader_avherald,
    .sitetitle,
    .headline_article {
        font-family: Arial, Helvetica, sans-serif !important;
        font-size: 1.5em !important;
    }

    /* equivalent to h3 */
    .bheadline_avherald {
        font-family: Arial, Helvetica, sans-serif !important;
        font-size: 1em !important;
        line-height: 2em !important;
        color: #667FB2 !important; 
    }

    /* equivalent to p, a */
    .sitetext,
    .sitesmalltext,
    .headline_avherald,
    .headline_faq,
    .bheadline_faq,
    .article_avherald,
    .headline_user_comments,
    .site_sticky_comment,
    body table tr td table tr td table tr td table tr,
    td[style="border:1px solid #000000;"] > .sitetitle,
    a {
        font-family: Verdana, Geneva, sans-serif !important;
        font-size: 0.95em !important; 
    }

    .sitetext {
        color: #80683b;
    }

    a {
        font-weight: bold;
    }



    /* smaller text */
    .copyright,
    .time_avherald,
    .brief_avherald,
    .headline_comment,
    .sitecomment {
        font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif !important;
        font-size: 0.6em; 
    }



    /* smaller text */
    .copyright,
    .time_avherald,
    .brief_avherald,
    .headline_comment,
    .sitecomment {
        font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif !important;
        font-size: 0.8em; 
    }    

    /* =========================== ====================== */
    .time_avherald { 
        color:#808080;
    }
    
    .site_sticky_comment {
        color:red; 
    }
    
    .separator { 
        color:#bac8eb; 
    }
    
    .cc_separator { 
        color:#bac8eb; 
    }

    /* ==================== TABLE ==================== */
    body table[style="min-width:1024px"] {
        min-width: 90% !important;
        max-width: 100% !important;
    }

    body table[style="min-width:1024px"] tr td table:first-child tr:first-child {
        max-width: 100% !important;
        min-width: 100% !important;
        width: 100% !important;
    }

    /* ---  header left --- */
    body table[style="min-width:1024px"] tr td table:first-child tr:first-child td[width="500"] {
        width: 40% !important;
        text-align: left !important;
        padding-left: 5% !important;
    }

    /* --- header middle --- */
    body table[style="min-width:1024px"] tr td table:first-child tr:first-child td[width="30"] {
        width: 0 !important;
    }

    /* --- header right --- */
    body table[style="min-width:1024px"] tr td table:first-child tr:first-child td[width="350"] {
        width: 40% !important;
    }

    body table:first-child table:first-child tbody tr:nth-child(2) td[style="text-align:center;"] {
        text-align: left !important;
        padding-left: 5% !important;
    }

    /* --- line of filters, search... --- */
    body table[style="min-width:1024px"] tr td table[style="position:relative;top:-8px;"],
    table[style="position:relative;top:-32px;"] {
        padding: 5px 0 !important;
        background: #f7e9cd !important; 
        border-radius: 5px !important;
        margin-bottom: 15px !important;
    }

    /* --- Left add --- */
    #adcell {
        max-width: 25% !important;
        min-width: 25% !important;
        width: 25% !important;
        border: none !important;
    }

    /* --- Content, articles between ads --- */
    .center_td { 
        padding: 0px 10px; 
        max-width: 80% !important;
        min-width: 50% !important;
        width: 50% !important;
    }

    /* articles */
    body table tbody tr td table tbody tr td table tbody tr td p {
        display: inline-block !important;
        padding: 15px 40% 0 0 !important;
    }

    body table tbody tr td table tbody tr td table tbody tr td p span {
        /* max-width: 60% !important;
        min-width: 60% !important;
        width: 60% !important; */
    }

    /* --- Even bgcolor --- */
    tr[bgcolor="#f0f8ff"] {
        background: #fffcf6 !important; 
    }

    body table tr td table tr td table tr td table tr {
        transition: all 0.3s ease-out;
    }

    body table tr td table tr td table tr td table tr:hover {
        background: #fbebcc !important; 
    }
    
    a:link { 
        color:black;
    }
    
    a:visited { 
        color:gray;
    }

    /* --- right add --- */
    td[style="border:1px solid #000000;"] {
        border: none !important;
    }
    
    /* ================ COOKIE MESSAGE ================ */
    .avhcookieconsent,
    .avhcc-link:link,
    .avhcc-link:hover,
    .avhcc-link:active,
    .avhcc-link:visited {
        color:white;
    }

    .avhcookieconsent {
        position: fixed; 
        top: 16px; 
        left: 16px;  
        z-index: 10;
        background-color:#237afc;
        width: 320px;
        max-width:320px;
        margin:0px;
        padding: 2em;
        text-align:left; 
        font-family:Helvetica,Calibri,Arial,sans-serif;
        font-size:12pt;
        line-height:1.5em;
    }
    
    .avhcc-link:link {
        text-decoration:none;
        font-weight:bold;
        opacity:.8;
        display:inline-block;
        padding:.2em
    }
    
    .avhcc-link:hover {
        opacity:1
    }
    
    .avhcc-link:active,
    .avhcc-link:visited {
        opacity:.8;
    }
    
    .avhcc-btn {
        color:#237afc;
        background-color: #FFFFFF;
        border-color:transparent;
        display: block;
        padding: .4em .8em;
        font-size: .9em;
        font-weight: 700;
        border-width: 2px;
        border-style: solid;
        text-align: center;
        white-space: nowrap;
        cursor:pointer;
    }
    
    .avhcc-message {
        display:block; 
        margin-bottom: 1em;
    }
    
    /* ========================= IMG FRAME & FRAME1 ================= */
    img.frame,
    img.frame:visited { 
        border:0; 
    }

    img.frame1,
    img.frame1:visited {
        border:1px;
    }
    
    img.frame1:visited { 
        border-color:gray; 
    }
    
    img.frame1 { 
        border-color:black; 
    }
}
