css:
@-moz-document url-prefix("http://www.eecg.toronto.edu/~ashvin/courses/ece344") {
body{
    color:			#444;
    font-family:    Georgia;
    background:		#fff;
    background:		hsl(220, 10%, 92%);
    max-width:		700px;
    margin:			auto;
    margin-bottom:	100px;
    margin-top:		100px;
}

p, p span{
    font-size:		15pt;
    line-height:	1.58;
    letter-spacing: -.003em;
    font-family:	Georgia;
}

li{
    font-size:		13pt;
    line-height:	1.58;
    letter-spacing: -.003em;
    font-family:	Georgia;
}

table{
    vertical-align: top;
    font-size:		13pt;
    line-height:	1.58;
    letter-spacing: -.003em;
    font-family:	Georgia;
}

tbody, tr, td{
    vertical-align: inherit;
    font:           inherit;
}

pre{
    font-family:    consolas;
    background:     #354450;
    color:          #1fd886;
    overflow:       auto;
    padding:        10px;
}

tt{
    background:     #f8f8f8;
    padding:        3px 4px 2px 4px;
    border:         1px solid;
    border-color:   hsl(0, 100%, 90%);
    border-radius:  3px 3px 3px;
    font-family:    consolas;
    font-size:      12pt;
    color:          hsl(0, 60%, 60%);
}

li tt,dd>tt{
    background:     #fff;
    padding:        2px 1px 1px 2px;
    border:         1px solid;
    border-color:   hsl(0, 100%, 90%);
    border-radius:  4px 4px 4px;
    font-family:    consolas;
    font-size:      10pt;
    color:          hsl(0, 60%, 60%);
}

h1{
    font-size:		26pt;
    font-family:    BlinkMacSystemFont, Calibri;
    font-weight:    600;
}

h2{
    font-size:		22pt;
    font-family:    BlinkMacSystemFont, Calibri;
    font-weight:    400;
}

h3{
    font-size:		18pt;
    font-family:    BlinkMacSystemFont, Calibri;
    font-weight:    400;
}

a{
    font:           inherit;
    color:          hsl(220, 60%, 50%);
}

ul{
    font:           inherit;
}
}
