css:
@-moz-document domain(bilibili.com) {
  
.bilibili-player-video-progress .bpui-slider-progress,
.bilibili-player-area.video-control-show .bilibili-player-video-progress-slider .bui-bar-normal {
    background: url("data:image/png;base64,") repeat-x !important;
    background: linear-gradient(to bottom, #FF0000 0%, #FF0000 16.5%, #FF9900 16.5%, #FF9900 33%, #FFFF00 33%, #FFFF00 50%, #33FF00 50%, #33FF00 66%, #0099FF 66%, #0099FF 83.5%, #6633ff 83.5%, #6633ff 100%) !important;
    background: -webkit-linear-gradient(top, #FF0000 0%, #FF0000 16.5%, #FF9900 16.5%, #FF9900 33%, #FFFF00 33%, #FFFF00 50%, #33FF00 50%, #33FF00 66%, #0099FF 66%, #0099FF 83.5%, #6633ff 83.5%, #6633ff 100%) !important;
    background: -moz-linear-gradient(top, #FF0000 0%, #FF0000 16.5%, #FF9900 16.5%, #FF9900 33%, #FFFF00 33%, #FFFF00 50%, #33FF00 50%, #33FF00 66%, #0099FF 66%, #0099FF 83.5%, #6633ff 83.5%, #6633ff 100%) !important;
}

.bilibili-player-video-progress .bilibili-player-video-progress-buffer-range,
.bilibili-player-area.video-control-show .bilibili-player-video-progress-slider .bui-bar-buffer {
    background: url("data:image/gif;base64,") !important;
}

.bilibili-player-area.video-control-show .bui-slider .bui-track.bui-track-autohide-thumb .bui-thumb .bui-thumb-dot {
    -webkit-transform: scale(1);
    transform: scale(1);
}

.bilibili-player-video-progress-slider .bui-thumb .bui-thumb-dot svg {
    display: none;
}

.bilibili-player-video-progress .bpui-slider-handle,
.bilibili-player-area.video-control-show .bilibili-player-video-progress-slider .bui-thumb .bui-thumb-dot {
    background: url("data:image/gif;base64,") !important;
    width: 34px !important;
    height: 21px !important;
    border: none !important;
    margin-top: 0px !important;
    top: -7px !important;
    margin-left: -17px !important;
    box-shadow: none !important;
    transform: scale(0.8) !important;
    -webkit-transform: scale(0.8) !important;
    -moz-transform: scale(0.8) !important;
    -ms-transform: scale(0.8) !important;
    transition: transform .1s ease;
}

.bilibili-player-area.video-control-show .bilibili-player-video-progress-slider .bui-thumb .bui-thumb-dot {
    top: 0px !important;
    margin-left: -22px !important;
}

.bilibili-player-video-progress:hover .bpui-slider-handle,
.bilibili-player-area.video-control-show .bilibili-player-video-progress-slider:hover .bui-thumb .bui-thumb-dot {
    transform: scale(1) !important;
    -webkit-transform: scale(1) !important;
    -moz-transform: scale(1) !important;
    -ms-transform: scale(1) !important;
}
  
}
