css:
@-moz-document domain("ptt.cc") {
a:link{    color: #79e0ff;}
a:visited {    color: #ff8181;}
a:hover{    color: #ffcc6e;        background-color: #0000;}

.r-ent:hover{    color: #eee;    background-color: #222}

.f3{    color: #ddd;}
.bbs-screen{    color: #eee;}
.f2{    color: #80d080;}
.f6{    color: #4ad7d7;}
.hl.f3{    color: #ffff86;}

#topbar a:hover{    background-color: #222;    color: #ffa387;}

#topbar-container{background-color: #000000;height: 33px;}
.article-metaline-right {   background-color: #777;}
#action-bar-container{padding: 33px 0 0 0;}

.article-meta-tag {    background-color: #222;    color: #ffa387;}


.article-meta-value,.article-metaline {    background-color: #333;    color: #6cd6e7;}

.bbs-content {    font-family: "微软雅黑";}
#navigation-container{display:none !important}
#logo:link{color:#fbf538}

#main-content {    line-height: 130%;}
.push {    line-height: 150%;}

.bbs-content,#article-polling {    font-size: 20px !important;}

.bbs-screen, #topbar, #navigation, .action-bar, #article-polling {    min-width: 600px;    max-width: 1100px;}

::-webkit-scrollbar { background-color: #0000; -webkit-box-shadow: inset 0 0 0 rgba(0, 0, 0, 0) !important; border-radius: 0 !important; border: none !important; }
::-webkit-scrollbar { width: 12px; }
::-webkit-scrollbar-thumb { background: #333; }
::-webkit-scrollbar-thumb:hover { background: #555; }
::-webkit-scrollbar-button:start:decrement, ::-webkit-scrollbar-button:end:increment { display: block; background: #333 }
}
