css:
/*
*    由 ZCat 乱改
*    Bilibili 动态首页净化
*    https://zcat.ga
*/

@-moz-document url-prefix("https://t.bilibili.com")
{
/*背景*/
body {
    background-color: #fff
}
#app{
    background: none !important;
}

/*尾部*/
.footer{
    display: none !important;
}

/*导航栏*/
.bili-header-m .bili-wrapper {
    width: 1200px;
    margin-top: 12px !important;
}
.bili-header-m .nav-menu .nav-mask {
    background-color: #fff !important;
    box-shadow: 0 2px 4px 0 rgba(128, 128, 128, .26);
}
.bili-header-m .nav-menu {
    position: relative;
    z-index: 200;
    height: 50px;
}
.bili-header-m .nav-menu .nav-con .nav-item .t {
    color: #000;
}
.bili-header-m .nav-menu .nav-con .nav-item.mbili,
.bili-header-m .nav-menu .nav-con .nav-item.game,
.bili-header-m .nav-menu .nav-con .nav-item.buy,
.bili-header-m .nav-menu .nav-con .nav-item.nav-bml,
.bili-header-m .nav-menu .nav-con .nav-item.moe,
.bili-header-m .nav-menu .nav-con .nav-item.mobile,
.bili-header-m .nav-menu .up-load,
.bili-header-m .nav-menu .nav-con .nav-item.home .header-icon-bilibili-tv {
    display:none;
}
.bili-header-m .nav-menu .nav-con .nav-item.home i {
    position: absolute;
    background-image: url(http://osteshczz.bkt.clouddn.com/bili.png);
    width: 156px;
    height: 52px;
    left: 500px;
    top: -10px;
    background-position: 1px 1px;
}
/*左侧*/
.user-panel {
    position: relative;
    width: 244px;
    height: 188px;
    background: #fff;
    overflow: hidden;
    border-radius: 5px !important;
    box-shadow: 0 0 2px rgba(0,0,0,0.2);
}
.user-panel .content .user-pic {
    background-image: none !important;
    background: none !important;
}
.user-panel .content .user-head {
    width: 56px;
    height: 56px;
    left: 40px !important;
    top: 40px !important;
    border-radius: 50%;
}
.user-panel .content .user-name {
    font-size: 16px !important;
    letter-spacing: 0;
    margin-left: 110px !important;
    margin-top: -80px !important;
}
.tc-blue {
    color: #555;
}

/*中间*/
.home-page .home-container .home-content .center-panel .section-block {
    padding: 15px;
    background-color: #fff;
    border-radius: 5px!important;
    box-shadow: 0 0 2px rgba(0,0,0,0.2);
}
.textarea {
    border-radius: 5px!important;
}
.publish-panel .dynamic-btn.with-border-radius {
    border-radius: 5px !important;
    box-shadow: 0 0 2px rgba(0,0,0,0.2);
    background-color: #fff !important;
    color: #868686 !important;
}
.f-left .box-toggle  {
    color: #555;
}
.tab-bar {
    border-radius: 5px!important;
    box-shadow: 0 0 2px rgba(0,0,0,0.2);
}
.tab-bar .tab .tab-text.selected, 
.tab-bar .tab .tab-text:hover{
    color: #555!important;
}
.tab-bar .line {
    background-color: #666!important;
}
.new-notice-bar .message {
    border-radius: 5px;
    background: #fff;
    color: #555!important;
    box-shadow: 0 0 2px rgba(0,0,0,0.2);
}
.card {
    border-radius: 5px!important;
    box-shadow: 0 0 2px rgba(0,0,0,0.2);
}
.dynamic-link-hover-bg {
    border-radius: 5px;
}
.original-poster .username {
    color: #555!important;
}
.imagesbox .zoom-wrap .zoom-list .card.card {
    border-radius: 5px!important;
    box-shadow: none;
}
.card .more-panel {
    position: absolute;
    width: 94px;
    text-align: center;
    top: 58px;
    background: #fff;
    border: 8px solid #e5e9ef;
    border-radius: 5px!important;
    color: #222;
    z-index: 10;
    -webkit-box-shadow: 0 0 2px rgba(0,0,0,0.2)!important;
}
.live-container,
.live-container .status,
.live-container .image-area {
    border-radius: 5px!important;
}

/*右侧*/
.right-panel .notice-panel,
.tag-panel,
.live-panel {
    position: relative;
    color: #555;
    border-radius: 5px!important;
    box-shadow: 0 0 2px rgba(0,0,0,0.2);
}
.tag-panel .tag-list a {
    color: #979797 !important;
}
}
