css:
@-moz-document domain("markos.roanm.net") {
body{
    background: url(https://www.onmsft.com/wp-content/uploads/2016/06/xpbliss.jpg)no-repeat;
    background-size: 100%;
}
#taskbar{
    background: -webkit-linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
    background: -moz-linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
    background: linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
	border: 1px solid #5089CE;
	-webkit-box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	-moz-box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	overflow: hidden;
}
#backgroundImg{
    visibility: hidden;
}
#openButton{
    color: white;
    font-family: Tahoma;
    background: -webkit-linear-gradient(top, #51AA4E 0%, #358D36 100%);
    background: -moz-linear-gradient(top, #51AA4E 0%, #358D36 100%);
    background: linear-gradient(top, #51AA4E 0%, #358D36 100%);
	border-top: 1px solid #8ECB90;
    border-top-right-radius: 20px;
    border-bottom-right-radius: 20px;
    -webkit-box-shadow: 0px 0px 0px rgba(146,201,134,1) inset, 0px 0px 3px rgba(0,0,0,0.2);
	-moz-box-shadow: 0px 0px 0px rgba(146,201,134,1) inset, 0px 0px 3px rgba(0,0,0,0.2);
	box-shadow: 0px 1px 0px rgba(146,201,134,1) inset, 0px 0px 3px rgba(0,0,0,0.2);
    text-transform: lowercase;
    padding-left: 12.5px;
}
.taskbarIcon{
    height: 30px;
    margin-left: 10px;
    margin-top: 2px;
    padding: 2.5px;
    background: linear-gradient(top, rgba(0,0,0,0.1) 0%, rgba(0,0,0,0.5) 100%);
    background: -moz-linear-gradient(top, rgba(0,0,0,0.1) 0%, rgba(0,0,0,0.5) 100%);
    border-radius: 7.5px;
    border: 1px rgba(0,0,0,0.2) solid;
}
.taskbarIcon:active{
    background-color: #408AF7;
}

.desktopItem{
    background-color: rgba(0,0,0,0)
}
.prgnName{
  color: black;
}
.desktopItem:active > .prgnName{
    color: white;
  background-color: #004A94;
}
#time{
    width: 100px;
    bottom: 0px;
    height: 40px;
    right: 0px;
    text-align: center;
    line-height: 40px;
    border-top-left-radius: 3px;
    border-bottom-left-radius: 3px;
    background-color: #1D99F1;
    -webkit-box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	-moz-box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
    background: -moz-linear-gradient(top, #35A4DB 0%, #35A4DB 100%);
}
#startMenu{
    width: 300px;
    height: 400px;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    background: linear-gradient(
    to right, 
    white 0%, 
    white 50%,
    black 50.3%,
    #D2E2F9 50%, 
    #D2E2F9 100%
  );       
}
#topStartmenuItem{
    position: relative;
    top: -10px;
    width: 318px;
    column-count: 4;
    column-gap: 0px;
    font-size: 30px;
    left: -10px;
    height: 60px;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    background-color: red;
    background: -webkit-linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
    background: -moz-linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
    background: linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
	border: 1px solid #5089CE;
	-webkit-box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	-moz-box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
}
#bottomStartmenuItem{
    position: relative;
    bottom: -207px;
    width: 318px;
    column-count: 4;
    column-gap: 0px;
    font-size: 30px;
    left: -10px;
    height: 40px;
    background-color: red;
    background: -webkit-linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
    background: -moz-linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
    background: linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
	border: 1px solid #5089CE;
	-webkit-box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	-moz-box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
}
#profileIcon{
    margin: 5px;
}
#format{
    position: absolute;
    z-index: 2200;
    bottom: 8.6px;
    right:40px;
    width: 20px;
    height: 20px;
    color: rgba(255,255,255,0);
    border: white solid 1px;
    padding: 2px;
    background: url("https://y7b.imgh.ga/Bbl27rJEaiQE4R4uHLo6/this%20turns%20me%20off.png")no-repeat;
    background-size: 25px;
    border-radius: 5px;
}
.exitButton{
 color: white;
 width: 16px;
 display: block;
 padding: 0px;
 border-radius: 3px;
 margin-top: -10px;
 background: -moz-linear-gradient(top, #D28188 0%, #D35B4A 100%);
}
.window{
    border-left: 3px solid #2761CF;
    border-bottom: 3px solid #2761CF;
    border-right: 3px solid #2761CF;
    background-color: #ECE9D8;
}
.header{
    height: 20px;
    margin-top: -10px;
    color: white;
    background: -webkit-linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
    background: -moz-linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
    background: linear-gradient(top, #4F8CCF 0%, #2761CF 100%);
	border: 1px solid #5089CE;
	-webkit-box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	-moz-box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	box-shadow: 0px 1px 1px rgba(255,255,255,0.4) inset, 1px 1px 3px rgba(0,0,0,0.2);
	overflow: hidden;
}
input{
    background-color: white;
}
button{
    background-color: white;
    border-radius: 2px;
}
#hellowizkid{
    color: white;
}
}
