css:
@-moz-document url-prefix("https://www.toodledo.com/tasks/"), url-prefix("http://www.toodledo.com/tasks/") {

/* Global */
body,
span,
p {
    color: #999;
    background-color: #2f2f2f;
}

#tasks {
    color: #999;
}

.row {
    border-top: 1px dotted #444;
}

.cols:hover span,
.cols:hover .note {
    background-color: transparent;
}

.subtasks .row {
    border-top: #353535;
}

.sep {
    border-bottom: 1px solid #33312b;
    background: #33312b;
}
#colhead {
    background-color: #2f2f2f;
    border-bottom: 1px solid #444;
}

/* Check box */
.task_check-sm .box-bg {
    color: #2f2f2f;
}

/* Task */
.col0 .task + span {
    color: #eee;
}

.col0 .task + input {
    color: #2f2f2f;
}

/* Due Date */
.date1 {
    background-color: #EEE !important;
    color: #f73b42 !important;
}
.date2 {
    color: #f73b42 !important;
}
.date3 {
    background-color: #f73b42 !important;
    color: #fff !important;
}

/* Repeat */
/* Tag */
/* Context */
/* Start Date */
/* Date Complete */
.col98 {
    display: none !important
}

/* Goal */
.col1024:hover {
    overflow: hidden !important;
}

/* Folder */
/* Priority */
.pri0 {
    color: #999 !important;
}
.pri1 {
    background-color: #827f66 !important;
    color: #111 !important;
}
.pri2 {
    background-color: #547184 !important;
    color: #111 !important;
}
.pri3 {
    background-color: #51805c !important;
    color: #111 !important;
}

/* Length */
/* Delete and Edit Button */
.cols .del {
    margin: 0px 0px 0px 5px !important;
}
.cols .note,
.cols .ednote {
    margin: 0px 0px 0px 0px !important;
}

/* Dimmed Text */
.dim {
    color: #DDD !important;
}

/* Status */
.pri1,
.pri2,
.pri3,
.pri4,
.status1,
.status2,
.status3,
.status4 {
    text-shadow: none;
}


/* Sidebar */
#tn_switcher,
#sidebar,
#sidebar .sb-card,
#sidebar .sb-firstcard,
#contents_left {
    color: #999 !important;
    background-color: #2f2f2f !important;
    border-bottom: 1px solid #2f2f2f;
}

#topnav-in {
    background-color: #2f2f2f !important;
    color: #999 !important;
    background-image: none !important;
}

#ribbon,
#ribbon .rib_btn_1,
#ribbon .rib_btn_2,
#ribbon .rib_btn_3,
#ribbon .rib_btn_4,
#ribbon .rib_btn_5,
#ribbon .rib_btn_6,
#ribbon .rib_btn_7,
#ribbon .rib_btn_8,
#ribbon .rib_btn_9,
#ribbon .rib_btn_10,
#ribbon .rib_btn_11,
#ribbon .rib_btn_12,
#ribbon .rib_btn_13
{
    background-color: #2f2f2f;
}


/* Add */
#addtask,
#multieditPop,
#quicksearch,
#searchtask {
    color: #999;
    background-color: transparent;
}

button.cta-nav-add {
    background-color: #2f2f2f !important;
}

/* Others */
#contents_right #internal-footer .copyrighted {
    color: #999;
}

#searchtask #search_header,
#contents_right .breadcrumb,
#quickadd_bar,
#quickAdd.placeholder {

    background-color: #2f2f2f !important;
    color: #999 !important;
    background-image: none !important;
}

#addtaskinner {
    background-color: #2f2f2f;
    color: #999;
}

#addtask #addtask_header {
    background-color: #2f2f2f;
    color: #999;
}

#addtask #addtask_footer {
    margin-top: 0px;
    background-color: #2f2f2f;
}

.field b, #addtask label {
    color: #999;
}

.note {
    color: #999;
}

#quickAdd {
    border: 2px solid #4a4a4a;
}

}
