css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
/* FONT */
  
  body, .tabbar a.button, .menugroup .button, .userlist li button, .setmenu button, .teamlist button, .folder .selectFolder, .button, .teambar button, .utilichart h3, .dexentry h3, .resultheader h3, .select, button, .trainer strong, .turn, .battle-log, .battle-log h2, .chatbox label {
    font-family: "Comic Sans MS";
  }


  
  /* GENERAL */
  body ::-moz-selection {
    background: #DB3400;
    color: #FFDDD2;
    text-shadow: none;
  }
  
  body {
    background: url("http://i.imgur.com/q8acF4t.jpg") bottom !important;
    background-size: cover;
  }
  
  .ps-room.ps-room-light, .dark .ps-room.ps-room-light {
    background: none;
    border-color: #CBB47F;
    color: black;
  }
  
  .rooms-officialchatrooms, .rooms-chatrooms, .dark .rooms-officialchatrooms, .dark .rooms-chatrooms {
    color: black;
    text-shadow: 0px 0px 2px #DB3400;
  }
  
  .ps-room.ps-room-light.scrollable {
    background: -webkit-linear-gradient(rgba(238, 121, 51, 0.6), rgba(0, 0, 0, 0.6));
    background: -o-linear-gradient(rgba(238, 121, 51, 0.6), rgba(0, 0, 0, 0.6));
    background: -moz-linear-gradient(rgba(238, 121, 51, 0.6), rgba(0, 0, 0, 0.6));
    background: linear-gradient(rgba(238, 121, 51, 0.6), rgba(0, 0, 0, 0.6));
    text-shadow: 1px 1px 1px #FFE29C;
  }
  
  .ps-room.ps-room-light.scrollable .button, .pad button {
    background: -webkit-linear-gradient(#FFE29C, #EEB75C);
    background: -o-linear-gradient(#FFE29C, #EEB75C);
    background: -moz-linear-gradient(#FFE29C, #EEB75C);
    background: linear-gradient(#FFE29C, #EEB75C);
    padding: 3px 8px;
    border: 1px solid black;
    border-radius: 5px;
    color: #222;
    text-shadow: 1px 1px 1px #FFE29C;
  }
  
  .ps-room.ps-room-light.scrollable .button:hover, .pad button:hover {
    background: #EEB75C;
    text-shadow: none;
  }
  
  .ps-room.ps-room-light.scrollable .button:focus {
    box-shadow: 0px 0px 3px #DB3400;
  }
  
  .ps-room.ps-room-light.scrollable button[name="joinRoomPopup"] {
    box-shadow: inset 0px 1px 2px #D2D2D2, 0px 0px 5px #DB3400;
  }
  
  .pad input, .pad input:hover, .dark .pad input, .dark .pad input:hover {
    background: #F8FBFD;
    border: 1px solid #222;
    color: black;
    box-shadow: inset 0px 1px 2px rgba(0, 0, 0, 0.8);
  }
  
  .pad input:focus {
    border: 1px solid #FF6501;
    box-shadow: inset 0px 1px 2px rgba(0, 0, 0, 0.8), 0px 0px 2px #DB3400;
  }
  
  .pad button {
    padding: 2px 8px;
  }
  
  .roomlist a.ilink, .dark .roomlist a.ilink {
    background: rgba(50, 50, 50, 0.8);
    border: 2px solid #222;
    padding: 10px;
    box-shadow: 1px 1px 1px rgba(255, 255, 255, 0.3) inset, -1px -1px 1px rgba(0, 0, 0, 0.5) inset;
    color: #DDD;
    font-size: 11pt;
    text-shadow: 0px -1px #222;
  }
  
  .roomlist a.ilink small, .dark .roomlist a.ilink small {
    color: #D8BA8D;
  }
  
  .roomlist a.ilink:hover, .dark .roomlist a.ilink:hover {
    background: rgba(30, 30, 30, 0.85);
    border: 2px solid #222;
    color: #DDD;
  }
  
  .roomlist a.ilink:hover small {
    color: #CBB18A;
  }
  
  .roomcounters button {
    background: #705333 !important;
    border: 2px solid #222 !important;
    color: #FFE29C !important;
    text-shadow: 1px 1px #4B3720 !important;
    box-shadow: 1px 1px 1px rgba(255, 255, 255, 0.3) inset, -1px -1px 1px rgba(0, 0, 0, 0.5) inset !important;
  }
  
  .roomcounters button:hover {
    background: #81613C !important;
    border: 2px solid #222 !important;
  }

/* Change the background URL in this section to add your own image next to your name! Image should be 32x32. */

.userbar:before{
  display: block;
  content:"";
        width:32px;
        height: 32px;
        float: left;
        position: relative;
        top: -9px; background: url(https://cdn.discordapp.com/attachments/506621363138723842/506810935541760000/scizorsm.png);
      }
.logo {
    float: left;
    margin: 2px 0 0 2px;
    background-color: #222222;
     opacity: 0.0;
    
}



     
.icon-user{
     display:none; 
    }

/*********************************************************
 * Global
 *********************************************************/

/*********************************************************
 * Main menu
 *********************************************************/

.leftmenu {padding: 40px 0 0 0;width: 300px;
}
.rightmenu {position: absolute;top: 40px;right: 35px;
}
.activitymenu {position: absolute;left: 325px;top: 40px;
}
.mainmenu {padding: 0 0 40px 0;
}

.battleform {width: 230px;text-align: left;margin: 0 auto;
}
.battleform .buttonbar {text-align: center;
}

.mainmenuwrapper {position: relative;min-height: 100%;
}
.mainmenufooter {position: absolute;bottom: 15px;left: 20px;color: #BBBBBB;text-shadow: 0 0 1px black, 0 0 1px black, 0 0 1px black;
}
.mainmenufooter blockquote {margin: 0 0 1em 0;padding: 0;
}
.mainmenufooter small {font-size: 8pt;
}
.mainmenufooter a {text-decoration: underline;color: #FFFFFF;
}
.mainmenufooter a:hover {text-decoration: underline;color: #99BBFF;
}

.pmbox {width: 270px;margin: 0 auto;min-height: 2px;
}
.pm-window {margin-bottom: 12px;
}
.pm-window h3 {background: #000000;color: #ffffff;margin: 0;padding: 7px 10px;font-size: 8pt;border: 2px solid #004ce7;
    border-top-left-radius: 20px;border-top-right-radius: 20px;cursor: pointer;
}
.pm-window h3:hover {color: #000000;
}
.pm-window h3.pm-minimized {border-radius: 0px;
}
.pm-window h3.pm-notifying {border-color: #004ce7;background: #000000;
}
.pm-window h3.pm-notifying:hover {border-color: #604020;background: #000000;
}
.pm-window h3 .closebutton,
.pm-window h3 .minimizebutton {
	float: right;
	padding-right: 30px;
}
.pm-window h3 small {color: #ff2727;font-weight: normal;vertical-align: 10px;
}
.pm-log {min-height: 100px;max-height: 300px;background: #000000;color: #aaa;word-wrap: break-word;border: 2px solid #004ce7;border-top: 0;
	border-bottom: 0;overflow: auto;-webkit-overflow-scrolling: touch;overflow-scrolling: touch;
}
.pm-log-add {border: 2px solid #004ce7;border-bottom-left-radius:20px;border-bottom-right-radius: 20px;background: #000000;
}
.pm-window.collapsed .pm-log,
.pm-window.collapsed .pm-log-add {display: none;
}
.pm-window.focused h3,
.pm-window.focused .pm-log,
.pm-window.focused .pm-log-add {border-color: #004ce7;
}
.pm-window.focused h3,
.pm-window.focused h3:hover {background: #ff3e3e;color: #222222;cursor: default;
}
.challenge {margin-top: -1px;background: url("https://static.wixstatic.com/media/10af1d_9c5d75e111e24843917e33787320ac06.png?dn=FIAL+LONE.png");
	border: 1px solid #004ce7;color: #fff;padding: 1px 5px;
}
.challenge p {margin: 1px 0;
}

.tiny-layout .leftmenu,
.tiny-layout .rightmenu,
.tiny-layout .activitymenu {position: static;top: 0;right: 0;left: 0;width: auto;margin: 0;padding: 0;
}
.tiny-layout .leftmenu {padding-top: 30px;
}
.tiny-layout .activitymenu {padding-bottom: 8px;
}

.mainmessage {display: block;padding: 10px 15px;margin: 30px 40px;max-width: 400px;border-radius: 20px;background: rgba(201, 207, 219, .8);
	color: black;
}
.menugroup {display: block;padding: 10px 0;margin: 0 27px 30px 27px;max-width: 400px;border-radius: 30px;color: white;
    text-shadow: 0 1px 0 black;
}
.menugroup {background: #000000;text-align: center;
}

.mainmessage p,
.menugroup p {
	margin: 10px 0;
}
.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
	box-sizing: border-box;font-size: 14pt;font-family: Joystick, Verdana, Helvetica, Arial, sans-serif;display: inline-block;
	padding: 5px 10px 8px 7px;width: 200px;box-shadow:none;outline: none;cursor: pointer;text-align: center;text-decoration: none;
	text-shadow: 0 -1px 0 hsl(295, 100%, 77%);border-radius: 5px;

	/* default colors */
	color: #aaa;border: solid 1px #0044ef;background: #779EC5;
    background: url("https://cdn.discordapp.com/attachments/506621363138723842/506817511392804864/PbDk0iT.png");
	box-shadow:none;outline: none;cursor: pointer;text-align: center;text-decoration: none;text-shadow: 0 -1px 0 hsl(210, 100%, 82%);
	border-radius: 20px;
}
 .mainmenu .menugroup:nth-child(1) .button {width: 230px;padding: 6px 7px 6px 7px;font-size: 14pt;color: #ff0d0d;border: solid 1px #0044ef;
	background: #779EC5;
	background: url("https://cdn.discordapp.com/attachments/506621363138723842/584180896001818634/PbDk0iT.png");
box-shadow:none;outline: none;cursor: pointer;text-align: center;text-decoration: none;text-shadow: 0 -1px 0 hsl(210, 100%, 91%);
	border-radius: 20px;
}
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
	background: url("https://static.wixstatic.com/media/10af1d_1330e18dc4584ac5ad239f0c21725425.png?dn=HOVER.png");border-color: #0044ef;
   box-shadow: -1px 1px 40px 0px rgba(0,34,255,1);-webkit-transition-delay: 0.1s;-webkit-transition-duration: 0.2s;
    }
 .mainmenu .menugroup:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:active,.mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:active,.mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:active,.mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:active {
	box-shadow: none;background: #0044ef !important;
	border-color: #bb2222;-webkit-box-shadow: -2px -2px 75px 0px rgba(255,255,255,1);
}
.menugroup .button.disabled,
.menugroup .button.disabled:hover,
.menugroup .button.disabled:active {cursor: default;background: #EEEEEE;border: 1px solid #CCCCCC;color: #999999;text-shadow: none;
	-webkit-box-shadow: -2px -2px 75px 0px rgba(255,255,255,1);
}

.rightmenu {width: 294px;
}
.rightmenu .menugroup .button {width: 180px;
}
@media (max-width:895px) {
	.mainmenu {padding-bottom: 1px;
	}
	.rightmenu {position: static;top: 0;right: 0;padding-bottom: 30px;
	}
	.rightmenu {
		width: 324px;
	}
	.rightmenu .menugroup .button {
		width: 200px;
	}
}


a:link {
    color: #30a4e0;
}


a:visited {
    color: #8f30e0;
}
html, body {
	margin: 0;
	padding: 0;
}
body {background:url(http://i.imgur.com/wlHm7B4.png);color: white;font-size: 11pt;font-family: Joy, Verdana, Helvetica, Arial, sans-serif;
}.optlabel {font-size: 9pt;display: block;
}.textbox {border: 1px solid #AAAAAA;border-radius: 3px;padding: 2px 3px;font-family: Joy, Verdana, Helvetica, Arial, sans-serif;font-size: 9pt;box-shadow: inset 0px 1px 2px #CCCCCC, 1px 1px 0 rgba(255,255,255,.6);background: #F8FBFD;color: black;
}.textbox:hover {border-color: #474747;box-shadow: inset 0px 1px 2px #D2D2D2, 1px 1px 0 rgba(255,255,255,.6);background: #FFFFFF;
}.textbox:focus {outline: 0 none;border: 1px solid #004488;box-shadow: inset 0px 1px 2px #D2D2D2, 0px 0px 5px #2266AA;background: #FFFFFF;
}a.subtle {text-decoration: none;color: black;
}a.subtle:hover {text-decoration: underline;color: #6688CC;
}.buttonbar {margin-top: 1em;text-align: center;
}

/*********************************************************
 * Header
 *********************************************************/

i.fa.fa-user {display:none;}

.header {position: relative;height: 50px;background: url(https://cdn.discordapp.com/attachments/506621363138723842/506782654662836235/jp2.png);
    
}.header:hover {position: relative;height: 50px;background: url(https://cdn.discordapp.com/attachments/506621363138723842/506782654662836235/jp2.png);-webkit-transition-delay: 0.3s;-webkit-transition-duration: 0.7s;-webkit-box-shadow: -1px -1px 29px 0px rgba(0,43,255,1);
}.userbar .username {color: black;text-shadow: none;
}.userbar button.icon i {margin: 0 -5px;
}.tabbar {position: absolute;bottom: 0px;left: 0px;right: 0;display: block;list-style: none;margin: 0;padding: 0 0 0 0;height: 35px;text-align: left;color: white;text-shadow: 0 1px 0 black;font-size: 10pt;line-height: 100%;border-top-right-radius: 30px;border-top-left-radius: 30px;
}.maintabbarbottom {content: "";display: block;position: absolute;left: 0;right: 0;bottom: -2px;height: 2px;background: #004ce7;border: solid 0px #004ce7;border-left: 0;margin: -1px 0 0 0;-webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2);-moz-box-shadow: 0 1px 2px rgba(0,0,0,.2);box-shadow: 0 1px 2px rgba(0,0,0,.2);border-top-right-radius: 30px;border-top-left-radius: 30px;
}


body {
    background: #000 !important;
}

.ps-room.ps-room-light {
    background: rgba(0, 0, 0, 0);
    border-left: 1px solid #171616;
}

.ps-room.ps-room-light.scrollable {
    background: rgba(0, 0, 0, 0.2) url("https://cdn.discordapp.com/attachments/506621363138723842/584195581476470795/99A4873C5B93DC9A1D.png") center right no-repeat;
    color: #fff;
    background-size: cover;
background-position: top center !important;
background-repeat: no-repeat !important;
background-attachment: fixed;
    text-shadow: 0px 0px 3px #000;
}

.ps-overlay {
    background: rgba(0, 0, 0, .82);
}

.mainmenuwrapper {
    background: #5B5756 url("https://cdn.discordapp.com/attachments/506621363138723842/584176220699361282/99A4873C5B93DC9A1D.png") center right no-repeat;
 background-size: cover;
background-position: top center !important;
background-repeat: no-repeat !important;
background-attachment: fixed;
}








/*****************************/

/* Teambuilder CSS */

/*****************************/
.teamchartbox {
    border-top: 1px solid #000;
    background: url("https://cdn.discordapp.com/attachments/506621363138723842/584602633101115392/99A4873C5B93DC9A1D.png");
     background-size: cover;
background-position: top center !important;
background-repeat: no-repeat !important;
background-attachment: fixed;
}

.setchart,
.setchart-nickname {
    background: #000;
    box-shadow: 1px 1px 0px rgba(0, 0, 0, 0.8) inset, 0px 0px 3px #bb7474;
    border: 1px solid #ee4d4d;
}

.setchart label,
.setchart-nickname label {
    color: #8dfeff;
    text-shadow: 0px 0px 1px #000;
}

.setchart input,
.setchart-nickname input,
.statform input.inputform,
.setchart input:hover,
.setchart-nickname input:hover,
.statform input.inputform:hover,
.setdetails,
.setdetails:hover,
.setstats,
.setstats:hover {
    border: 1px solid #ee4d4d;
    box-shadow: none;
    box-shadow: 0px 0px 3px #000;
    background: #201E1E;
    color: #7ee1e4;
}

.setdetails .detailcell {
    border-color: #4de3ee;
}

.setchart input:focus,
.setchart-nickname input:focus,
.statform input.inputform:focus {
    border: 1px solid #4deee9;
    box-shadow: 0px 1px 2px #151515 inset, 0px 0px 5px #4deee6;
    background: none repeat scroll 0% 0% #201E1E;
}

.setchart input.incomplete,
.utilichart .illegalcol em {
    color: #45d0d7;
    border-color: #45d7d7;
}

.setdetails:focus,
.setstats:focus {
    border: 1px solid #4deeeb;
}

.jslider .jslider-label {
    color: #FFF;
    opacity: 0.6;
}

.jslider .jslider-value {
    color: #000;
}

.teambuilder-results h3,
.teambuilder-results .utilichart h3 {
    background: #201E1E;
    box-shadow: 1px 1px 0px rgba(0, 0, 0, 0.8) inset, 0px 0px 3px #000;
    border-color: #4de6ee;
    color: #FFF;
    text-shadow: 0px 0px 1px #000;
}

.setstats .statrow-head em {
    color: #DDD;
}

.teambar button {
    border-color: #171616;
    background: #201E1E;
    box-shadow: 1px 1px 0px rgba(0, 0, 0, 0.8) inset, 0px 0px 3px #000;
    color: #fff;
    overflow-x: hidden;
}

.teambar button:hover {
    border-color: #171616;
    background: #171616;
    box-shadow: 1px 1px 0px rgba(0, 0, 0, 0.8) inset, 0px 0px 3px #000;
}

.teambar button:disabled,
.teambar button:disabled:hover {
    border-color: #171616;
    background: #201E1E;
    box-shadow: none;
    color: #fff;
    opacity: 0.8;
}

.setmenu {
    top: 3px;
}

.setmenu button {
    text-shadow: 0px 0px 1px #000;
}

.setmenu button:hover {
    background: #201E1E;
    border-color: #4de9ee;
}

.teambuilder-results .firstresult a,
.teambuilder-results .firstresult a:hover,
.teambuilder-results .result a:hover {
    border-color: #4deee6;
    background: #201E1E;
    box-shadow: 1px 1px 0px rgba(0, 0, 0, 0.8) inset, 0px 0px 3px #000;
}

.utilichart b {
    color: #4de6ee;
}

.utilichart .movenamecol,
.utilichart .namecol,
.utilichart .pokemonnamecol {
    color: #ff6f6f;
}

.utilichart .movedesccol,
.utilichart .labelcol em,
.utilichart .widelabelcol em,
.utilichart .pplabelcol em,
.utilichart .statcol em,
.utilichart .bstcol em,
.utilichart .numcol {
    color: #8ffcff;
    text-shadow: 0px 0px 1px #000;
}

.utilichart .labelcol,
.utilichart .widelabelcol,
.utilichart .pplabelcol,
.utilichart .twoabilitycol,
.utilichart .abilitycol,
.utilichart .statcol,
.utilichart .itemdesccol,
.utilichart .abilitydesccol {
    color: #F9F9F9;
    text-shadow: 0px 0px 1px #000;
}

.teambuilder-clipboard-container {
    border-color: #4de9ee;
}

.teambuilder-clipboard-data,
.teambuilder-clipboard-data:hover {
    background: #201E1E;
    box-shadow: 1px 1px 0px rgba(0, 0, 0, 0.8) inset, 0px 0px 3px #000;
    border: 1px solid #4de1ee;
}

.teambuilder-clipboard-data .section {
    border-right: 1px solid #4de6ee;
}

.utilichart .typecol img {
    opacity: 0.7;
    margin-right: 1px;
}



/*****************************/

/** Teambuilder CSS **/

/*****************************/
.chat-log {
    border-bottom: 1px solid #171616;
    border-left: 1px solid #171616;
    background: rgba(242, 247, 250, 0.925);
}

.chat-log-add {
    border-top: 1px solid #201E1E;
    background: rgba(0, 0, 0, 0.65);
}

.userlist {
    border-right: 1px solid #201E1E;
    background: rgba(0, 0, 0, 0.65);
}

.userlist li {
    background-color: rgba(255, 255, 255, 0) !important;
}

.userlist li button {
    border-radius: 0px;
    text-shadow: 1px 1px #000;
}

.userlist li em.group {
    color: #AAA;
}

.userlist li button:hover {
    background: rgba(23, 22, 22, 0.5);
}

.userlist li {
    color: #F9F9F9;
    text-shadow: 0px 0px 1px #000;
    border-bottom: 1px solid #171616;
}

.broadcast-red {
    background: #D57575;
    border: 1px solid #985555;
    color: #FFD7D7;
    padding: 5px 1px 3px 8px;
    text-shadow: 1px 1px 1px #985555;
    font-family: helvetica, arial, sans-serif;
    text-align: center;
    font-style: normal;
}

.broadcast-green {
    background: #578A54;
    border: 1px solid #375735;
    padding: 5px 1px 3px 8px;
    color: #BEFFB9;
    text-shadow: 1px 1px 1px #375735;
    font-family: helvetica, arial, sans-serif;
    text-align: center;
    font-style: normal;
}

.broadcast-blue {
    background: #677992;
    border: 1px solid #414D5E;
    padding: 5px 1px 3px 8px;
    color: #C1DAFF;
    text-shadow: 1px 1px 1px #414D5E;
    font-family: helvetica, arial, sans-serif;
    text-align: center;
    font-style: normal;
}

.ps-popup {
    background: #201E1E;
    box-shadow: 1px 1px 0px #101010 inset, -1px -1px 0px #121212 inset, 0px 0px 3px #000;
    border: 1px solid #ee4d4d;
    color: #DDD;
    text-shadow: 0px 0px 1px #000;
    z-index: 1000;
}

.ps-popup hr {
    border: none;
    border-bottom: 1px solid #EEE;
}

.ps-popup a {
    color: #ee4d4d;
