/*
 * Name:			Dark Mathworks MATLAB
 * Author:			Atx
 * Version:			v1.0
 * Latest update:	05.11.2018
 */

@-moz-document domain("mathworks.com") {
body,
#content_container,
.offcanvas_horizontal_nav,
#sidebar,
.offcanvas_nav,
.navbar-header,
#doc_center_content .expand_collapse .collapsible_content,
.nav_disambiguation,
.content_container,
.body_trail_container,
.navbar-default,
.horizontal_nav_container,
.header,
.panel-footer,
.add_background_color_gray,
tr.add_background_color_gray td,
.content_container_no_conflict
{
	background: #1C1C1C !important;
}

.syntax_signature_module,
#nav_toggle.offcanvas_actuator,
.panel.panel-default > .panel-heading,
.panel-group .panel-heading,
.codeinput,
.language-matlab,
.table-responsive > .table > thead > tr > th,
.table-responsive > .table > tbody > tr:nth-child(2n) > td,
ul.nav-tabs.tabs-vertical,
.panel.panel-default,
.containsResourceName.resourceClass-panel.section > .panel
{
	background: #2C2C2C !important;
}

.table-responsive > .table > tbody > tr:nth-child(2n+1) > td,
.sidebar-offcanvas .nav_scrollspy li a:hover
{
	background: #3C3C3C !important;
}

.topnav_products.active > a,
.topnav_products > a:hover,
.topnav_solutions.active > a,
.topnav_solutions > a:hover,
.topnav_academia.active > a,
.topnav_academia > a:hover,
.topnav_support.active a,
.topnav_support > a:hover,
.topnav_community.active > a,
.topnav_community > a:hover,
.topnav_events.active > a,
.topnav_events > a:hover
{
	background: #4C4C4C !important;
}

.panel-collapse > .panel-body > .pull-right > .panel.metadata_container
{
	background: none !important;
}

#doc_center_content,
.sidebar-offcanvas .nav_scrollspy li a,
h2,
h4,
.navbar-default #topnav.navbar-nav > li > a,
.navbar-default #topnav.navbar-nav > li > a:visited,
body,
h1,
h2,
h3,
.answered,
.table-responsive > .table > thead > tr > th,
.panel-group .panel-heading,
.codeinput,
.language-matlab,
.namevaluecontainer,
.nav > li > a:focus,
.nav > li > a:hover,
.sidebar-offcanvas .nav_toc li.active a
{
	color: #CCC !important;
}

.add_font_color_general
{
	color: #888 !important;
}

.alert.alert-info > h3
{
	color: #2A2A2A !important;
}

.sidebar-offcanvas .nav_scrollspy li.nav_scrollspy_function > a
{
	color: #C45400 !important;
}

div#mobile_search.col-xs-12.col-sm-6.col-sm-push-6.col-md-5.col-md-push-7
{
	background: #2f7EB2 none !important;
}

div.programlisting div.codeinput,
.panel-body > .table-responsive table.table.table-condensed
{
	border: 1px solid #4C4C4C;
}

.anchor_hinting
{
	background-color: #007099 !important;
}

.panel.panel-default > .panel-heading,
.content_container,
.release_introduced,
.ref_sect > h2,
.examples > h2,
#doc_center_content h1,
.sidebar-offcanvas ul.nav_breadcrumb,
.content_container_no_conflict,
.add_bottom_rule
{
	border-bottom: 1px solid #4C4C4C !important;
}

.release_introduced,
.panel-group .panel-heading + .panel-collapse > .panel-body,
.description_module,
.panel > .panel-body > hr,
.description_element + .description_element
{
	border-top: 1px solid #4C4C4C !important;
}

.sidebar-offcanvas .nav_disambiguation
{
	border-bottom: 3px solid #4C4C4C !important;
}

.parsys > .horizontalrule > hr.add_thickness_5
{
	border-top: 5px solid #4C4C4C !important;
}

.panel.panel-default
{
	border-color: #4C4C4C !important;
}

.panel
{
	border: none !important;
}

.sidebar-offcanvas
{
	border-right: 3px solid #4C4C4C !important;
}

table td,
.table > tbody > tr > td,
table th,
.table > thead > tr > th
{
	border-right: 1px solid #4C4C4C !important;
	border-bottom: 1px solid #4C4C4C !important;
}


.inlinemediaobject > img
{
	filter: invert(100%) !important;
}

#doc_center_content > div > iframe
{
	border: 1px solid #4C4C4C !important;
}
}

@-moz-document url-prefix("https://www.getfeedback.com/r") {
.theme-background-scaled,
textarea.answer-text
{
	background: #2C2C2C !important;
}

textarea.answer-text
{
	border: 1px solid #4C4C4C !important;
}

.presenter-component-content > .component-title.theme-question
{
	color: #CCC !important;
}

.is-iframed-non-website .SurveyNavBar .NavigationButton.NavigationButton--chevron.disabled
{
	filter: invert(100%) !important;
}
}

@-moz-document url-prefix("https://www.mathworks.com/help/") {
.topnav_support > a
{
	background: #4C4C4C !important;
}
}
