css:
@-moz-document domain("ecosia.org") {
.search-section{
background: transparent;
}

.sidebar {
    background: white;
    color: black;
    margin-top: 11px;
}
.search-form-wrapper {
	border-radius: 25px;
    border: none;
}
.search-section .search-form-wrapper {
	border-radius: 25px;
    border: none;
}
.search-section {
	background: url(https://source.unsplash.com/user/anywombat/likes/1920x1080);
}
.background-image[data-v-6b585cf2]{
    display: none;
}
.search-form-input {
	border-radius: 25px;
    z-index: 20;
}
.search-form-button {
	border-radius: 25px;
    z-index: 20;
}
}

@-moz-document url-prefix("https://www.ecosia.org/search?") {
.results-page {
background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.5) 10px, rgba(0, 0, 0, 0.4) 30px, rgba(0, 0, 0, 0.3) 50px, rgba(0, 0, 0, 0.2) 80px, rgba(255, 255, 255, 0.1) 250px, rgba(176, 176, 75, 0) 300px), url(https://source.unsplash.com/user/anywombat/likes/1920x1080);
background-repeat: repeat;
background-attachment: fixed;
}
.result-wrapper {
background:none;
}
body {
	color: black;
}
.card-desktop {
background: white;
border-radius: 0px;
padding-left: 10px;
padding-right: 10px;
}
.entity {
box-shadow: none;
}
.entity-subtitle {
color: black;
}
.navbar-row {
background: none;
border-bottom: none;
}
.navbar-tabs {
	border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    margin-top: 35px;
    margin-bottom: -5px;
}
.navbar-link {
	padding: 10px;
    color: white;
}
.navbar-item-active .navbar-link {
	font-weight: 700;
    color: white;
}
.card-title {
	padding: 0 0 5px;
	font-weight: 400;
	display: none;
}

.search-header{
    padding-bottom: 0px;
    background: none;
    padding-top: 25px;
}
.typeahead{
	border: none;
    border-radius: 25px;
    margin-top: -40px;
    padding-top: 40px;
}
g{
    fill: white;
}
}
