css:
@-moz-document domain(play.pokemonshowdown.com), domain(psim.us) {
.ps-room.ps-room-opaque, .dark .ps-room.ps-room-opaque {
    background: url("http://www.kemecer.com/i/pokemon-wallpaper-legendary-wallpaper-wide.jpg");
    background-size: cover;
  }
  
  .ps-room .battle-controls {
    background: rgba(0, 0, 0, 0.2);
    padding-top: 5px;
  }
  
  button[name="switchSides"], button[name="instantReplay"], .replayDownloadButton, button[name="saveReplay"], button[name="closeAndMainMenu"], button[name="closeAndRematch"], button[name="goToEnd"], button[name="skipTurn"], button[name="setTimer"], button[name="undoChoice"] {
    background: rgba(51, 51, 51, 0.8);
    padding: 3px 8px;
    border: 1px solid black;
    border-radius: 3px;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  button[name="switchSides"]:hover, button[name="instantReplay"]:hover, .replayDownloadButton:hover, button[name="saveReplay"]:hover, button[name="closeAndMainMenu"]:hover, button[name="closeAndRematch"]:hover, button[name="goToEnd"]:hover, button[name="skipTurn"]:hover, button[name="setTimer"]:hover, button[name="undoChoice"]:hover {
    background: rgba(31, 31, 31, 0.8);
    border: 1px solid black;
    box-shadow: 0px 0px 2px black;
  }
  
  button[name="goToEnd"] {
    margin-left: 2px;
  }
  
  .dark .replayDownloadButton {
    color: #EEE !important;
  }
  

  .lstatbar {
    z-index: 1;
    left: 455px !important;
    top: 32px !important; 
  }
  
  .rstatbar {
    z-index: 1;
    left: 10px !important;
    top: 32px !important;   
  }
  
  .statbar .hpbar {
    background: #333;
    width: 151px;
    height: 12px;
    border: 1px solid #000;    
    box-shadow: 0px 0px 2px black inset;
  }
  
.backdrop:after {
    display: inline-block;
    content: "";
    background: url(https://cdn.discordapp.com/attachments/506621363138723842/509076731865333766/maxresdefault_2.jpg) fixed;;
    position: absolute;
    background-position: 00px 55px;
    width: 3500px;
    height: 1800px;
    opacity: 1 !important;
}

  .lstatbar .hpbar {
    border-radius: 0px 0px 20px 0px;
  }
  
  .rstatbar .hpbar {
    border-radius: 0px 0px 0px 20px;
  }
  
  .statbar .hpbar .prevhp {
    height: 12px;
    transition-duration: 0.5s;
    transition-timing-function: linear;
  }
  
  .statbar .hpbar .hp {
    height: 8px;
    transition-duration: 0.2s;
    transition-timing-function: linear;
  }
  
  .lstatbar .hpbar .prevhp {
    border-radius: 0px 0px 20px 0px;
  }
  
  .lstatbar .hpbar .hp {
    border-radius: 0px 0px 20px 0px;
  }
  
  .rstatbar .hpbar .prevhp {
    border-radius: 0px 0px 0px 20px;
  }
  
  .rstatbar .hpbar .hp {
    border-radius: 0px 0px 0px 20px;
  }
  
  .statbar .hpbar .hptext {
    background: #000;
    height: 16px;
  }
  
  .statbar .hpbar .hptextborder {
    border-color: #000;
  }
  
  .turn {
    background: rgba(71, 71, 71, 0.8);
    top: 0 !important;
    left: 275px !important;
    width: 90px;
    height: 80px;
    line-height: 40px;
    padding: 0px;
    border: none;
    border-bottom: 1px solid #000;
    border-radius: 0px;
    color: #AAA;
    text-align: center;
    text-shadow: 0px -1px #000;
    opacity: 1 !important;
    box-shadow: 0px 0px 2px #000;
  }
  
  .weather {
    text-shadow: none;
  }
  
  .weather em {    
    z-index: 1 !important;
    width: 80px;
    margin: 15px auto 0px auto;    
    color: #AAA;
    text-align: center;   
  }
  
  .trainer {
    top: 0;
    opacity: 1 !important;
  }

  .trainer div {
    opacity: 1 !important;
  }
  
  .leftbar {
    background: rgba(51, 51, 51, 0.8);
    width: 275px;
    height: 80px;
    border-bottom: 2px solid #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  .leftbar .trainer strong {
    float: right;
    margin-right: 5px;
    margin-top: 5px;
    color: #EEE;
    text-shadow: 0px -1px #000;
  }
  
  .leftbar .trainer .teamicons {
    float: left;
    position: relative;
    top: -80px;
    left: 0;
  }
  
  .leftbar .trainer .trainersprite {
    position: relative;
    top: 15px;
    left: 0px;
  }
     
  .leftbar .trainer div {
    margin-right: 0;
  }
    
  .rightbar {
    background: rgba(51, 51, 51, 0.8);
    width: 275px;
    height: 80px;
    border-bottom: 2px solid #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  .rightbar .trainer strong {
    float: left;
    margin-left: 5px;
    margin-top: 5px;
    color: #EEE;
    text-shadow: 0px -1px #000;
  }
  
  .rightbar .trainer .teamicons {
    float: right;
    position: relative;
    top: -80px;
    right: 0;
  }
  
  .rightbar .trainer .trainersprite {
    position: relative;
    top: 15px;
    right: 210px;
  }
  
  .rightbar .trainer div {
    margin-right: 0;
  }
  
  .messagebar {
    background: rgba(51, 51, 51,.5);
    bottom: 0;
    left: 0;
    right: 0;
    width: 100%;
    border-top: 2px solid #000;
    border-radius: 0px;
    color: black;
    text-align: center;
  }
  
  .messagebar strong {
    color: black;
  }
  
  .moveselect button {
    color: #7B80B8;
    text-shadow: 1px 1px #373952;
    padding-top: 0px;
    padding-left: 0px;
  }
  
  .movecontrols {
    background: rgba(51, 51, 51, 0.8);
    width: 230px;
    height: 120px;
    position: absolute;
    top: -150px;
    right: 10px;
    padding: 5px;
    padding-top: 0px;
    border-width: 1px;
    border-style: solid;
    border-color: #000;
    border-radius: 7px;
    box-shadow: 0px 0px 2px black inset;
    overflow: hidden;
  }
  
  .movemenu {
    background: #FCFAFB;
    width: calc(100% - 10px);
    padding: 5px;
    border-radius: 7px;
    box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3);
    display: inherit;
  }
  
  .movemenu button {
    width: calc(50% - 4px);
    margin-right: 4px;
    font: 10pt/100% Verdana, sans-serif;
    position: relative;
    padding: 1px 0px 0px 0px !important;
  }
  
  .megaevo, .megaevo:hover {
    background: none;
    border: none;
    float: right;
    top: -115px;
    left: 32px;
    color: #252636;
    text-shadow: 0px 0px 1px #7B80B8;
    display: table-cell !important;
    vertical-align: middle !important;
  }
  
  .switchselect button {
    color: #FA4040;
  }
  
  .switchmenu button::before {
    content: url(http://i.imgur.com/2wCxQO5.png?1);
    position: absolute;
    top: -22px;
    left: 10px;
    width: 80px;
  }
  
  .switchmenu button {
    background: rgba(51, 51, 51, 0.8);
    border-color: #000;
    box-shadow: 0px 0px 2px black inset;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    overflow: visible;
    margin-bottom: 20px;
  }
  
  .switchmenu button:hover {
    background: rgba(31, 31, 31, 0.8);
    border: 1px solid black;
    box-shadow: 0px 0px 2px black;
  }
  
  .switchmenu button .hpbar, .switchmenu button .status {
    border-color: #000;
  }
  
  .switchmenu button:hover {
    background: rgba(51, 51, 51, 0.9);
    border-color: #000;
  }
  
  .switchmenu button.disabled, .switchmenu button:disabled {
    background: rgba(101, 101, 101, 0.5) !important;
    border-color: #000 !important;
    color: #FFF !important;
  }
  
  .switchmenu button.disabled::before, .switchmenu button:disabled::before {
    content: '';
  }
  
  .battle-log .chat strong, .dark .battle-log .chat strong {
    color: #275E94;
  }
  
  .small-layout .battle-chat-toggle {
    background: rgba(51, 51, 51, 0.8);
    padding: 3px 8px;
    border: 1px solid black;
    border-radius: 3px;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  .small-layout .battle-chat-toggle:hover {
    background: rgba(31, 31, 31, 0.8);
    border: 1px solid black;
    box-shadow: 0px 0px 2px black;
  }
  
  .small-layout .battle-chat-toggle {
    z-index: 1;
  }
  
  .battle-controls .whatdo {
    color: #CCC;
  }
  
  .battle-controls .whatdo small {
    border-color: #000;
    color: #000;
  }
  
  .dark .battle-log .message-throttle-notice, .dark .battle-log .message-error {
    color: #992222;
  }
  
  div[id*="battle"].ps-popup {
    background: #333;
    border-color: #000;
    box-shadow: 0px 0px 2px black;
    color: #DDD;
  }
  
  div[id*="battle"].ps-popup a {
    color: #98C0E9;
  }
  
  div[id*="battle"].ps-popup button {
    background: rgba(31, 31, 31, 0.8);
    padding: 3px 8px;
    border: 1px solid black;
    border-radius: 3px;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  div[id*="battle"].ps-popup button:hover {
    background: rgba(17, 17, 17, 0.8);
    border: 1px solid black;
    box-shadow: 0px 0px 2px black;
  }
  
  #tooltipwrapper {
    text-align: left;
    color: #DDD;
    text-shadow: 0px 0px 2px #000;
  }

  #tooltipwrapper .tooltip {
    width: 300px;
    background: #333;    
    border-color: #000;
    border-radius: 3px;
    box-shadow: 0px 0px 2px black inset, 0px 0px 5px rgba(0, 0, 0, 0.4);
  }

  #tooltipwrapper .tooltip h2 {
    padding: 8px 15px;
    margin: 0;
    border-bottom: 1px solid #000;
    font-size: 10pt;
    color: #FFF;
    text-shadow: 0px 0px 2px #000;
  }

  #tooltipwrapper .tooltip p {
    padding: 4px 8px;
    margin: 0;
    font-size: 8pt;
  }

  #tooltipwrapper .tooltip p.section {
    border-top: 1px solid #000;
  }

/*********************************************************
 * Main menu
 *********************************************************/

.leftmenu {padding: 40px 0 0 0;width: 300px;
}
.rightmenu {position: absolute;top: 40px;right: 35px;
}
.activitymenu {position: absolute;left: 325px;top: 40px;
}
.mainmenu {padding: 0 0 40px 0;
}

.battleform {width: 230px;text-align: left;margin: 0 auto;
}
.battleform .buttonbar {text-align: center;
}

.mainmenuwrapper {position: relative;min-height: 100%;
}
.mainmenufooter {position: absolute;bottom: 15px;left: 20px;color: #BBBBBB;text-shadow: 0 0 1px black, 0 0 1px black, 0 0 1px black;
}
.mainmenufooter blockquote {margin: 0 0 1em 0;padding: 0;
}
.mainmenufooter small {font-size: 8pt;
}
.mainmenufooter a {text-decoration: underline;color: #FFFFFF;
}
.mainmenufooter a:hover {text-decoration: underline;color: #99BBFF;
}

.pmbox {width: 270px;margin: 0 auto;min-height: 2px;
}
.pm-window {margin-bottom: 12px;
}
.pm-window h3 {background: #000000;color: #ffffff;margin: 0;padding: 7px 10px;font-size: 8pt;border: 2px solid #004ce7;
    border-top-left-radius: 20px;border-top-right-radius: 20px;cursor: pointer;
}
.pm-window h3:hover {color: #000000;
}
.pm-window h3.pm-minimized {border-radius: 0px;
}
.pm-window h3.pm-notifying {border-color: #004ce7;background: #000000;
}
.pm-window h3.pm-notifying:hover {border-color: #604020;background: #000000;
}
.pm-window h3 .closebutton,
.pm-window h3 .minimizebutton {
	float: right;
	padding-right: 30px;
}
.pm-window h3 small {color: #ff2727;font-weight: normal;vertical-align: 10px;
}
.pm-log {min-height: 100px;max-height: 300px;background: #000000;color: #aaa;word-wrap: break-word;border: 2px solid #004ce7;border-top: 0;
	border-bottom: 0;overflow: auto;-webkit-overflow-scrolling: touch;overflow-scrolling: touch;
}
.pm-log-add {border: 2px solid #004ce7;border-bottom-left-radius:20px;border-bottom-right-radius: 20px;background: #000000;
}
.pm-window.collapsed .pm-log,
.pm-window.collapsed .pm-log-add {display: none;
}
.pm-window.focused h3,
.pm-window.focused .pm-log,
.pm-window.focused .pm-log-add {border-color: #004ce7;
}
.pm-window.focused h3,
.pm-window.focused h3:hover {background: #ff3e3e;color: #222222;cursor: default;
}
.challenge {margin-top: -1px;background: url("https://static.wixstatic.com/media/10af1d_9c5d75e111e24843917e33787320ac06.png?dn=FIAL+LONE.png");
	border: 1px solid #004ce7;color: #fff;padding: 1px 5px;
}
.challenge p {margin: 1px 0;
}

.tiny-layout .leftmenu,
.tiny-layout .rightmenu,
.tiny-layout .activitymenu {position: static;top: 0;right: 0;left: 0;width: auto;margin: 0;padding: 0;
}
.tiny-layout .leftmenu {padding-top: 30px;
}
.tiny-layout .activitymenu {padding-bottom: 8px;
}

.mainmessage {display: block;padding: 10px 15px;margin: 30px 40px;max-width: 400px;border-radius: 20px;background: rgba(201, 207, 219, .8);
	color: black;
}
.menugroup {display: block;padding: 10px 0;margin: 0 27px 30px 27px;max-width: 400px;border-radius: 30px;color: white;
    text-shadow: 0 1px 0 black;
}
.menugroup {background: #000000;text-align: center;
}

.mainmessage p,
.menugroup p {
	margin: 10px 0;
}
