css:
@-moz-document url-prefix("http://localhost/phpmyadmin"), domain("demo.phpmyadmin.net") {

/*[[theme]]*/

#serverinfo {
    background: var(--lbg);
    text-shadow: none;
}
#pma_navigation_collapser {
    background: var(--lbg);
    border: none;
    color: var(--txt);
    z-index: 0;
}

#pma_navigation_tree li.activePointer {
    background: var(--lbg);
}

ul#topmenu,
ul#topmenu2,
ul.tabs,
#topmenu a,
ul#topmenu > li,
.menucontainer {
    background: var(--lbg);
    text-shadow: none;
    border: none;
    color: var(--txt);
    margin-top: 0;
    font-size: 20px;
}

ul#topmenu > li > a:hover,
ul#topmenu > li > .tabactive {
    background: var(--lbg) !important;
    text-shadow: 0 0 1px white;
}

ul#topmenu2 a,
ul#topmenu2 a.tab:hover,
ul#topmenu2 a.tabactive {
    background: var(--lbg) !important;
    border-radius: 0;
    border: none;
    margin-top: 0;
    height: 15px;
}

ul#topmenu2 a.tabactive,
ul#topmenu2 a.tab:hover {
    text-shadow: 0 0 1px #235a81;
}

body {
    background: var(--bg);
    color: var(--txt);
    overflow-x: hidden;
}

h2 {
    text-shadow: none;
    color: var(--ttl);
}
.exportoptions h3,
.importoptions h3 {
    border: none;
}

h1.notice,
div.notice,
p.notice,
.ajax_notification {
    border: none;
    border-radius: 0;
    box-shadow: none;
    color: var(--txt);
    background: var(--lbg);
}

#maincontainer,
#page_content {
    background: var(--bg);
    color: var(--txt);
    margin-left: 20px;
    margin-top: 30px;
}

fieldset legend,
fieldset,
.preview_sql,
div.tools,
.tblFooters {
    border: none;
    border-radius: 0;
    box-shadow: none;
    background: var(--bg);
    color: var(--txt);
    width: 100%;
    padding-left: 0;
}

table tr th,
table tr,
table td,
thead,
div#page_content div#tableslistcontainer table.data,
#div_mysql_charset_collations table th,
#div_mysql_charset_collations table td {
    background: var(--lbg) !important;
    color: var(--txt);
    text-shadow: none;
    border: none;
    padding: 5px;
}

th {
    font-size: 14px;
    text-align: left;
}

table {
    box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.5);
}

td.marked:not(.nomarker),
table tr.marked:not(.nomarker) td,
table tbody:first-of-type tr.marked:not(.nomarker) th,
table tr.marked:not(.nomarker) {
    background: darkgrey;
}

.CodeMirror-scroll,
.CodeMirror-sizer,
.CodeMirror-gutter,
.CodeMirror-gutters,
.CodeMirror-linenumber {
    border: none;
    font-size: 15px;
}
fieldset fieldset,
#fieldset_add_user_login div.item {
    background: var(--lbg);
    border: none;
}

div.success,
div.notice,
div.error,
.error {
    border-radius: 0;
    box-shadow: none;
    border: none;
    color: var(--txt);
}
div.success {
    border-left: 5px solid lime;
    background: var(--lbg);
}
div.error {
    border-left: 5px solid red;
    background: var(--lbg);
}


#pma_navigation_content {
    background: var(--bg);
    box-shadow: 2px 0 9px 0px black;
    color: var(--txt);
}

#pma_navigation_tree a {
    color: var(--txt);
}
#pma_navigation_tree li.selected {
    background: var(--lbg);
}

.group,
.group h2 {
    border: none;
    box-shadow: none;
    text-shadow: none;
    border-radius: 0;
    background: var(--bg);
}

.group h2 {
    color: var(--ttl);
}

#pma_navigation_resizer {
    background: var(--bg);
}

#selflink {
    visibility: hidden;
}

span {
    color: var(--txt);
}

#pma_console .toolbar {
    background: var(--lbg);
    border: none;
    height: 35px;
    font-size: 23px;
}

#pma_console .message span.action:hover,
#pma_console .toolbar .button:hover,
#pma_console .switch_button:hover,
#pma_console .toolbar .button.active {
    background: var(--lbg);
}

#pma_console .content {
    background: var(--dbg);
    border: none;
    color: var(--txt);
}

.CodeMirror-scroll,
.CodeMirror-sizer,
.CodeMirror-gutter,
.CodeMirror-gutters,
.CodeMirror-linenumber {
    background: var(--dbg);
}

#pma_console .message,
#pma_console .query_input {
    color: var(--txt);
}

select,
select[multiple],
#select_seriesColumn,
#select_valueColumn {
    background: var(--dbg);
    border: none;
    border-radius: 0;
    height: auto;
    color: var(--txt);
}

#select_seriesColumn[disabled],
#select_valueColumn[disabled] {
    background: grey;
}

select:focus,
input[type=submit],
input[type=button],
input.button,
input[type=submit]:hover,
input[type=button]:hover,
.exportoptions #buttonGo,
.importoptions #buttonGo {
    height: auto;
    background: var(--dbg);
    border: none;
    border-radius: 0;
    text-shadow: none;
    color: var(--txt);
}
div#tablefieldscontainer select {
    background: var(--lbg);
}
#fieldset_user_priv div.item select {
    background: var(--dbg);
    border: none;
    border-radius: 0;
    text-shadow: none;
    color: var(--txt);
    height: auto;
}

.pma_quick_warp .drop_button,
.pma_quick_warp .drop_list:hover .drop_button,
.pma_quick_warp .drop_list li,
.pma_quick_warp .drop_list ul {
    background: var(--dbg);
    border: 4px solid;
    border-color: var(--dbg);
    border-radius: 0;
    color: var(--txt);
    box-shadow: none;
}

li.fast_filter input,
input[type=text]:hover,
input[type=password]:hover,
input[type=number]:hover,
input[type=date]:hover,
input[type=text],
input[type=password],
input[type=number],
input[type=date],
input[type=text]:hover,
input[type=text]:focus,
input[type=password]:hover,
input[type=password]:focus,
input[type=number]:hover,
input[type=number]:focus,
input[type=date]:hover,
input[type=date]:focus,
button[type=submit]:not(.mult_submit),
input[type=reset],
input[name=submit_reset],
button[type=submit]:not(.mult_submit):hover,
input[type=reset]:hover,
input[name=submit_reset]:hover,
input.button:hover,
#sectionlinks a,
#sectionlinks a:hover,
.buttonlinks a,
a.button {
    background: var(--dbg);
    box-shadow: none;
    text-shadow: none;
    height: auto;
    font-size: 12px;
    color: var(--txt) !important;
    border: none;
    border-radius: 0;
}

code.sql,
div.sqlvalidate {
    background: var(--lbg);
    font-size: 14px;
}

.result_query div.sqlOuter {
    background: var(--lbg);
}

#sectionlinks {
    border: none;
    border-radius: 0;
    background: var(--lbg);
    box-shadow: none;
}

.tblHeaders {
    color: var(--txt);
    background: var(--lbg);
    border: none;
    text-shadow: none;
}

td.print_ignore {
    background: var(--lbg) !important;
    visibility: hidden;
}

#canvas,
#name-panel,
.side-menu {
    background: var(--lbg) !important;
    border: none;
}

#osn_tab {
    background: var(--lbg);
}

.pmd_tab {
    border: 1px solid;
    border-color: var(--txt);
}

a.M_butt_Selected_down_IE:hover,
a.M_butt_Selected_down:hover,
a.M_butt:hover {
    background: var(--lbg);
}

.ui-dialog .ui-dialog-content,
.ui-widget.ui-widget-content,
.ui-dialog .ui-dialog-buttonpane,
.ui-draggable .ui-dialog-titlebar {
    border: none;
    border-radius: 0;
    background: var(--bg);
}

.ui-widget.ui-widget-content {
    box-shadow: 2px 2px 11px 0 black;
}

.ui-widget-overlay {
    background: black;
}

.ui-dialog .ui-dialog-buttonpane button {
    background: var(--dbg);
    border: none;
    border-radius: 0;
    color: var(--txt);
}

.tooltip,
.ui-widget-shadow {
    box-shadow: none;
    text-shadow: none !important;
    border: none !important;
    border-radius: 0 !important;
    background: black !important;
}
}
