css:
@-moz-document url-prefix("https://twitter.com/") {
.global-nav {
    box-shadow: 0 1px 15px black;
    border: none;
}
.new-tweets-bar {
    border: none;
}
.new-tweets-bar:hover {
    border: none;
}
.AdaptiveRelatedSearches {
    border: none;
}
.TwitterCard .SummaryCard-image {
    border-bottom: none;
}
.TwitterCard .MomentCard-cover {
    border: none;
}
.PlayableMedia--hasMetadata .PlayableMedia-player {
    border: none;
}
.TwitterCard .PlayerCard--video .PlayerCard-videoContainer {
    border: none;
}
.btn:focus,
.btn.focus,
.Button:focus,
.EmojiPicker-item.is-focused,
.EmojiPicker .EmojiCategoryIcon:focus,
.EmojiPicker-skinTone:focus + .EmojiPicker-skinToneSwatch,
a:focus > img:first-child:last-child,
button:focus {
    box-shadow: none;
}
.DashboardProfileCard,
.DashboardProfileCard-avatarLink,
.DashboardProfileCard-avatarImage {
    border: none;
}
.module .flex-module {
    border: none;
}
.Trends {
    border: none;
}
.top-timeline-tweetbox .timeline-tweet-box {
    border: none;
}
.PermalinkOverlay-modal {
    border-radius: 0;
}
.tweet {
    border: none;
    border-bottom: 5px solid #10171e;
}
.stream-item+.stream-item.separated-module:not(.no-header-background-module), .stream-item.separated-module+.stream-item:not(.no-header-background-module), .PromptbirdPrompt-streamItem.separated-module+.stream-item:not(.no-header-background-module) {
    border: none;
}
.stream-item:not(.no-header-background-module) {
    border: none;
}
.wtf-module .UserSmallListItem {
    border: none;
}
.permalink .inline-reply-tweetbox {
    border: none;
}
.ThreadedConversation--header {
    border: none;
}
.tweet .stats {
    border: none;
}
.permalink .stats .avatar-row a:first-child {
    border: none;
}
.TwitterCard[data-theme="night-mode"] .TwitterCard-container {
    border-radius: 2px;
    border: none;
    background-color: rgba(0, 0, 0, 0.3)
}
.SummaryCard-image.TwitterCardsGrid-float--prev {
    border: none;
}
.Gallery-content {
    border-radius: 0;
}
.GalleryTweet .content .stream-item-footer {
    border: none;
}
.PermalinkOverlay .permalink {
    border-radius: 0;
    border: none;
    box-shadow: 2px 2px 11px 0 black;
}
.ThreadedConversation,
.ThreadedConversation-moreReplies:after {
    border: none;
}
.ProfileHeading-content {
    border: none;
    background-color: #15202b;
    margin-bottom: 10px;
}
.permalink .in-reply-to .tweet,
.permalink .replies-to .tweet,
.permalink .replies-to .ThreadedConversation--loneTweet .stream-tombstone-item {
    border: none;
}
.ThreadedConversation--header {
    margin-top: 0;
}
.ThreadedConversation-showMoreThreads {
    margin-top: 0;
}
.permalink.has-replies .inline-reply-tweetbox {
    border: none;
}
#global-actions > li > a {
    border: none;
}
.EdgeButton,
.EdgeButton:visited,
.global-nav .search-input,
.RichEditor,
global-nav .search-input:focus,
.global-nav .search-input.focus {
    border: none;
    border-radius: 22px;
}
.not-following .follow-button .follow-text,
.following .follow-button .following-text,
.following .follow-button:hover .unfollow-text,
.following .follow-button.cancel-hover-style:hover .following-text,
.blocked .follow-button .blocked-text,
.blocked .follow-button:hover .unblock-text,
.pending .follow-button .pending-text,
.pending .follow-button:hover .cancel-text,
.pending .follow-button.cancel-hover-style:hover .pending-text {
    background-color: rgba(255, 255, 255, 0.1);
}
.PlayableMedia-container {
    border: none;
    border-radius: 0;
}
.AdaptiveMedia {
    border-radius: 2px;
}
.AdaptiveMedia-video {
    border-radius: 0;
}
.AdaptiveMedia.is-square:not(.is-video) {
    border: none;
}
.AdaptiveMedia-photoContainer img {
    background: none;
}
.PermalinkOverlay-spinnerContainer {
    border-radius: 0;
    box-shadow: 2px 2px 11px 0 black;
}
.content-header,
.content-no-header {
    border: none;
}
.NotificationsHeadingContent {
    border: none;
    border-bottom: 5px solid #10171e;
    background-color: #283441;
}
.ActivityItem {
    border-bottom: 5px solid #10171e;
}
.DMInboxItem {
    border: none;
}
.DMActivity-header {
    border: none;
    background-color: #283441;
    border-radius: 0;
}

.DMActivity {
    border-radius: 0;
    box-shadow: 2px 2px 11px 0 black;
}
.DMComposer-container {
    border: none;
    border-radius: 22px;
}
.DMConversation-composer {
    border: none;
}

.tweet-box textarea:focus,
.tweet-box input[type=text],
.currently-dragging .tweet-form.is-droppable .tweet-drag-help,
.tweet-box[contenteditable="true"]:focus,
.RichEditor.is-fakeFocus,
.RichEditor.is-fakeFocus ~ .TweetBoxAttachments {
    box-shadow: none;
}

.QuoteTweet {
    border: none;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 2px;
}

#global-tweet-dialog .modal-header,
#Tweetstorm-dialog .modal-header,
#global-tweet-dialog .modal-tweet-form-container,
#Tweetstorm-dialog .modal-body,
#Tweetstorm-dialog .modal-body {
    border-radius: 0;
    border: none;
}

.SidebarFilterModule {
    border: none;
}

.SidebarFilterModule-filters .SidebarFilterModule-select {
    border: none;
    border-radius: 0;
}

.AdaptiveSearchTimeline .stream .stream-item:first-child {
    border: none;
}

.modal-content {
    box-shadow: 2px 2px 11px 0 black;
    border-radius: 0;
}
.dashboard.dashboard-right {
    visibility: visible;
}
.dashboard.dashboard-left {
    visibility: visible;
}
.Trends {
    visibility: visible;
}
.DashboardProfileCard, .DashboardProfileCard-avatarLink, .DashboardProfileCard-avatarImage {
    visibility: visible;
}
}

@-moz-document url-prefix("https://twitter.com/") {
:root {
    --bg: #0F111A;
    --fg: #111923;
}

header, html,  main {
    background: var(--bg);
}

article {
    border: none !important;
    border-bottom: 10px solid var(--bg) !important;
}

.r-utggzx {
    background: var(--fg) !important;
}

.r-1h3ijdo {
        box-shadow: 0px 3px 5px 0px #00000015;
}

.r-18bvks7 {
    border: none !important;
}

.r-136ojw6 {
    background: none;
}

.r-1siec45 {
    box-shadow: 0 1px 15px rgba(0, 0, 0, 0.5);
}
.r-109y4c4 {
    height: 0;
}
.r-qklmqi {
    border-bottom: 0;
}
.r-jh177g {
    height: 2rem;
    transform: translateY(-1.2rem);
    margin-bottom: -0.9rem !important;
}
.r-m611by {
    padding-bottom: 2%;
    margin-bottom: 0;
}
.r-t23y2h, .r-16hm58k {
    border: 0;
    border-radius: 2px;
    background: #111923;
}
.r-5kkj8d {
    border: 0;
}

.r-1or9b2r {
    background-color: var(--bg);
}
}
