css:
@-moz-document url-prefix("https://trello.com/b"), url-prefix("https://trello.com/c") {
/*
    @author: Amer Bakeer
    @date: 25 Oct 2017
    */
    /*■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■*/
    /* Plugin Name: Next Step for Trello
    /*■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■*/
    .aj-next-step {
        font-family: inherit !important;
        font-weight: normal !important;
        color: #000;
        direction: rtl;
        text-align: start;
        unicode-bidi: plaintext;
    }
    .aj-next-step .checklist-item-checkbox
    {
        background-color: transparent;
        border: 2px solid;
        border-radius: 0;
        width: 11px !important;
        height: 11px !important;
        top: -4px !important;
        left: -5px !important;
        opacity:0.5;
    }
    .aj-next-step span.aj-checkbox-tick
    {
      color: #000;
    }

    .aj-next-step > .aj-checkbox-tick {
        top: -2px;
        left: 3px;        
    }
    .aj-next-step > .aj-checkbox-tick::before {
        font-size: 14px;
        content: "✔";
        font-weight: bold;
    }




    /*■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■*/
    /* Plugin Name: Pro for Trello, FREE Trello tweaks
    /*■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■*/
    .tpro-list-stats
    {
      background-color: rgba(0,0,0,0.75);
      padding-bottom: 2px;
      height: auto !important;
    }
    .tpro-list-stats span.tpro-stat
    {
      box-shadow: none !important;
    }
    .tpro-list-stats .progress-bar-wrapper
    {
        width: 120px;
        float: left;
    }

    .list-card.tpro-priority-normal {
        border-left: 0;
    }
    .list-card.tpro-priority-medium,
    .list-card.tpro-priority-high,
    .list-card.tpro-priority-critical
    {
        border-left-width: 6px;
    }
    .list-card.tpro-priority-high,
    .list-card.tpro-priority-critical
    {
        border-left-color: #EB5A46;
    }

    .checklist-progress-percentage {
        color: #fff;
    }
    .tpro-settings-banner {
        display: none !important;
    }
    .tpro-settings-wrapper .tpro-settings-header label.switch {
        margin-top: 2px;
    }
  .badge.is-due-past {
      background-color: #d60f0f;
  }
}
