css:
@-moz-document domain(forums.marriland.com) {

html {
    background: #333;
}

.p-sectionLinks-list .p-navEl a {
    transition: .2s ease-in-out;
}

a {
    color: rgba(230, 230, 230);
}

a:hover {
    color: rgba(230, 230, 230, .5);
}

.memberTooltip-name a {
    color: rgba(230, 230, 230) !important;
}

.memberTooltip-name a:hover {
    color: rgba(230, 230, 230, .5) !important;
}

.block-minorHeader a:hover {
    color: rgba(230, 230, 230, .5);
}

.block-minorHeader,
.block-minorHeader a {
    color: rgb(230, 230, 230);
}

.node-description,
.node--unread .node-icon i:before,
.subNodeLink.subNodeLink--unread:before,
.contentRow-minor,
.node-extra-row {
    color: rgb(230, 230, 230);
}

.p-header,
.p-nav,
.p-body,
.p-footer {
    background: #333;
    color: rgb(230, 230, 230);
}

.p-sectionLinks,
.p-nav-list .p-navEl.is-selected,
.menu-content,
.block-container,
.overlay,
.menu-row {
    background: #4d4d4d;
    color: rgb(230, 230, 230);
    transition: .2s ease-in-out;
}

.block-container,
.overlay,
.tooltip--member .tooltip-content {
    border: 1px solid #8c8c8c;
}

.p-sectionLinks-list .p-navEl:hover,
.p-nav-list .p-navEl.is-menuOpen {
    color: rgba(230, 230, 230, .8);
}

.p-nav-list .p-navEl.is-selected:hover,
.menu-linkRow.is-selected,
.menu-linkRow:hover,
.menu-linkRow:focus,
.menu-linkRow.menu-linkRow--alt:hover {
    background: #666;
    color: rgba(230, 230, 230, .8);
}

.p-sectionLinks {
    border-bottom: 1px solid #8c8c8c;
}

.p-nav-list .p-navEl {
    background: #666;
    color: rgba(230, 230, 230, .5);
    transition: .2s ease-in-out;
}

.p-nav-list .p-navEl:not(.is-selected):not(.is-menuOpen):hover,
.p-nav-list .p-navEl:not(.is-selected):not(.is-menuOpen) .p-navEl-link:hover,
.p-nav-list .p-navEl:not(.is-selected):not(.is-menuOpen) .p-navEl-splitTrigger:hover {
    background-color: #808080;
    color: rgb(230, 230, 230);
}

.p-footer {
    border-top: 1px solid #8c8c8c;
}

.node + .node,
.formRow.formRow--fullWidth + .formRow,
.formRow + .formRow.formRow--fullWidth,
.formRow.formRow--fullWidth + * > .formRow:first-of-type,
.formRow + * > .formRow.formRow--fullWidth:first-of-type {
    border-top: 1px solid #737373;
}

.block-footer {
    background: #666;
    border-top: 1px solid #737373;
    color: rgb(230, 230, 230);
}

.block-header,
.overlay-title,
.block-filterBar {
    background: #666;
    border-bottom: 1px solid #737373;
}

.input,
.input:focus,
.input.is-focused {
    background: #666;
    border: 1px solid #8c8c8c;
}

.button,
a.button,
.button.button--link,
a.button.button--link,
.button.is-disabled,
a.button.is-disabled,
.button.button--link:focus,
a.button.button--link:focus {
    background: rgb(230, 230, 230);
    color: #333;
    opacity: 1;
    transition: opacity .2s ease-in-out;
    border-color: #ccc;
}

.button:hover,
a.button:hover,
.button.button--link:hover,
a.button.button--link:hover {
    opacity: .5;
    background: rgb(230, 230, 230);
    border-color: #8c8c8c;
}

.button.is-disabled:hover,
a.button.is-disabled:hover,
.button.is-disabled:active,
a.button.is-disabled:active,
.button.is-disabled:focus,
a.button.is-disabled:focus {
    opacity: .5;
    background: rgb(230, 230, 230) !important;
    border-color: #8c8c8c;
}

.p-footer a {
    color: rgba(230, 230, 230, .5);
}

.p-footer a:hover {
    color: rgb(230, 230, 230);
}

.menu-linkRow.menu-linkRow--alt {
    background: #4d4d4d;
    color: rgb(230, 230, 230);
    border-bottom: 1px solid #737373;
    text-align: center;
}

.menu-linkRow {
    color: rgb(230, 230, 230)
}

.memberTooltip-header {
    background: #666;
    border-bottom: 1px solid #737373;
}

.memberTooltip-info,
.memberTooltip-actions {
    background: #4d4d4d;
}

.tooltip--member.tooltip--top .tooltip-arrow {
    border-top-color: #8c8c8c;
}

.tooltip--member.tooltip--top .tooltip-arrow:after {
    border-top-color: #4d4d4d;
}

.memberTooltip-separator,
.formRowSep {
    border-color: #737373;
}

.pairs > dt,
.tooltip--member .tooltip-content {
    color: rgb(230, 230, 230)
}

.u-concealed,
.u-concealed a,
.u-cloaked,
.u-cloaked a {
    color: rgb(230, 230, 230) !important;
}

.u-concealed:hover,
.u-concealed a:hover,
.u-cloaked:hover,
.u-cloaked a:hover {
    color: rgba(230, 230, 230, .5) !important;
}

.blockStatus,
.message-cell.message-cell--user,
.message-cell.message-cell--action,
.bbCodeInline {
    background: #666;
    border-color: #8c8c8c;
    color: rgb(230, 230, 230);
}

.message-userArrow {
    border-right-color: #8c8c8c;
}

.message-userArrow:after {
    border-right-color: #4d4d4d
}

.block--messages .message,
.block--messages .block-row {
    border-color: #8c8c8c;
    background: #4d4d4d;
}

.message-attribution,
.likesBar {
    border-color: #8c8c8c;
}

.likesBar,
.structItem-pageJump a,
.pageNav-page.pageNav-page--current {
    background: #666;
    color: rgb(230, 230, 230);
}

.tabs--standalone,
.smilieText {
    background: #666;
    border-color: #8c8c8c;
    color: rgba(230, 230, 230, .5);
    transition: .2s ease-in-out;
}

.tabs--standalone .tabs-tab.is-active,
.tabs--standalone .tabs-tab:hover {
    color: rgb(230, 230, 230)
}

.structItem-pageJump a,
.menu-separator,
.memberHeader-separator,
.block-formSectionHeader {
    border-color: #8c8c8c;
}

.structItem-pageJump a:hover,
.block-row.block-row--alt,
.pageNav-page {
    opacity: 1;
    background: #4d4d4d;
}

.structItem,
.message + .message,
.message.message--bordered,
.block-row.block-row--separated + .block-row,
.dataList-cell,
.block-body + .block-minorHeader,
.pageNav-page,
.pageNav-page:not(:first-child) {
    border-color: #737373;
    color: rgba(230, 230, 230, .5);
    transition: .2s ease-in-out;
}

.message-responseRow,
.memberOverviewBlock-seeMore,
.blockLink.is-selected,
.blockLink:hover,
.bbCodeDemoBlock-item > dd,
.menu-header,
.menu-footer,
.pageNav-page.pageNav-page--current,
.pageNav-page.pageNav-page--current:hover,
.pageNav-page.pageNav-page--current:active,
.pageNav-page:hover,
.pageNav-page:active,
.pageNav-jump,
.menu-row.menu-row--highlighted,
.formSubmitRow-bar,
.block-tabHeader,
.memberHeader-main,
.formRow > dt,
.fr-toolbar,
.fr-box.fr-basic .fr-wrapper,
.menu-row.menu-row--clickable:hover {
    background: #666;
    border-color: #8c8c8c;
    color: rgb(230, 230, 230);
}

.comment-actionBar .actionBar-set,
.memberOverviewBlock-seeMore,
.blockLink:hover,
.blockLink.is-selected {
    color: rgb(230, 230, 230);
}

.blockLink {
    color: rgba(230, 230, 230, .5);
}

.bbCodeBlock-title,
.dataList-row.dataList-row--header .dataList-cell,
.dataList-row.dataList-row--subSection .dataList-cell,
.dataList-cell.dataList-cell--alt,
.dataList-cell.dataList-cell--action,
.menu-row.menu-row--alt:hover {
    background: #666;
    border-color: #737373;
    transition: .2s ease-in-out;
}

.bbCodeBlock,
.smilieText:hover,
.menu-row.menu-row--alt,
.block-tabHeader .tabs-tab.is-active {
    background: #4d4d4d;
    border-color: #8c8c8c;
}

.message-inner,
.message + .message {
    color: rgb(230, 230, 230);
}

.pageNav-jump:hover,
.pageNav-jump:active,
.block-filterBar .filterBar-filterToggle,
.message-signature {
    background: #4d4d4d;
    border-color: #737373;
}

.button.button--primary,
a.button.button--primary {
    background: rgb(230, 230, 230);
    border-color: #ccc;
}

.p-navgroup-link.is-menuOpen,
.p-sectionLinks-list .p-navEl.is-menuOpen {
    color: rgb(230, 230, 230);
}

.p-navgroup-link.is-menuOpen:hover,
.p-sectionLinks-list .p-navEl.is-menuOpen:hover,
.fr-toolbar .fr-command.fr-btn.fr-disabled,
.fr-popup .fr-command.fr-btn.fr-disabled {
    color: rgba(230, 230, 230, .5);
}

.fr-separator {
    background: #8c8c8c;
}

.notice.notice--primary {
    background: #666;
    color: rgb(230, 230, 230);
}

.notice {
    border: none;
    border-top: 1px solid #8c8c8c;
}

.overlay-content > .blocks > .block:last-child .block-outer.block-outer--after {
    border-color: #8c8c8c;
}

.contentRow-fader {
    background: linear-gradient(to bottom, rgba(8, 85, 141, 0) 0%, #666 80%);
}

.inputGroup.inputGroup--joined .inputGroup-text,
.inputGroup.inputGroup--joined .inputGroup-text + .input {
    background: #666;
    border-color: #8c8c8c;
}

.select2-results__options {
    background: #666;
    border-color: #8c8c8c;
    color: rgb(230, 230, 230);
}

.select2-results__option.select2-results__option--highlighted {
    background: #8c8c8c;
}

.select2 .select2-selection ul .select2-search .select2-search__field {
    color: rgb(230, 230, 230);
}

.select2 .select2-selection ul > li.select2-selection__choice {
    background: #4d4d4d;
    border-color: #8c8c8c;
}



/** Mobile **/
@media (max-width: 650px) {

    .offCanvasMenu--nav .offCanvasMenu-content {
        color: rgba(230, 230, 230);
        background: #333;
    }

    .offCanvasMenu--nav .offCanvasMenu-linkHolder.is-selected {
        color: rgb(230, 230, 230);
        background: #808080;
    }

    .pageNavSimple-el.pageNavSimple-el--prev,
    .pageNavSimple-el.pageNavSimple-el--next,
    .pageNavSimple-el.pageNavSimple-el--first,
    .pageNavSimple-el.pageNavSimple-el--last,
    .pageNavSimple-el.pageNavSimple-el--current:hover {
        background: #666;
        color: rgb(230, 230, 230);
        opacity: .5;
        border-color: #8c8c8c;
    }

    .pageNavSimple-el.pageNavSimple-el--prev:hover,
    .pageNavSimple-el.pageNavSimple-el--next:hover,
    .pageNavSimple-el.pageNavSimple-el--first:hover,
    .pageNavSimple-el.pageNavSimple-el--last:hover,
    .pageNavSimple-el.pageNavSimple-el--current {
        background: #666;
        opacity: 1;
        border-color: #8c8c8c;
        color: rgb(230, 230, 230);
    }

    .message:not(.message--forceColumns) .message-userArrow,
    .message:not(.message--forceColumns) .message-cell.message-cell--user {
        border-bottom-color: #8c8c8c;
    }

    .message:not(.message--forceColumns) .message-userArrow:after {
        border-bottom-color: #4d4d4d;
    }
}

}
