@-moz-document url-prefix(https://www.google.) {
  
/*Gmail Images Notifications & all apps opacity*/
.Ne6nSd, .Ne6nSd.gb_Ag.gb_Ua, .gb_Ra, .gb_Hg.gb_Ta, .gb_Ta {opacity: 0.3; transition: .3s ease-out !important;}
.Ne6nSd:hover, .gb_Hg.gb_Ta:hover, .gb_Ag.gb_Ua:hover, .gb_Ra:hover, .gb_Ta:hover {opacity: 1; transition: .6s ease-out !important;}

/*Hide Crap*/
.b0KoTc, #SIvCob, .Q8LRLc, .FPdoLc, #fsl, #fsr > a, .c93Gbe {display: none !important;}

/*Search Bar simplify*/
body, #appbar, .big .mw, #cnt, .srp #gs_st0, #searchform.big .RNNXgb, .RNNXgb, #hdtbSum, .sfbg, .Lj9fsd, .yg51vc, .dXdtIf, .kp-blk, .MGqjK,  .aajZCb, .jZWadf, .o6juZc {background: #f1f3f4 !important;}

a.aciXEb, #gb, .yyjhs, .FalWJb, .GHDvEf, .I6TXqe, .rl_item_base, .hqwb1b, .tsp-dsl, .qe1Dgc, .v7ex9d, .Ffrp4e, .frDyzc, .K5xnQc {background: transparent !important;}
  .p0tlZe {outline: 20px solid transparent !important;}

/*Search*/
.o6juZc, .zthhpf, .RNNXgb, .sbibod {box-shadow: 0 0px 0px 0 rgba(0,0,0,0.16),0 1px 0 0 rgba(0,0,0,0.09) !important; border-radius: 0 !important; border: 1px !important;}
.qcTKEe, .zthhpf, .sbib_a, .sbico-c, .s8GCU, .ECgenc, .lgJJud, .FoDaAb {background: transparent !important;}
div.gstl_51, div.gstl_52, .dG2XIf, .Ww4FFb, .ZXb90b, .kuydt {background: transparent !important;}
.JUypV {display: none !important;}
.sbico-c {top: 1px !important;}

/*Simpler Google Search, I'm Feeling Lucky & Tools button*/
#hdtb-msb .hdtb-tl-sel, #hdtb-msb .hdtb-tl-sel:hover {background: #e0e0e0 !important; box-shadow: none !important;}
#hdtb-tls:hover {box-shadow: none; background-image: none;}
.jhp input[type="submit"]:hover, .sbdd_a input:hover, .gbqfba:hover {background-image: none !important; box-shadow: none !important;}
.jhp input[type="submit"], .sbdd_a input, .gbqfba {background-image: none !important;}

/*Footer*/
#fbar {opacity: 0.3; transition: .3s ease-out !important; background: #f1f3f4 !important; border-top: 0 !important; padding-bottom: 10px !important;}
#fbar:hover {opacity: 1; transition: .6s ease-out !important;}
.b2hzT {border-bottom: 0 !important;}
}
