@-moz-document domain("gogoanime.in")
{
html, body {
      background-color: black !important;
      color: red !important;
      background: #000 !important;
      background:url(https://steamuserimages-a.akamaihd.net/ugc/924795650018658391/4B6A2F61A04887F0DA4B01462CE19459311F9005/)#000 fixed!important;
      background-size:100% 100%!important
}
.ad {
      width: 1px !important;
      height: 1px !important;
      border: none !important;
      display: none !important;
      background: transparent !important;
}
img#logo {
      width: 1px !important;
      height: 1px !important;
      border: none !important;
      display: none !important;
      background: transparent !important;
}
video {
      width: 1px !important;
      height: 1px !important;
      border: none !important;
      display: none !important;
      background: transparent !important;
}
a#link.ad {
      width: 1px !important;
      height: 1px !important;
      border: none !important;
      display: none !important;
      background: transparent !important;
}
#link.ad {
      width: 1px !important;
      height: 1px !important;
      border: none !important;
      display: none !important;
      background: transparent !important;
}
#link .ad {
      width: 1px !important;
      height: 1px !important;
      border: none !important;
      display: none !important;
      background: transparent !important;
}
a#link .ad {
      width: 1px !important;
      height: 1px !important;
      border: none !important;
      display: none !important;
      background: transparent !important;
}
.banner_center {
      width: 1px !important;
      height: 1px !important;
      border: none !important;
      display: none !important;
      background: transparent !important;
}
#wrapper {
      background: transparent !important;
}
nav.menu_top ul li.active a {
      background: rgba(128, 0, 128, 0.2) !important;
      Color: red !important;
      font-size:14px !important;
}
nav.menu_top ul li a:hover {
      background: rgba(255, 0, 0, 0.5) !important;
      Color: black !important;
      font-size:18px !important;
}
nav.menu_top ul li a {
      background: rgba(0, 0, 0, 0.2) !important;
      Color: pink !important;
      font-size:14px !important;
}
.menu_top_link a {
      background: rgba(0, 0, 0, 0.5) !important;
      Color: red !important
}
.menu_top_link a:hover {
      background: rgba(0, 0, 0, 0.5) !important;
      Color: organge !important;
      font-size:16px !important
}
.link_face a.facebook:hover {
      background-image:url(https://gogoanimes.co/img/bg/share_link_3.png) !important;
      background-repeat:no-repeat !important;
}
.link_face a.twitter:hover {
      background-image:url(https://gogoanimes.co/img/bg/share_link_3.png) !important;
      background-repeat:no-repeat !important;
      background-position:center -24px !important;
}
.link_face a.google:hover {
      background-image:url(https://gogoanimes.co/img/bg/share_link_3.png) !important;
      background-repeat:no-repeat !important;
      background-position:right -24px !important;
}
.main_body {
      background: rgba(0, 0, 0, 0.2) !important;
}
ul.items li p.name a {
      background: rgba(0, 0, 0, 0.2) !important;
      Color: organge !important;
}
ul.items li p.name a:hover {
      background: rgba(0, 0, 0, 0.5) !important;
      Color: red !important;
      font-size: 16px !important;
}
ul.items li p.episode {
      Color: red !important;
      background: rgba(0, 0, 0, 0.2) !important;
}
.added_series_body.popular ul li p.genres {
      Color: red !important;
      background: rgba(0, 0, 0, 0.2) !important;
}
.added_series_body.popular ul li p.genres a {
      Color: yellow !important;
      background: rgba(0, 0, 0, 0.2) !important;
}
.added_series_body.popular ul li p {
      Color: lightblue !important;
      background: rgba(0, 0, 0, 0.2) !important;
}
.added_series_body.popular ul li a {
      Color: lightgreen !important;
      background: rgba(0, 0, 0, 0.2) !important;
}
.added_series_body ul li a:hover {
      Color: lightpink !important;
      background: rgba(0, 0, 0, 0.5) !important;
}
nav.menu_series ul li a {
      Color: lightpink !important;
      background: rgba(0, 0, 0, 0.2) !important;
}
nav.menu_series ul li a:hover {
      Color: red !important;
      background: rgba(0, 0, 0, 0.5) !important;
      font-size: 16px !important;
}
.added_series_body ul li a {
      Color: #ff006e !important;
      background: rgba(0, 0, 0, 0.2) !important;
}
h1, h2, h3, h4, h5, h6 {
      background: rgba(0, 0, 0, 0.4) !important;
      color: limegreen !important;
}
.anime_video_body_cate span {
      background: rgba(0, 0, 0, 0.2) !important;
}
.anime_video_body_cate a {
      background: rgba(0, 0, 0, 0.2) !important;
}
.anime_video_body_cate a:hover {
      color: red !important;
      font-size: 16px !important;
      background: rgba(0, 0, 0, 0.2) !important;
}
element {
      background: none !important;
}
nav.menu_recent ul li a {
      background: rgba(0, 0, 0, 0.2) !important;
}
nav.menu_recent ul li a:hover {
      color: lime !important;
      font-size: 16px !important;
      background: rgba(0, 0, 0, 0.2) !important;
}
.anime_name_img_anime_video {
      background-image:url(https://uploads.disquscdn.com/images/ac561eed534c3fdedbbce030a8703f144e4a70c32779bd89188bd7e442a30396.gif) !important;
      background-size:100% 100%!important;
}
.anime_name {
      background-color: transparent !important;
}
.anime_name_img_ongoing {
      background-image:url(https://steamusercontent-a.akamaihd.net/ugc/849339809542605472/1E0BF3A5A6C02FB2E745ABED417D54B5B710E3C2/) !important;
      background-size:100% 100%!important;
}
.anime_name_img_recent_release {
      background-image:url(https://steamusercontent-a.akamaihd.net/ugc/922549301957584425/5A09907B4BA2692D8A7680485458CFE1618FBA9D/) !important;
      background-size:100% 100%!important;
}
.anime_name_img_comedy {
      background-image:url(https://orig00.deviantart.net/d808/f/2016/082/6/0/azuki_by_margaretlovez-d9w6l8y.gif) !important;
      background-size:100% 100%!important;
}
.anime_name_img_added_series {
      background-image:url(https://orig00.deviantart.net/2c85/f/2016/090/f/c/cinnamon__by_margaretlovez-d9x6qj8.gif) !important;
      background-size:100% 100%!important;
}
.anime_list_body ul li a {
      background: rgba(0, 0, 0, 0.2) !important;
}
.anime_list_body ul li a:hover {
      font-size: 16px !important;
      background: rgba(0, 0, 0, 0.2) !important;
}
.anime_name_img_anime_list {
      background-image:url(https://pa1.narvii.com/6182/e60bcc942dd9cd5fd94f71311149c0dc28eef50e_hq.gif) !important;
      background-size:100% 100%!important;
}
.anime_name_img_new_series {
      background-image:url(https://steamusercontent-a.akamaihd.net/ugc/992366867835916212/6AEE30B837DEB156D5F4E254436E4499EB8F9347/) !important;
      background-size:100% 100%!important;
}
.anime_name_img_genre {
      background-image:url(https://steamusercontent-a.akamaihd.net/ugc/992366867835916212/6AEE30B837DEB156D5F4E254436E4499EB8F9347/) !important;
      background-size:100% 100%!important;
}
.anime_name_img_anime_movies {
      background-image:url(https://orig00.deviantart.net/a83b/f/2016/083/f/2/maple_by_margaretlovez-d9w9w1c.gif) !important;
      background-size:100% 100%!important;
}
.anime_name_img_anime_info {
      background-image:url(https://orig00.deviantart.net/8fc1/f/2016/092/a/3/shigure_by_margaretlovez-d9xeqb3.gif) !important;
      background-size:100% 100%!important;
}
.anime_info_body p.type span {
      background: rgba(0, 0, 0, 0.2) !important;
}
.anime_info_body p.type span:hover {
      font-size: 16px !important;
      background: rgba(0, 0, 0, 0.2) !important;
      color:red !important;
}
.type a {
      background: rgba(0, 0, 0, 0.2) !important;
      color: orange !important;
}
.type a:hover {
      background: rgba(0, 0, 0, 0.4) !important;
      color: blue !important;
      font-size: 16px !important;
}
.anime_info_body p.type {
      color: blue !important;
      background: rgba(0, 0, 0, 0.4) !important;
}
.anime_info_body p.type:hover {
      color: cyan !important;
      background: rgba(0, 0, 0, 0.4) !important;
      font-size: 16px !important;
}
