css:
@-moz-document domain('czateria.interia.pl') {
    body > div:not(#check-css):not(#m-tab-main-container-1):not(.login-form):not(#tooltip):not(.m-popup):not(.m-crop-panel-wrapper),
    .login-form > header,
    #mainHeader,
    .rodo-popup,
    #video-player,
    .m-invitation-container,
    .m-invitation > hr,
    #animation_container,
    .floatbox-box-background,
    .video-player-container,
    .go-to-free-chat-info {
        display: none !important;
    }
    #m-tab-main-container-1 {
        height: 100% !important;
    }
    body {
        background-color: #fff;
    }
    .m-messagesTextArea {
        background: none !important;
        border-radius: 0 !important;
        border-color: #ccc !important;
    }
    .m-roomsList, .m-usersList,
    .m-tab-container-ul li a,
    ul#m-tab-main-container-1-nav li a {
        border-radius: 0 !important;
        border-color: #ccc !important;
    }
    .m-tab-container-ul li a,
    ul#m-tab-main-container-1-nav li a {
        background: #ddd;
        color: #999;
        font-weight: normal !important;
        text-transform: none !important;
    }
    ul#m-tab-main-container-1-nav li a.active,
    ul#m-tab-main-container-1-nav li.activeli:before {
        background-color: #fff !important;
    }
    input[type="button"],
    input[type="file"],
    select {
        background-color: #eee !important;
        color: #333 !important;
        font-weight: normal !important;
        text-transform: none !important;
        border-radius: 0 !important;
        border-color: #ccc !important;
    }
    input[type="text"] {
        border-radius: 0 !important;
        border-color: #ccc !important;
    }
    input[type="checkbox"] + label.m-show-short-list-check::before {
        background-position: -18px 0;
    }
    .m-msg-item .img-gif {
        opacity: 0.1;
        height: 20px;
    }
}
