css:
@-moz-document domain("xenoversemods.com"), domain("fighterzmods.com"), domain("sm4shmods.com"), domain("nomansskymods.com"), domain("justcause3mods.com"), domain("justcause2mods.com"), domain("rocketleaguemods.com"), domain("finalfantasyxvmods.com"), domain("callofdutymods.com"), domain("mafiamods.com"), domain("cemumods.com"), domain("botwmods.com"), domain("watchdogsmods.com"), domain("civilizationmods.com"), domain("narutomods.com"), domain("starcitizenmods.com"), domain("madmaxmods.com"), domain("dyinglightmods.com"), domain("grandtheftauto5mods.com"), domain("thewitcher3mods.com"), domain("earthsspecialforcesmods.com"), domain("wwe2kmods.com") {
/* Main Stuff */
.vgmnavtop {
    background-color: /*[[main-color]]*/;
}

.vgmnavwrap {
    background-color: /*[[secondary-color]]*/;
}

.nav-wrapper {
    background-color: /*[[secondary-color]]*/;
}

.tabs .indicator {
    background-color: /*[[main-color]]*/;
}

.tabs .tab a:hover {
    color: /*[[secondary-color]]*/;
}

.themebgcolor {
    background-color: /*[[main-color]]*/;
}

.themebgtextcolor {
    color: /*[[main-color]]*/;
}

.owl-theme .owl-controls .owl-page.active span, .owl-theme .owl-controls.clickable .owl-page:hover span {
    background: /*[[main-color]]*/;
}

a {
    color: /*[[main-color]]*/;
}

a:hover {
    color: /*[[secondary-color]]*/;
}

.vgmmodcard a:hover h3.card-title {
    color: /*[[main-color]]*/;
}



/* Download Button */
.vgmdownload a {
    background-color: /*[[main-color]]*/;
}



/* Search */
.vgmwidget [type=radio]:checked+label:after {
    border: 2px solid /*[[main-color]]*/;
    background-color: /*[[main-color]]*/;
}

.vgmwidget [type=checkbox]:checked+label:before {
    border-right: 2px solid /*[[main-color]]*/;
    border-bottom: 2px solid /*[[main-color]]*/;
}



/* Page Selection */
.pagination li.active {
    background-color: /*[[main-color]]*/;
}



/* Bottom Page Header */
.vgmsitebackgroundbottom {
    border-bottom: 4px solid /*[[main-color]]*/;
}

}
