css:
@-moz-document url-prefix("https://www.vidlii.com/") {
body {
    background: #211e1e;
}

a {
    color: #00f;
    text-decoration: none;
    text-decoration-line: none;
    text-decoration-style: initial;
    text-decoration-color: initial;
}

element.style {
    margin-top: 30px;
}

footer {
    background: #000000;
    background: -moz-linear-gradient(top, #efefef 0, #ebece0 100%);
    background: -webkit-linear-gradient(top, #efefef 0, #ebece0 100%);
    background: linear-gradient(to bottom, #efefef 0, #000000 100%);
    padding: 10px 0;
    border-radius: 6px;
    margin: 10px 0 20px;
    border: 1px solid #ccc;
}

footer,
footer strong {
    font-size: 12px!important;
}

#ch_prv,
#s_toggle > div,
#video_progress,
.con_bx,
.friend_activity,
.ln2,
.mv_under,
.mv_under > div > a,
.mv_wr,
.partner_box,
.w_actions,
.w_btm,
.wrapper,
footer {
    overflow: hidden;
}

user agent stylesheet article,
aside,
footer,
header,
hgroup,
main,
nav,
section {
    display: block;
}

.settings_menu a {
    display: block;
    color: #fff;
    text-decoration: none;
    padding: 7px 0 7px 7px;
    border: 1px solid transparent;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    width: 193px;
}

