css:
@-moz-document domain("poj.org") {
input[type="text"],
input[type="password"],
td[align="left"] a[href="register"] {
    height: calc(1.5rem + 2px);
    padding: 0.375rem 0.75rem;
    font-size: 0.8rem;
    line-height: 1.5;
    color: #495057;
    background-color: #fff;
    background-clip: padding-box;
    border: 1px solid #ced4da;
    border-radius: 0.25rem;
    transition: border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
}
input[type="Submit"] {
    margin-left: 10px;
    display: inline-block;
    text-align: center;
    font-size: 0.85rem;
    line-height: 1.5;
    min-width: 50px;
    white-space: nowrap;
    vertical-align: middle;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    border: 1px solid transparent;
    border-radius: 0.25rem;
    transition: color 0.15s ease-in-out, background-color 0.15s ease-in-out, border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
    color: #fff;
    background-color: #007bff;
    border-color: #007bff;
}
a {
    color: #343a40 !important;
}
table[class="a"],
table.a {
    position: relative;
    display: -ms-flexbox;
    -ms-flex-direction: column;
    flex-direction: column;
    word-wrap: break-word;
    background-color: #fff;
    border-collapse: separate;
    background-clip: border-box;
    border: 1px solid rgba(0, 0, 0, 0.125);
    border-radius: 6px 6px 6px 6px !important;
    -webkit-box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2);
    box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2);
    margin-top: 30px;
    padding: 30px 30px 30px 30px;
}
p[style="font-family:Arial, Helvetica, sans-serif"],
hr {
    display: none;
}
select {
    height: calc(1.5rem + 2px);
    min-width: 50px;
    line-height: 1.5;
    color: #495057;
    background-color: #fff;
    background-clip: padding-box;
    border: 1px solid #ced4da;
    border-radius: 0.25rem;
    transition: border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
}
table[cellspacing="0"] tbody tr {
    height: 30px;
    border: 1px solid #dee2e6;
}
table[cellspacing="0"] tbody tr.in {
    background-color: /*[[MainColor]]*/ !important;
}
tr[valign="top"] {
    height: 30px;
    background-color: /*[[MainColor]]*/ !important;
}
table.a {
    background-image: none !important;
    width: 100%;
    margin-bottom: 1rem;
    background-color: transparent;
    background-color: #fff;
}
table[background="images/table_back.jpg"] {
    background-image: none !important;
    border-collapse: separate;
    width: 100%;
    background-color: transparent;
    background-color: #fff;
    position: relative;
    display: -ms-flexbox;
    -ms-flex-direction: column;
    flex-direction: column;
    word-wrap: break-word;
    background-clip: border-box;
    border: 1px solid rgba(0, 0, 0, 0.125);
    border-radius: 6px 6px 6px 6px !important;
    -webkit-box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2);
    box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2);
    margin-top: 30px;
}
table[background="images/table_back.jpg"] > tbody > tr > td{
    padding :30px 30px 30px 30px;
}
table[background="images/table_back.jpg"] p.pst,
table[background="images/table_back.jpg"] div.ptt{
    color:black;
}
table[background="images/table_back.jpg"] pre{
    color: #ccc;
    font-family: Consolas, Monaco, 'Andale Mono', 'Ubuntu Mono', monospace;
    text-align: left;
    white-space: pre;
    word-spacing: normal;
    word-break: normal;
    word-wrap: normal;
    line-height: 1.5;
    -moz-tab-size: 4;
    -o-tab-size: 4;
    tab-size: 4;
    -webkit-hyphens: none;
    -moz-hyphens: none;
    -ms-hyphens: none;
    hyphens: none;
    background: #2d2d2d;
    padding: 1em;
    margin: .5em 0;
    overflow: auto;
    font-size:80% !important;
}
table th,
table td {
    border-top: 1px solid #dee2e6;
}
table tbody th {
    vertical-align: bottom;
    border-bottom: 2px solid #dee2e6;
}
body {
    width: 80%;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
}
div[style="position: absolute; right: 10px;"] {
    display: none;
}
center {
    font-size: 0;
}
center a {
    border-style: solid;
    border-width: 1px;
    padding: 20px 8px 10px 8px;
    position: relative;
    margin-left: -1px;
    line-height: 1.25;
    color: #007bff;
    background-color: #fff;
    border: 1px solid #dee2e6;
}
center font {
    font-size: 20px;
}
center a font {
    font-size: 15px;
}
a[href="moreproblem"] font {
    font-size: 10px;
}
form[action="searchproblem"] font[size="5"] {
    font-size: 20px;
    color: #000;
}
table[style="BORDER-COLLAPSE: collapse"] {
    position: relative;
    display: -ms-flexbox;
    -ms-flex-direction: column;
    flex-direction: column;
    word-wrap: break-word;
    background-color: #fff;
    background-clip: border-box;
    border: 1px solid rgba(0, 0, 0, 0.125);
    border-radius: 0.25rem !important;
    border-radius: 6px 6px 6px 6px;
    -webkit-box-shadow: 0 8px 12px 1px rgba(0, 0, 0, 0.14), 0 3px 15px 2.5px rgba(0, 0, 0, 0.12), 0 4px 5px -2.5px rgba(0, 0, 0, 0.2);
    box-shadow: 0 8px 12px 1px rgba(0, 0, 0, 0.14), 0 3px 15px 2.5px rgba(0, 0, 0, 0.12), 0 4px 5px -2.5px rgba(0, 0, 0, 0.2);
    margin-bottom: 30px;
}
table[style="BORDER-COLLAPSE: collapse"] tbody tr td {
    background-color: #FFF !important;
    line-height: 30px;
}
table[style="BORDER-COLLAPSE: collapse"] td[align="left"] {
    width: 10%;
}
table[style="BORDER-COLLAPSE: collapse"] td[colspan="3"] td[width="95"] {
    width: 10%;
}
font[color="blue"] {
    color: black;
}
}
