/* ==UserStyle==
@name			Mailgun
@author			Cascadea User
@version		1.0 
==/UserStyle== */

@-moz-document domain("app.mailgun.com")
{
	.mg-envelope {
	background-image: none;}
	.rs-panel {
	background: #fff;
	box-shadow: #c2c2c2 0px 3px 50px 1px;}
	body {
	background-color: #fff;
	background-image: none;
	}
	.rs-nav-primary .rs-nav {
	box-shadow: none;
	border-bottom: none;}
	.rs-nav-primary {
	background-color: #fff;
	background-image: none;
	}
	.rs-btn {
	color: #333;
	background-color: #f4f4f4;
	border: 1px solid #a6a6a6;
	background-image: none;
	border-radius: 2px;
	text-shadow: none;
	}
}
