css:
@-moz-document domain("www.tangthuvien.vn"){

body {
    background: url("/*[[background]]*/") fixed !important;
    background-size: cover !important;
    max-width: 100% !important;
    margin: auto !important;
    font-family: 'Roboto', sans-serif;
  }
/*logo*/
img[src="images/ttv-logo.png"] {
    margin-left: 20px;
}
/* login topbar */
#toplinks ul.nouser li a {
	background: none !important;
    border: none !important;
}
#logindetails {
	background: none !important;
    border: none !important;
}
#toplinks a[href="faq.php"] {
	display: none;
}
/* user info topbar */
.toplinks ul.isuser li a, .toplinks ul.isuser li.welcomelink {
    color: #fbe4fa;
    font-family: 'Roboto', sans-serif;
}
.toplinks ul.isuser li.welcomelink > a {
    text-shadow: gold 0px 0px 5px, pink 0px 0px 5px, pink 0px 0px 5px;
    font-size: 120%;
}
#vtlai_topx > h2, .fb-comments {
    display: none !important;
}
#vtlai_topx  a[href="member.php?u=159938"], a[href="member.php?u=159938"] > strong {
    text-shadow: violet 0px 0px 5px, violet 0px 0px 5px, violet 0px 0px 5px;
    color: #fff;
}
.body_wrapper, .thread_controls.toolsmenu, .noinlinemod.below_postlist, .navlinks, .postbit ,  #thread_info > h4, #thread_onlineusers, #quickreply_title, #quick_reply .blockbody.formcontrols, #quick_reply .blockfoot.actionbuttons {
    box-shadow: none !important;
    min-width: 800px !important;
    max-width: 100% !important;
    background: none !important;
    border: none !important;
  }
/*thread info show num user in thread*/
#thread_onlineusers .inner_block > div > p, #thread_onlineusers .inner_block > div > p > span {
	color: #000;
}
/* new thread */
#newthreadlink_bottom, #newthreadlink_top {
    background: none !important;
    border: 1px solid;
}
.pagination.popupmenu.nohovermenu a {
    box-shadow: none !important;
    min-width: 800px !important;
    max-width: 100% !important;
    background: none !important;
    border: none !important;
    color: #000 !important;
	font-weight: bold;
}
div[style="padding: 3px; background-color: #FFFFFF; border: 1px solid #d8d8d8; font-size: 1em;"]{
    background: none !important;
    border: none !important;
}
.bbcode_container .bbcode_quote_container {
    background: none !important;
}
/* url path to thread on header */
#breadcrumb, #breadcrumb a {
	color: #000;
}
/*color quote, attachments*/
.bbcode_container div.bbcode_quote, .attachments{
    background: none !important;
    border-radius: 0px;
    border: none;
    border-left: 3px solid #c4c4c4;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19) !important;        
}
.bbcode_container div.bbcode_quote div.message {
    background: none !important;
}
#postpagestats_above{
    color: #fff !important;
}
.postdetails_noavatar .posthead span.postdate {
    background: none !important;
    margin:0px;
}
.posthead .date .time, .posthead .date, .threadlisthead.table > div > span a {
    color: #000;
}
 
.forumhead.foruminfo .forumtitle a, .forumbit_nopost .forumhead .forumtitle, .forumbit_nopost .forumhead span, .forumbit_nopost .forumhead .collapse, .forumbit_post .forumhead h2 span, .wgo_block .blockhead {
    color: #000 !important;
}
.postuseravatarlink > img {
    max-width: 100px;
    max-height: 100px;
}
.pagination.popupmenu.nohovermenu .selected a {
    box-shadow: none !important;
    min-width: 800px !important;
    max-width: 100% !important;
    background: none !important;
    border: none !important;
    color: #000099 !important;
}
/* thread info show user view...*/
#thread_info {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19) !important;        
    padding: 5px;
	background-color: #ffffff85;
}
/* recent post in home page */
.vtlaitopx .topx .content .topxtitle, .vtlaitopx .topx .content .topxlastposter, .vtlaitopx .topx .content .topxforum {
    margin: 10px;
}
.vtlaitopx .topx .content thead th , #pagetitle {
    text-align: center;
    font-weight: bold;
}
/*display none*/
.subforumdescription, #navbar_notice_1, #postpagestats_above, .forum_info .options_block, .forum_info .options_block2 {
    display: none;
}
/* thread info view background none*/
#stickies > li > div > ul, #stickies > li > div > ul, .threadbit .alt {
	background: none !important;
}
#newreplylink_bottom, .above_postlist > .newcontent_textcontrol{
    background: none !important;
    border: 1px solid;
    box-shadow: inset 0 0 10px #000000;
}
/*split sticker thread with normal thread*/
#threads {
	border-top: 2px solid;
}
.pagination span a, .formcontrols input.button {
	background: none !important;
	background-color: none !important;
	border: none !important;
}
#vtlai_topx_list, #topx_bighead, #above_threadlist_controls, #vtlai_topx .tab .vtlai_topx_tabitem, .threadlisthead.table, #stickies > li > div, .forumhead.foruminfo, .forumrow.table, #quick_reply, #forum_moderators, #threads > div[style="padding:10px;background-color:#DDDDDD"], .blockhead, .textcontrols.floatcontainer, .postbody, .userinfo, .userinfo_noavatar, .posthead, .message, #wgo .blockbody.formcontrols.floatcontainer, .threadbit .nonsticky, #forum_info, #forum_display_options, .forum_info_form .options_input_block select, #forumbits .forumhead, #forum261 .forumrow {
    background: #ffffff85 !important;
    border: none !important;
}
#topx_bighead, #forumbits .forumhead {
    color: #000;
    border: 1px solid;
}
.postbit.postbitim.postcontainer.old > center, .postbit.postbitim.postcontainer.new > center , .postbody > .postrow > center {
    display: none !important;
}

.postrow > .signature.restore {
    border-top: 1px #c4c4c4 solid;
}
#postlist_popups, #footer, #navbar, .body_wrapper > center, .postrow > .posttitle {
    display: none;
}
div.forumrow.table {
    padding-top: 10px;
    padding-bottom: 10px;
}
/*Bỏ side bar*/
  #sidebar_container {
    display: none !important;
  }
  /*Banh body*/
  body{
    width: 100% !important;
    max-width: 100% !important;
  }
  /*Banh content cho = với body*/
  #content_container #content {
   width: 100% !important;
    margin: 0 !important; 
  }
  /*Bỏ margin của body*/
  .body_wrapper{
    margin: 0 !important;
  }

}
