css:
@-moz-document domain("youtube.com") {
canvas.ytd-masthead,
caption.ytd-masthead,
center.ytd-masthead,
cite.ytd-masthead,
code.ytd-masthead,
dd.ytd-masthead,
del.ytd-masthead,
dfn.ytd-masthead,
div.ytd-masthead,
dl.ytd-masthead,
dt.ytd-masthead,
em.ytd-masthead,
embed.ytd-masthead,
fieldset.ytd-masthead,
font.ytd-masthead,
form.ytd-masthead,
h1.ytd-masthead,
h2.ytd-masthead,
h3.ytd-masthead,
h4.ytd-masthead,
h5.ytd-masthead,
h6.ytd-masthead,
hr.ytd-masthead,
i.ytd-masthead,
iframe.ytd-masthead,
img.ytd-masthead,
ins.ytd-masthead,
kbd.ytd-masthead,
label.ytd-masthead,
legend.ytd-masthead,
li.ytd-masthead,
menu.ytd-masthead,
object.ytd-masthead,
ol.ytd-masthead,
p.ytd-masthead,
pre.ytd-masthead,
q.ytd-masthead,
s.ytd-masthead,
samp.ytd-masthead,
small.ytd-masthead,
span.ytd-masthead,
strike.ytd-masthead,
strong.ytd-masthead,
sub.ytd-masthead,
sup.ytd-masthead,
table.ytd-masthead,
tbody.ytd-masthead,
td.ytd-masthead,
tfoot.ytd-masthead,
th.ytd-masthead,
thead.ytd-masthead,
tr.ytd-masthead,
tt.ytd-masthead,
u.ytd-masthead,
ul.ytd-masthead,
var.ytd-masthead {
    margin: 0;
    padding: 0;
    border: 0;
    background: gainsboro;
    box-shadow: 0px 9px 20px 0px #8a8a8a6b;
    border-radius: 5px;
}
Style Attribute {
    font-size: 12px;
    font-family: Google sans;
    background-color: #fafafa;
    text-shadow: 0 0 3px #FFF;
}
paper-button.ytd-subscribe-button-renderer {
    background-color: #ff0000;
    border-radius: 5px;
    color: hsl(0, 0%, 100%);
    padding: 10px 16px;
    margin: auto var(--ytd-subscribe-button-margin, 4px);
    white-space: nowrap;
    font-size: 1.4rem;
    font-weight: 500;
    letter-spacing: .007px;
    text-transform: uppercase;
    -webkit-flex-direction: row;
    flex-direction: row-reverse;
    text-shadow: 0 0 16px #000000D4;
    box-shadow: 0 0 3px black;
}
img.yt-img-shadow {
    display: block;
    margin-left: auto;
    margin-right: auto;
    max-height: var(--yt-img-max-height, none);
    max-width: var(--yt-img-max-width, 100%);
    border-radius: 5px;
}
a.yt-simple-endpoint.ytd-active-account-header-renderer {
    padding: 16px;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    box-shadow: 0 0 13px #00000052;
}
yt-formatted-string#account-name {
    text-shadow: 0 0 6px #90909075;
}
button#search-icon-legacy {
    box-shadow: 0 0 4px #0000003b;
}
div#buttons {
    background-color: #bbbbbb;
}
#scrim.app-drawer {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    transition-property: opacity;
    -webkit-transform: translateZ(0);
    transform: translateZ(0);
    opacity: 0;
    background: rgba(0, 0, 0, 0.72);
}
.html5-video-player {
    position: relative;
    width: 100%;
    height: 100%;
    overflow: hidden;
    outline: 0;
    font-family: "YouTube Noto",Roboto,Arial,Helvetica,sans-serif;
    color: #eee;
    text-align: left;
    direction: ltr;
    font-size: 11px;
    line-height: 1.3;
    -webkit-font-smoothing: antialiased;
    -webkit-tap-highlight-color: rgba(0,0,0,0);
    touch-action: manipulation;
    -ms-high-contrast-adjust: none;
    box-shadow: 0 0 20px #00000085;
}
}
