css:
@-moz-document domain("fanatical.com") {
.wishlist .assistant-wishlist-sync,
.ts-item .ts-item-wrapper .ts-body .ts-desc,
.cookie-container
{display:none !important;}


/*■■■ Wish List ■■■*/

.card-saving
{color:#333 !important; font-weight:bold; background:#aeff00;}

.wishlist-card .content-container .item-name
{font-weight:bold; font-size:20px;}

.wishlist-card .image-container
{width:170px;}

.wishlist-card,
.wishlist-card .content-container
{padding:7px 10px;}

.mt-5
{margin-top:12px !important;}
.pb-3
{padding-bottom:0 !important;}
.wishlist-card
{margin-bottom:10px !important;}

.navbar .top-navbar
{height:65px;}

/*■■■ Top Seller ■■■*/

.ts-item .ts-item-wrapper .ts-body .ts-title
{font-weight:bold; font-size:20px; margin-bottom:20px !important;}

.ts-item .ts-item-wrapper .ts-number
{width:8%;}

.ts-item .ts-item-wrapper .ts-body
{width:48%;}

.ts-item .ts-item-wrapper .ts-pricing
{width:20%; float:right; padding:0;}

.ts-item .ts-item-wrapper>div
{padding:10px;}

.ts-item
{margin-bottom:10px !important;}

/*■■■ Latest Deal ■■■*/

.hit-card
{margin-bottom:1.25rem;}
}
