	margin-top: 40px;
}
.minidiv .sfbg {
    background: none;
    box-shadow: none;
	height: 72px;
}
.minidiv .RNNXgb {
	border-radius: 0px;
	height: 32px;
	margin: 0px 0 0;
}
.RNNXgb {
	background: #fff;
	display: flex;
	border-radius: 25px;
	width: 632px;
	z-index: 3;
	height: 44px;
	margin: 0 auto;
    box-shadow: 0 1px 6px 0 rgba(32,33,36,0.28);
    border: none;
}

}

@-moz-document url-prefix("https://www.google.com") {
body.hp {
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.5) 10px, rgba(0, 0, 0, 0.4) 30px, rgba(0, 0, 0, 0.3) 50px, rgba(0, 0, 0, 0.2) 80px, rgba(255, 255, 255, 0.1) 250px, rgba(176, 176, 75, 0) 300px), url(https://source.unsplash.com/user/anywombat/likes/1920x1080);
    width: 100%;
    z-index: 15;
}

#fbar {
    visibility: hidden;
    color: white;
}
#als {
    visibility: hidden;
}
.knowledge-panel {
    background: white;
    border-radius: 2px;
}
#sfdiv {
    border-radius: 25px;
	box-shadow: none;
}
#gb#gb a.gb_O, #gb#gb a.gb_P, #gb#gb span.gb_P {
	color: white;
	text-decoration: none;
}
#hptl a {
	display: inline-block;
	padding-left: 15px;
	color: white;
	opacity: 0.75;
}
.gb_fc {
	background: white url('https://ssl.gstatic.com/gb/images/i1_1967ca6a.png');
    border-radius: 25px;
}
.gb_b .gb_Rc, .gb_b:focus .gb_Rc {
	background-color: rgba(0,0,0,.85);
}
.lst-c {
	overflow: hidden;
	border-radius: 25px;
}
.jhp input[type="submit"], .sbdd_a input, .gbqfba {
	background-image: none;
	-moz-border-radius: 25px;
	-moz-user-select: none;
	background-color: white;
	border: 0 solid #f2f2f2;
	border-radius: 25px;
	color: black;
	cursor: default;
	font-family: arial,sans-serif;
	font-size: 13px;
	font-weight: bold;
	margin: 11px 4px;
	min-width: 54px;
	padding: 0 16px;
	text-align: center;
}
.sbdd_b {
	border: none;
	border-radius: 25px;
	padding-top: 44px;
	margin-top: -44px;
}
.vasq .sbdd_a {
	z-index: -1;
}
}
