css:
@-moz-document domain("perpusindo.info") {
/* Sidebar */

.sidebar {
    background: #2b2b2b !important;
    color: #b3b3b3 !important;
}

.sidebar>.nav>li>.nav-item,
.sidebar>.nav>li>a {
    color: #b3b3b3 !important;
    transition: color 0.25s ease;
}

.sidebar>.nav>li>.nav-item:hover,
.sidebar>.nav>li>a:hover {
    color: #fff !important;
}


/* Main Body */

body,
body:hover,
body:focus {
    color: #fafafa !important;
}

.panel-default {
    border-color: #464646 !important;
}

.panel-default>.panel-heading {
    color: #cacaca !important;
    background-color: #4c4c4c !important;
    border-color: #4c4c4c !important;
}

.panel-footer {
    background-color: #3e3e3e !important;
    border-top: 1px solid #3e3e3e !important;
}

a.blackLink,
a.blackLink:hover {
    color: #cacaca !important;
}

.perpusindo-tbl {
    background-color: #3f3f3f !important;
}

tr {
    transition: background-color 0.15s ease;
}

tr:hover {
    background-color: #4c4c4c !important
}

.text-muted {
    color: #c5c7c7;
}

.switch span {
    background-color: #545454;
    border: 1px solid #636363;
}

.switch span:after {
    background-color: #949494;
    border: 1px solid #797979;
}

.table>tbody>tr>td,
.table>tbody>tr>th,
.table>tfoot>tr>td,
.table>tfoot>tr>th,
.table>thead>tr>td,
.table>thead>tr>th {
    border-top: 1px solid #636363 !important;
}

.btn-default {
    color: #b1b1b1 !important;
    background-color: #616161 !important;
}

.btn-default:hover {
    color: #b1b1b1 !important;
    background-color: #616161 !important;
}

.animeBlock>.title,
.animeBlock>.title:focus {
    transition: background-color 0.25s ease;
    background: #4a4a4a;
}

.animeBlock>.title:hover {
    background: #5b5b5b;
}

.pagination>li>a,
.pagination>li>span,
.pagination>li>a:focus,
.pagination>li>span:focus {
    color: #d2d2d2;
    transition: all 0.25s ease;
    background-color: #848484;
    border: 1px solid #868686;
}

.pagination>li>a:hover,
.pagination>li>span:hover {
    color: #ececec;
    background-color: #777777;
    border: 1px solid #5d5d5d;
}

div.col-md-6>div.panel {
    position: relative;
    background-image: linear-gradient(to right, rgb(49, 49, 49) 0%, rgb(115, 115, 115) 29%, rgb(128, 128, 128) 51%, rgba(132, 132, 132, 0.23) 99%, #464646 100%);
}

.anime-title {
    background: rgb(64, 64, 64);
}

.bg-gray-lighter {
    background-color: #4c4c4c;
    color: #c5c5c5!important;
}

.form-control,
.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control,
form-control.form-max {
    background-color: #7b7b7b;
    border: 1px solid #4e4e4e;
    color: #ececec;
}

a.list-group-item,
button.list-group-item {
    color: #e2dede;
    background-color: #585858;
    border-color: #4e4e4e !important;
}

a.list-group-item:hover {
    background-color: #636363;
    color: #c1c1c1;
}

.list-group-item.active,
.list-group-item.active:focus,
.list-group-item.active:hover {
    color: #c3c3c3;
    background-color: #696969;
    border-color: #696969;
}

.list-group.notify-notif {
    background-color: #484848 !important;
}

.list-group.issue-container {
    background-color: #484848 !important;
}

.btn.btn-default.btn-xs {
    background-color: #484848 !important;
    border-color: #616161 !important;
}

.btn-info {
    color: #dcdcdc;
    background-color: #2e509c;
    border-color: #28508c;
}

.dataContainer:hover {
    background-color: #585858;
}

.btn.btn-default {
    border-color: #7b7b7b;
}

.content {
    background-color: #3c3c3c !important;
}

.right {
    background-color: #3c3c3c !important;
}

.right label {
    color: #b1b1b1;
}

.right input {
    background-color: #3c3c3c !important;
    border-bottom: 1px solid #212121;
}

.content-wrapper {
    background-color: #404040 !important;
}

.panel {
    background-color: #585858 !important;
}

.proses {
    background: #7b7b7b url(https://www.perpusindo.info//assets/PINew/img/loading.gif) no-repeat;
    border: 2px solid #6f6f6f;
}

.select2-container--bootstrap .select2-search--dropdown .select2-search__field {
    background-color: #7a7a7a;
    border-color: #8e8e8e;
    color: #cacaca;
}

a,
a:hover {
    color: #c3c3c3;
}

.input-group {
    color: #f1f1f1;
}

.btnDownload.whiteLink,
.btnAddComment,
.pull-right.whiteLink,
.btnDownload.whiteLink:focus,
.btnAddComment:focus,
.pull-right.whiteLink:focus {
    background-color: #7a7a7a;
}

.btnDownload.whiteLink:hover,
.btnAddComment:hover,
.pull-right.whiteLink:hover {
    background-color: #8c8b8b;
}

a.whiteLink,
a.whiteLink:hover {
    color: #f1f1f1;
}

.wrapper {
    background: #404040 !important;
}

.select2-container--bootstrap .select2-selection {
    background-color: #7b7b7b;
    color: #9a9a9a;
    border-color: #7c7f82;
}

.select2-container--bootstrap .select2-selection--single .select2-selection__placeholder {
    color: #b4b7c6;
    border-color: #7c7f82;
}

.select2-container--bootstrap .select2-dropdown {
    border-color: #7c7f82;
    background-color: #565656;
}

a.list-group-item .list-group-item-heading,
button.list-group-item .list-group-item-heading {
    color: #d2d0d0;
}

body .select2-container--bootstrap .select2-selection--multiple,
body .select2-container--bootstrap .select2-selection--single {
    border: 1px solid #676767;
}

.form-control.select2-hidden-accessible {
    background-color: #4c4c4c;
    color: #c5c5c5!important;
}

button.login.btnExec.btnLogin {
    background: #565656 !important;
}

button.login.btnExec.btnLogin:hover {
    background: #6f6f6f !important;
}

.input-group-addon {
    color: #e2e2e2;
    background-color: #5f5f5f;
    border: 1px solid #676767;
}

button.off.login {
    color: #ababab;
}

button.off.login:hover {
    color: #f1efef;
}

button.btnForgotForm {
    background: #3c3c3c !important;
}

button.btnForgotForm:hover {
    background: #4a4949 !important;
}

button.login.off {
    background: #3c3c3c !important;
}

button.login.off:hover {
    background: #4a4949 !important;
}

.popover.fade,
.popover-title,
.popover.fade.left.in:focus,
.popover-title:focus {
    background: #545454 !important;
}

.arrow,
.arrow:focus,
.arrow:after {
    border-color: #545454;
}

input {
    padding-left: 12px;
}

button {
    color: #ccc;
}

.iziToast {
    background: #3f3f3f !important;
    border-color: #3f3f3f !important;
    color: #d2d2d2;
}

.iziToast>.iziToast-body .iziToast-title {
    color: #c5c5c5;
}

.iziToast>.iziToast-body .iziToast-buttons>a,
.iziToast>.iziToast-body .iziToast-buttons>button,
.iziToast>.iziToast-body .iziToast-buttons>input {
    color: #c5c5c5;
}

.iziToast>.iziToast-body .iziToast-icon.ico-question {
    filter: invert(80%)
}

.rc-anchor-dark.rc-anchor-normal {
    border: 1px solid #525252;
}

.rc-anchor-dark {
    background: #3c3c3c;
    color: #d2d2d2;
}
}
