css:
@-moz-document domain(nitrotype.com)
{

  .page-garage .racer-info
  {
    width: 100%;
    height: 431px;
    margin-right: 10px;
    margin-bottom: 30px;
    border: 1px solid #4f1c89;
    padding: 10px;
    position: relative;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    -webkit-box-pack: justify;
    -ms-flex-pack: justify;
    justify-content: space-between;
  }

  .app-header .navigation li a
  {
    position: relative;
    display: inline-block;
    padding: 12px 8px;
    font-family: Orbitron,sans-serif;
    font-size: 14px;
    font-weight: 700;
    color: #000000;
    text-decoration: none;
  }

  .content-box
  {
    position: relative;
    padding: 15px;
    margin-bottom: 20px;
    background: rgb(0, 0, 0);
    border: 1px solid #121212;
    border-top-color: #c5c5c5;
  }

  .race-results .racers .racer .player .stats
  {
    float: right;
    width: 65%;
    font-size: 13px;
    text-align: right;
    padding-right: 12px;
    overflow: hidden;
    color: #fff;
  }

  .page-achievements .achievements .groups
  {
    padding: 30px 10px;
    width: 183px;
    background: #1c1c1c;
    position: relative;
  }

  .page-achievements .achievements .groups .outline-trophy
  {
    margin: 20px auto 0;
    background: url(https://i.imgur.com/Jzu3wDH.png) no-repeat 0 0;
    width: 145px;
    height: 181px;
  }

  .page-achievements .achievements .list .progress-bars>div p
  {
    position: relative;
    padding: 8px 10px 6px;
    font-size: 12px;
    font-family: Orbitron,sans-serif;
    color: #fff;
    background: url(https://cdn.discordapp.com/attachments/497986224716513291/509421298041094145/unknown.png) no-repeat 0 0;
  }

  .page-achievements .achievements .groups a.active, .page-achievements .achievements .groups a:active, .page-achievements .achievements .groups a:hover
  {
    background: #4f1c89;
  }

  .app-header .navigation li a:active:after, .app-header .navigation li a:hover:after, .app-header .navigation li.active a:after
  {
    content: "";
    position: absolute;
    width: 45px;
    height: 12px;
    background: url(https://i.imgur.com/Jzu3wDH.png) no-repeat 0 0;
    left: 50%;
    margin-left: -23px;
    bottom: -12px;
  }

  .app-header .navigation li a:active:after, .app-header .navigation li a:hover:after, .app-header .navigation li.active a:after
  {
    content: "";
    position: absolute;
    width: 45px;
    height: 12px;
    background: url(https://i.imgur.com/Jzu3wDH.png) no-repeat 0 0;
    left: 50%;
    margin-left: -23px;
    bottom: -12px;
  }

  .page-stats .stats-left table.stats-speed td
  {
    color: #fff;
    font-size: 22px;
    font-weight: 100;
    font-family: Droid Serif;
  }

  .page-friends .friends-table .status .offline:before
  {
    box-shadow: 0 0 3px 1px rgba(255, 255, 255, 0.7);
    border: 1px solid #000000;
    filter: none;
    background: linear-gradient(to bottom,#a79d9d 0,#3a3737 100%);
  }

  .experience-bar
  {
    padding: 5px;
    font-size: 14px;
    text-align: center;
    border: 1px solid #ffffff;
    background: url(https://cdn.discordapp.com/attachments/497986224716513291/509421298041094145/unknown.png) no-repeat 0 0;
    background-size: 0 auto;
    color: #fff;
    height: 25px;
  }

  .page-stats .stats-right .typing-stats .graph-options a.active, .page-stats .stats-right .typing-stats .graph-options a:active, .page-stats .stats-right .typing-stats .graph-options a:hover
  {
    color: #3baafb;
  }

  .page-friends .invite-friends-callout
  {
    font-size: 18px;
    color: #909090;
    padding: 15px 0;
    border-top: 1px solid #161616;
    border-bottom: 1px solid #161616;
    margin-bottom: 15px;
    text-align: center;
  }

  .page-friends .friends-table .status .offline
  {
    color: #a59e9e;
  }

  .page-stats .stats-left table.stats-medals td
  {
    color: #fff;
  }

  .page-news .news-post .comments .comment .comment-content .comment-text .reply
  {
    color: #2f93db;
  }

  .page-friends .friends-table .racer .name
  {
    color: #fff;
    margin-bottom: 4px;
  }

  .page-news .news-post .comments .comment .comment-content .racer .name a
  {
    color: #fff;
  }

  .page-news .news-post .comments .comment
  {
    position: relative;
    display: table;
    padding: 15px 0;
    border-top: 1px solid #000000;
    border-bottom: 1px solid #000000;
    background-color: #000000;
  }

  .page-stats .stats-right .racer-stats table .member-since
  {
    color: #fff;
    font-family: Droid Serif;
    font-size: 25;
  }

  .page-stats .stats-left table.stats-races td, .page-stats .stats-left table.stats-races th
  {
    color: #fff;
  }

  .race-results .racers .racer .player .info
  {
    font-size: 15px;
    color: #ffffff;
    margin-bottom: 7px;
  }

  .wrap
  {
    width: 100%;
    min-height: 100vh;
    background: fixed url(http://images.nitrotype.com/default/images/wrap_bg.png) repeat-y center 0;
    display: table;
  }

  .page-dealership .current-car .car-info
  {
    margin-left: 0px;
    height: 75px;
    background: #3d3c40;
    border: 5px solid #000;
    margin-bottom: 25px;
    padding: 12px 10px 0;
  }

  .app-header .navigation li.active a
  {
    background: linear-gradient(to bottom,#7A2ED1 0,#9744CE 100%);
    color: #334cd2;
    border-radius: 5px;
    box-shadow: -3px 2px 0 rgba(0,0,0,.5);
    z-index: 10;
  }

  .app-header .navigation li a:active, .app-header .navigation li a:hover, .app-header .navigation li.active a
  {
    background: linear-gradient(to bottom,#7A2ED1 0,#9744CE 100%);
    color: #000;
    border-radius: 5px;
    box-shadow: -3px 2px 0 rgba(0,0,0,.5);
    z-index: 10;
  }

  .app-header .navigation li .navigation-jewel
  {
    background: linear-gradient(to bottom,#7A2ED1 0,#4f1c89 100%);
  }

  .page-garage .racer-info .stats .experience span, .page-garage .racer-info .stats .level span
  {
    color: #ffffff;
  }

  .app-header .navigation li a:active:after, .app-header .navigation li a:hover:after, .app-header .navigation li.active a:after
  {
    content: "";
    position: absolute;
    width: 45px;
    height: 12px;
    background: url(https://i.imgur.com/Jzu3wDH.png) no-repeat 0 0;
    left: 50%;
    margin-left: -23px;
    bottom: -12px;
  }

  .overall-stats td, .team-stats td
  {
    color: #fff;
  }

  .page-achievements .achievements .list .header-bar
  {
    font-family: Droid serif;
  }

  .overall-stats .stat-item, .team-stats .stat-item
  {
    color: #fff;
    font-size: 17px;
  }

  .overall-stats td, .overall-stats th, .team-stats td, .team-stats th
  {
    font-size: 17px;
    text-align: left;
    color: #ffffff;
    padding: 15px 10px;
    border-bottom: 1px solid #ffffff;
  }

  .page-dealership .current-car .car-info .cash
  {
    color: #c8c8c8;
  }

  .page-friends .my-friends .top-section>p
  {
    font-size: 16px;
    color: #e0e0e0;
    padding-top: 15px;
    font-family: Courier;
    text-align: center;
    clear: left;
  }

  .button.button-red
  {
    border: 1px solid #4f1c89;
    background: #7A2ED1;
    filter: none;
    box-shadow: 0 0 15px 1px rgba(0,0,0,.5) inset, 0 2px 0 2px #0c1e0a, 0 3px 0 3px #222323;
    color: #000;
  }

  .button
  {
    position: relative;
    display: inline-block;
    padding: 7px 30px 5px;
    background: #000;
    filter: none;
    background: linear-gradient(to bottom,#000 0,#000 100%);
    font-size: 13px;
    font-family: Orbitron,sans-serif;
    color: #c8c8c8;
    cursor: pointer;
    text-align: center;
    text-decoration: none;
    border-radius: 5px;
    border: 1px solid #4a4a4a;
    box-shadow: 0 1px 8px 0 rgba(0,0,0,.5) inset, 0 2px 0 2px #505050, 0 3px 0 3px #222323;
    -webkit-transition: color .5s ease;
    -moz-transition: color .5s ease;
    -o-transition: color .5s ease;
    transition: color .5s ease;
    line-height: 1;
  }

  .page-achievements .achievements .list .achievement .reward .title
  {
    font-size: 14px;
    color: #f1c42d;
    margin-top: 38px;
    font-family: Droid Serif;
  }

  .section-header
  {
    height: 33px;
    text-align: left;
    font-family: Droid Serif;
    font-size: 20px;
    font-weight: bold;
    border-top-left-radius: 5px;
    color: #fff;
    background: url(https://i.imgur.com/H3otAu5.png) no-repeat right top;
    top: 10px;
  }

  .page-dealership .current-car:after
  {
    content: "";
    position: absolute;
    left: 5px;
    top: -5px;
    background: url(https://i.imgur.com/Jzu3wDH.png) no-repeat 0 0;
    width: 215px;
    height: 110px;
  }

  .season-title
  {
    background: -webkit-linear-gradient(#fff,#fff 45%,#aaa 55%,#aaa);
    background: -o-linear-gradient(transparent,transparent);
    -webkit-background-clip: text;
    color: #fff;
    -webkit-text-fill-color: transparent;
    font: 900 40px lobster,sans-serif;
    line-height: 1.2;
    text-transform: uppercase;
  }

  .season-date
  {
    color: #fff;
    font-size: 22px;
    margin: 20px 0 30px;
    font-family: Orbitron;
  }

  .scoreboard-table tbody .title
  {
    color: #ff8f00;
    font-size: 12px;
    line-height: 1.5;
  }

  .page-achievements .achievements .groups a
  {
    display: block;
    background: #000;
    border: 1px solid #474747;
    border-radius: 5px;
    font-size: 13px;
    color: #c8c8c8;
    padding: 7px 10px 5px;
    text-decoration: none;
    margin-bottom: 5px;
    line-height: 1;
    position: relative;
    font-family: Droid Serif;
  }
}
