css:
@-moz-document domain("toppreise.ch") {

  * {
    font-family: Roboto, Helvetica, sans-serif !important;
    color: #ccc !important;
    background-color: #222 !important;
    border-image: none !important;
    box-shadow: none !important;
    text-shadow: none !important;
    border-color: #444 !important;
  }

  #contentWrapper,
  #contentWrapper::before,
  #contentWrapper::after,
  #middleCol,
  #middleCol::before,
  #middleCol::after {
    border-image: none !important;
    background-image: none !important;
    background-color: #222 !important;
  }

  td {
    background-image: none !important;
  }

  #raeppli {
    opacity: 0 !important;
  }

  #headerDivMiddle {
    background-image: none !important;
  }

  #NaviList,
  #naviWrapper {
    background-color: #222 !important;
    background-image: none !important;
    border: none !important;
    filter: saturate(0) !important;
  }

  #NaviList > li > a {
    background-color: #222 !important;
    background-image: none !important;
    border: 1px solid #444 !important;
    filter: saturate(0) !important;
  }

  .tab {
    background-image: none !important;
    background-color: #222 !important;
    border: 1px solid #444 !important;
  }

  .badge {
    display: none !important;
  }

  .tabBorderFirst,
  .selectedTabBorderBetweenLeft,
  .selectedTabBorderFirst,
  .selectedTabBorderLast,
  .selectedTabBorderBetweenRight,
  .tabBorderBetween,
  .tabBorderLast {
    display: none !important;
  }

  .selectedTab {
    background-image: none !important;
    background-color: #444 !important;
    border: 1px #777 solid !important;
  }

  .selectedTab > a,
  .selectedTab > div > a {
    color: #eee !important;
    background-color: #444 !important;
  }

  .languageBox,
  #language_currencyBox {
    filter: saturate(0) !important;
  }

  .currencyBox {
    height: 23px !important;
  }

  .currencyBoxCHF,
  .currencyBoxEUR {
    color: #bbb !important;
    background-color: #222 !important;
    background-image: none !important;
  }

  .currencyBoxCHF_sel,
  .currencyBoxEUR_sel {
    color: #ddd !important;
    background-color: #444 !important;
    background-image: none !important;
    border: 1px #777 solid !important;
  }

  .currencyBoxCHF_sel::before,
  .currencyBoxCHF::before {
    font-size: 1.2rem !important;
    content: "CHF" !important;
  }

  .currencyBoxEUR_sel::before,
  .currencyBoxEUR::before {
    font-size: 1.2rem !important;
    content: "EUR" !important;
  }

  .searchForm > input {
    border-color: #777 !important;
    border-width: 1px !important;
  }

  html body div#main_wrapper div#middleCol table tbody tr td div form.searchForm button img {
    filter: saturate(0) brightness(0.5) !important;
  }

  ul.ui-autocomplete.ui-menu.ui-widget.ui-widget-content.ui-corner-all {
    border: none !important;
  }

  ul.ui-autocomplete.ui-menu.ui-widget.ui-widget-content.ui-corner-all > li {
    border: none !important;
  }

  .ui-state-hover,
  .ui-state-focus {
    background-color: #444 !important;
  }

  ul.ui-autocomplete.ui-menu.ui-widget.ui-widget-content.ui-corner-all > li > a {
    border: none !important;
  }

  #pageFooter {
    background-image: none !important;
  }

  #leftColPos {
    position: relative !important;
  }

  #headerDivTop {
    display: none !important;
  }

  tr.altLinesOdd > td > div > img:nth-child(1),
  tr.altLinesEven > td > div > img:nth-child(1),
  tr.ngfProdTabRow > td > div > img:nth-child(1) {
    filter: hue-rotate(0.45turn) blur(0.15px) invert(1) saturate(0.75) contrast(0.7) !important;
  }

  .availability.prodAvail {
    background: none !important;
    filter: invert(1) hue-rotate(0.45turn) saturate(0.75) contrast(0.8) brightness(0.9) !important;
  }

  .ngfListEntry > div {
    background-image: none !important;
    border: 1px #333 solid !important;
    filter: saturate(0) !important;
  }

  .bluring {
    display: none !important;
  }

  .ngfSelWrap,
  .ui-slider {
    filter: saturate(0) !important;
  }

  .BOX_Content,
  .ngfConSelWrap,
  .ngf_link_formOuter,
  .ngfContentWrapper {
    background-color: #222 !important;
    background-image: none !important;
  }

  .ngfConActiveRestrictionsAbsolute {
    border: 1px #777 solid !important;
  }

  .ngfPaginationPanTop > div > a > span,
  .ngfPaginationPanBottom > div > a > span {
    background: none !important;
  }

  .resetButton {
    filter: saturate(0) invert(0.9) !important;
    color: #222 !important;
  }

  #leftColPos > div:nth-child(4) {
    filter: saturate(0) brightness(0.75) !important;
  }

  #middleCol > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(2) > div:nth-child(1) > div:nth-child(1) > a:nth-child(1) > img:nth-child(1) {
    filter: saturate(0) brightness(0.75) !important;
  }
}
