@-moz-document url("https://www.diffchecker.com/diff"), domain("diffchecker.com") {
/*original here:
http://userstyles.org/styles/132572*/
/* General */
html,
header.header,
.header.jsx-893473641,
.header.jsx-2086957248{
    background-color: #151414;
    border-style: none;
}

.header>.content .logo-and-nav .nav li a {
     color: #27ae60;
}


div.main {
    background-color: #151414;
}

a {
    color: #27ae60;
}

.diff-checker-container {
    background-color: #151414;
}

div.content {
    background-color: #151414;
}

div.CodeMirror-linenumber.CodeMirror-gutter-elt {
    background-color: #21211f;
}

.CodeMirror {
    background: #21211f;
    border: 1px solid #333;
}

.CodeMirror-gutter, .CodeMirror-gutters, .CodeMirror-linenumber, .CodeMirror-scroll, .CodeMirror-sizer {
    background: #21211f;
}

div.CodeMirror-scroll {
    background-color: #21211f;
}

td.diff-line-number {
    background-color: #151414;
}

div.diff-checker {
    background-color: #151414;
}

td.diff-line {
    background-color: #21211f;
}

span.diff-chunk.diff-chunk-equal {
    color: #acacac;
}

div.sidebar-container {
    background-color: #151414;
    color: #27ae60;
}
.sidebar.jsx-2474910142{
    background-color: #151414;
}
div.main footer {
    background-color: #151414;
    color: #ddd;
}

.diff-location-bar {
    background: #21211f;
    opacity: 1;
    border: 0px solid #1a981f;
}

/* LeftGreen */
.diff-checker .diff-table-container .diff-table .diff-line-inserted.start, .diff-checker .diff-table-container .diff-table .start.diff-line-modified.diff-line-with-inserts {
    background-color: #2d4f33;
}

span.diff-chunk.diff-chunk-inserted {
    background-color: #2d4f33;
    color: #fff;
}

.diff-checker .diff-table-container .diff-table .diff-line-modified .diff-chunk-inserted {
    background-color: #172;
    color: #fff;
}

.diff-checker .diff-table-container .diff-table .diff-line-modified.diff-line-with-inserts {
    background-color: #2d4f33;
}

.diff-checker .diff-table-container .diff-table .diff-line-inserted.end, .diff-checker .diff-table-container .diff-table .end.diff-line-modified.diff-line-with-inserts {
    background-color: #2d4f33;
    border-color: #319443;
}

/* RightRed */
.diff-checker .diff-table-container .diff-table .diff-line-removed.start, .diff-checker .diff-table-container .diff-table .start.diff-line-modified.diff-line-with-removes {
    background-color: #711;
}

.diff-checker .diff-table-container .diff-table .diff-line-removed.end, .diff-checker .diff-table-container .diff-table .end.diff-line-modified.diff-line-with-removes {
    background-color: #711;
}

.diff-checker .diff-table-container .diff-table .diff-line-modified.diff-line-with-removes {
    background-color: #711;
}

.diff-checker .diff-table-container .diff-table .diff-line-modified .diff-chunk-removed {
    background: #e44;
}

/*my additions(ish)*/
