}
.button.grey{
    background-color: #21211f;
    color: #bbb;
    border-color: #555;
}
/*input box*/
.diff-input-header.jsx-4117400881{
    background-color: #2e3235;
}
.diff-input.jsx-1035723527:last-child,
.diff-line.jsx-2299094447{
    border-color: #21211f;
}
.diff-checker.jsx-3288212584 form[name='diff-form'].jsx-3288212584 .diff-inputs.jsx-3288212584{
    background-color: #151414;
}
/*desktop banner*/
.banner.jsx-1025311122{
    background-color: #151414;
