css:
@-moz-document domain(prnt.sc) {

.no-click {
	pointer-events: auto;
}

#head, body, #content, #foot, #foot div, .box, .center,
 .border, .aC button, .aC, td h2, .payment-check-box {
background-color: #272424;
background-repeat: repeat;
}

a, p, h1, h2, h3, h4, h5, small, b, tr, .aC, .box, .error, .aC, .cL {
    color: #236784 !important;
}

.footer-logo { 
    filter:invert(1)
}

.footer-copy {
    color: #fff;
}

.footer {
    background-color:#141313;
}

.header {
    background:url(https://i.imgur.com/r4MDy9w.png);
    border-bottom: 1px solid white;
}

.uploader__link_textbox {
   background-color: #000;
}

.loaded-info__main {
     filter:invert(1) 
}

.loaded-info__since{
     filter:invert(1) 
}

.twitter-header__title {
    filter:invert(1)
}

.twitter-header__title_small{
    display:none
}

.twitter-item__text{
     filter:invert(1)
}

.twitter__more {
     filter:invert(1) 
}

.uploader__title{
    color:white;
    opacity:0.6;
}

.uploader__browse_button { 
    filter: grayscale(100%);
}

.header-logo {
     filter: grayscale(100%);
}

}
