css:
/* i really want this to be global */
/**

Base from: https://userstyles.org/styles/144050/tower-beautify
**/


body {
    background: url('http://img.netbian.com/file/2018/1029/8ce30d870a07832afb60ee03f04037c5.jpg');
    background: -webkit-linear-gradient(left, rgba(131, 140, 145, .1) 0%, rgba(131, 140, 145, 1) 100%);
    background-color: rgba(131, 140, 145, .4);
    font-family: "Hack Nerd Font";
    color: white;
}

/* 背景半透明 */
.simple-stack-fluid>.page.page-root.simple-pjax,
#todolists {
    background-color: rgba(131, 140, 145, 1) !important;
    /*  background-color: rgba(55, 55, 55, 0.2) !important;*/
    position: relative;
    border-radius: 0;
}

/* 磨砂效果 */
.simple-stack-fluid>.page.page-root.simple-pjax:before,
#todolists:before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    filter: blur(20px);
    z-index: -1;
}

.simple-stack.simple-stack-fluid .page {
    border-right: 0;
    border-left: 0;
    border-top: 0;
}

.simple-checkbox .checkbox-container {
    border-color: #dfe3e6 !important;
    border-width: 2px !important;
    border-radius: 40%;
}

/* Checkbox */
.simple-checkbox.circle .checkbox-container {
    border-radius: 1px;
}

.simple-checkbox.checked .checkbox-container {
    color: #A6A6A6;
    background-color: #2aa51a;
}

/* todolist */
.todolist .todo .label {
    display: inline-block;
    position: relative;
    top: -2px;
    margin: 0 0 0 5px;
    padding: 4px 7px;
    line-height: 1.3;
    background-color: #e9f2f2;
    font-size: 12px;
    font-style: normal;
    vertical-align: middle;
    white-space: nowrap;
    word-break: keep-all;
    word-wrap: normal;
    border-radius: 10px;
}

.todolist .todo.completed .todo-content a {
    color: #000;
}

/* hide update to pro */
#nav-upgrade a[data-tower-pro] {
    color: #f5941d;
    font-size: 12px;
    font-weight: 500;
    display: none;
}



/* 顶部菜单栏 */
div.wrapper>div.header {
    margin-top: 0;
    padding-top: 5px;
    padding-bottom: 5px;
}

.header {
    width: 100%;
    padding: 0px;
    background: rgba(0, 0, 0, .15);
}

.header .notification-info {
    position: relative;
    margin: -2px 10px 0;
}

.header .account-info {
    margin-right: 10px;
}

.header .nav {
    margin-left: 0px;
}

.header .nav li {
    margin: 0 8px;
}

.header .nav li.active a {
    color: #17394d;
    font-weight: 500;
}

.header .nav a {
    color: #f6f6f6;
    font-size: 15px;
    font-weight: 500;
}

.header .header-team-name:before {
    content: "\F015";
}

.header .header-team-name {
    max-width: 10em;
    transition: .1s ease;
    background: hsla(0, 0%, 100%, .3);
    border-radius: 5px;
    color: #fff;
    display: inline-block;
    float: left;
    font-weight: 700;
    line-height: 32px;
    margin: 0 4px;
    min-width: 32px;
    padding: 0 5px;
    text-decoration: none;
    font-size: 16px;
    font-family: hack nerd font;
}

.active.search-wrap {
    right: 30px;
    padding-left: 15px;
}

.header .notification-info .label,
.header .search-wrap .link-search {
    position: absolute;
    top: -6px;
    left: -70px;
    background: hsla(0, 0%, 100%, .3);
    border-radius: 3px;
    color: #fff;
    display: block;
    float: left;
    font-weight: 700;
    margin-right: 0px;
    width: 40px;
    height: 36px;
    padding: 0;
    text-decoration: none;
}

.header .notification-info .label {
    margin-right: 0px;
    width: 40px;
    padding: 0;
    left: -38px;
    top: -20px;
}

.header .notification-info .label .twr,
.header .search-wrap .link-search .twr {
    padding: 10px 12px;
    font-size: 16px;
}

.header .search-wrap #txt-search {
    padding: 6px 0;
}

.header .notification-info .notifications-box {
    left: -390px;
    width: 440px;
}

.header .header-container {
    width: 100%;
    position: relative;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
}

.header .command-bar {
    top: 3px;
}

#page-project.project-kanban .project-header .project-hd-info {
    width: auto;
    padding: 0;
    border: none;
    margin-top: -28px;
    margin-left: 460px;
}


#page-project.project-kanban .project-header .project-name {
    /*display: none;*/
    margin-top: -7px;
    height: 32px;
    vertical-align: text-top;
    max-width: 100%;
    transition: .1s ease;
    background: hsla(0, 0%, 100%, .3);
    border-radius: 3px;
    color: #fff;
    float: left;
    font-weight: 700;
    line-height: 32px;
    min-width: 53px;
    padding: 0 18px 0 5px;
    text-decoration: none;
    font-size: 16px;
    font-family: hack nerd font;
    position: relative;
}

#page-project.project-kanban .project-tabs {
    position: relative;
    border-bottom: 0;
    margin: 0 auto;
    margin-top: -29px;
    padding: 0px;
    font-size: 15px;
    width: 250px;
}

#page-project.project-kanban .project-tabs .project-tab {
    margin: 0 5px;
    padding: 0;
    color: #f5f6f7;
    line-height: 20px;
    text-align: center;
}

#page-project.project-kanban .project-tabs .project-tab.active {
    color: #17394d;
    margin: 0 5px;
    padding: 0;
    font-weight: 500;
    pointer-events: none;
}

#page-project.project-kanban .project-tabs .project-tab:hover {
    font-weight: 500;
    color: #17394d;
}

#page-project .project-links a {
    margin-left: 1px;
    color: rgba(51, 51, 51, 0.9);
}

#page-project .project-links a:hover .main,
#page-project .project-links a:hover {
    color: #17394d;
}


#page-project .project-links .main {
    margin: 0;
    margin-right: -7px;
    font-size: 16px;
    color: rgba(51, 51, 51, 0.9);
}

#page-project .project-links a.link-admin-members .main {
    display: none;
}


#page-project.project-kanban .todolist-toolbar {
    position: relative;
    display: inline;
    float: right;
    /* top: -38px; */
    /* right: 430px; */
    font-size: 15px;
    line-height: 15px;
    width: 100px;
    margin: 0 auto;
    right: 420px;
}

#page-project.project-kanban .todolist-toolbar>a {
    display: inline-block;
    padding: 11px 1px 9px;
    width: 18px;
    color: rgba(51, 51, 51, 0.9);
    text-align: center;
    border: none;
}

#page-project.project-kanban .todolist-toolbar>a:hover,
#page-project.project-kanban .todolist-toolbar>a.active {
    color: #17394d;
}

#page-project.project-kanban .project-header {
    padding: 0px;
    margin: 0 auto;
}

#page-project.project-kanban {
    margin: 0 auto;
    padding: 0;
    min-height: 540px;
}

#page-project.project-kanban .section-todos {
    margin: 10px 0 0 0;
    padding: 0;
    min-height: 540px;
}


#page-project.project-kanban .project-header .project-desc {
    width: auto;
    height: 36px;
    margin: 8px 0 0 -17px;
    vertical-align: middle;
    line-height: 0px;
    font-size: 14px;
    visibility: hidden;
}



#page-project.project-kanban .project-links {
    line-height: 20px;
    position: relative;
    float: right;
    margin-top: -28px;
    right: 270px;
    height: 24px;
    width: auto;
}

#page-project.project-kanban .project-links .main,
#page-project.project-kanban .project-links a {
    font-size: 15px;
}

#page-project.project-kanban .project-links .main i {
    padding: 0;
    margin-right: -6px;
    display: none;
}



#page-project.project-kanban .project-links .main:before {
    display: none;
}

#page-project.project-kanban .project-links .link-stats span:before {
    content: "\f200";
}

#page-project.project-kanban .project-links .link-admin-settings span:before {
    content: "\f013";
}

#page-project.project-kanban .project-links a.link-admin-members span:before {
    content: "\f0c0";
}

#page-todo .topic .todo .todo-content {
    color: seagreen;
}

/* 项目选项 */
.project-tabs {
    text-align: center;
}

a.project-tab {
    border-bottom: 1px solid transparent;
    border-width: 3px;
    opacity: 1;
}

a.project-tab.active {
    border-bottom: 0;
}

a.project-tab:hover {
    border-bottom: 0;
}

/* 看板视图 */
.kanban-todos {
    position: relative;
    padding: 10px 0px;
    margin-left: -10px;
    overflow-x: auto;
    overflow-y: hidden;
    white-space: nowrap;
    font-size: 0;
    cursor: ew-resize;
    cursor: -webkit-grab;
    cursor: grab;
    min-height: 100%;
}

.kanban-todos .todolist .title .todolist-rest {
    font-family: Hack Nerd font;
}

.kanban-todos .todolist {
    border: 3px;
    background-color: #dfe3e6;
}

.kanban-todos .todolist .title {
    box-sizing: border-box;
    width: 100%;
    height: 40px;
    margin-bottom: 0;
    padding: 15px;
    /*border-bottom: 1px solid rgba(151, 151, 151, .1);*/
}


.kanban-todos .todolist .todo-detail .label {
    border-radius: 10px;
}

.kanban-todos .todolists .todolist .title i.twr-bars:before {
    content: '\f019';
}

.kanban-todos .todolists .todolist {
    background-color: #dfe3e6 !important;
}

.kanban-todos .todos-container .todo {
    padding: 10px 4px !important;
    background-color: #fff;
    border-radius: 3px;
    box-shadow: 0 1px 0 rgba(9, 45, 66, .25);
    margin: 8px 1px !important;
    height: 100%;
}

.kanban-todos .todolist .todo .todo-content {
    font-size: 13px;
}

.kanban-todos .todos.todos-uncompleted {
    margin-top: 4px;
}

.kanban-todos .todo.completed {
    background-color: #F7F7F7;
}

.kanban-todos .todolist .todo .todo-wrap {
    line-height: inherit !important;
}

/* 新建卡片 */
.todo-form.new {
    padding: 10px !important;
    background-color: white;
    border-radius: 1px;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
    margin: 0 8px 8px !important;
}

.todo-form.new .simple-checkbox.disabled {
    display: none;
}

/* 全屏状态新建卡片 */
.todolist-footer>.todo-new-wrap>.todo-form.new {
    margin: 0 !important;
}

/* 标题标签 */
.tag {
    background-color: #34A853 !important;
}

/* 指派人和评论标签 */
.todo-detail .label {
    color: #FAFAFA !important;
    background-color: rgba(21, 57, 81, .8) !important;
}

.kanban-todos .todolist .title .btn-new-todo,
.kanban-todos .todolist .title .link-kanban-menu {
    font-size: 15px;
}

.kanban-todos .todolist .todo.completed .completed-member {
    padding: 3px 7px;
    background-color: rgba(51, 121, 1, .8) !important;
    border-radius: 10px;
}

/* 设置滚动条的样式 */
.kanban-todos::-webkit-scrollbar,
.todos-container::-webkit-scrollbar {
    width: 6px;
    height: 8px;
}

/* 滚动槽 */
.kanban-todos::-webkit-scrollbar-track,
.todos-container::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 2px rgba(0, 0, 0, 0.3);
    border-radius: 1px;
}

/* 滚动条滑块
.kanban-todos::-webkit-scrollbar-thumb:horizontal,
.todos-container::-webkit-scrollbar-thumb {
    border-radius: 1px;
    background: #9BCE37;
    -webkit-box-shadow: inset 0 0 2px rgba(0, 0, 0, 0.5);
}
*/


.check-item .check-item-name {
    font-size: 14px;
}

/* 详情页面 */

#page-todo .topic {
    margin: 20px 0 30px;
    padding-bottom: 0;
    width: 700px;
    border-bottom: 0px solid #ddd;
}

div.comments.streams,
div.subtodo {
    border-top: 1px solid rgba(0, 0, 0, 0.1);
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    border-bottom-left-radius: 3px;
    border-bottom-right-radius: 3px;
    border-bottom: 1px solid rgba(0, 0, 0, 0.1);
    border-left: 1px solid rgba(0, 0, 0, 0.1);
    border-right: 1px solid rgba(0, 0, 0, 0.1);
}

#subtasks .task {
    border-bottom: 1px solid rgba(0, 0, 0, 0.05);
}

/* rewrite font color to black */
p,
#page-project-settings .select-section .project-radio,
.event-action {
    color: rgba(1, 1, 1, .8);
}
