/* ==UserStyle==
@name          Zaki's GitLab Venom (Dark)
@namespace     github.com/openstyles/stylus
@version       0.2.1
@description   A dark userstyle for gitlab, with some green tint.
@author        Zaki
==/UserStyle== */

@-moz-document domain('gitlab.com')
{
	:root {
		--col1: #202020;
		--col2: #141414;
		--font1: #c2c2c2;
		--font2: #6ab90b;
		--font3: #54821c;
		
		--blue: #007bff;
		--indigo: #6610f2;
		--purple: #6f42c1;
		--pink: #e83e8c;
		--red: #dc3545;
		--orange: #fd7e14;
		--yellow: #ffc107;
		--green: #28a745;
		--teal: #20c997;
		--cyan: #17a2b8;
		--white: #fff;
		--gray: #6c757d;
		--gray-dark: #343a40;
		--primary: #007bff;
		--secondary: #fafafa;
		--success: #1aaa55;
		--info: #1f78d1;
		--warning: #fc9403;
		--danger: #db3b21;
		--light: #f8f9fa;
		--dark: #343a40;
		
		--breakpoint-xs: 0;
		--breakpoint-sm: 576px;
		--breakpoint-md: 768px;
		--breakpoint-lg: 992px;
		--breakpoint-xl: 1200px;
		
		--font-family-sans-serif: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen-Sans, Ubuntu, Cantarell, "Helvetica Neue", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
		--font-family-monospace: "Menlo", "DejaVu Sans Mono", "Liberation Mono", "Consolas", "Ubuntu Mono", "Courier New", "andale mono", "lucida console", monospace;
	}
	
	/* p much everything */
	html, body, .cover-block, .cover-block .cover-desc, .navbar-gitlab, .projects-list > li, .text-plain, .text-plain:hover, .avatar:not([href]):hover, .avatar-container .avatar, .nav-links:not(.quick-links), .content-block, .slead, .avatar, body.ui-dark .navbar-gitlab, .nav-block.activities, .modal-content, .modal-header, .modal-body, .modal-footer, .close, .row-content-block, table {
		background: var(--col1);
		background-color: var(--col1);
		color: var(--font1);
		
		border: none;
		border-bottom: none;
		border-top: none;
		border-left: none;
		border-right: none;
		text-shadow: none;
	}
	
	/* title things */
	.cover-block .cover-title, h1, h2, h3, h4, h5, h6, .page-title-holder .page-title, .page-title-holder, .top-area, .wiki-page-header {
		background: var(--col1);
		background-color: var(--col1);
		color: var(--font2);
		
		border: none;
		border-bottom: none;
	}
	
	/* dropdowns and all that jazz */
	.dropdown-menu, .dropdown-menu-nav, .dropdown-menu li a, .form-control:focus, .form-control, .dropdown-menu, .dropdown-menu-nav, .frequent-items-dropdown-container .frequent-items-dropdown-sidebar, .identicon.bg1, .identicon.bg2, .identicon.bg3, .identicon.bg4, .identicon.bg5, .identicon.bg6, .identicon.bg7, .identicon.bg8, .dropdown-menu-toggle, .header-user.show .dropdown-menu, .dropdown-menu li button, .dropdown-menu li .menu-item, .dropdown-toggle, .dropdown-toggle:hover {
		background: var(--col2);
		background-color: var(--col2);
		color: var(--font1);
		
		border: none;
	}
	
	/* dropdown when lit */
	.dropdown-menu li a:not(.disable-hover):hover, .btn, .dropdown-menu .divider, .dropdown-menu-nav .divider, .dropdown-menu li button:not(.disable-hover):hover, .dropdown-menu li .menu-item:not(.disable-hover):hover {
		background: var(--col1);
		background-color: var(--col1);
		color: var(--font2);
		
		border-color: var(--col1);
	}
	
	/* event items, lists */
	.event-item, .event-item .event-title, .event-item .event_commits li.commit .commit-row-title {
		background: var(--col1);
		background-color: var(--col1);
		color: var(--font1);
		
		border: none;
		border-bottom: none;
	}
	
	/* event item links */
	.event-item a, a:hover, ul.content-list li a {
		color: var(--font3);
	}
	
	/* random images */
	.svg-content svg, .svg-content img {
		display: none;
	}
}
