css:
@-moz-document domain("uv.ulb.ac.be") {
#page-header { display:none }
}

@-moz-document url("https://uv.ulb.ac.be/my/index.php"), url("https://uv.ulb.ac.be/my/") {
.coursebox { padding: 0 !important }
.header { display: none }
.ui-tabs-anchor { padding: 0 10px !important; }
.ui-tabs-panel { padding: 5px 5px 5px 15px !important; }
#page-content { padding: 0 !important; }
}

@-moz-document url-prefix("https://uv.ulb.ac.be/user/index.php") {
.initialbar .letter { display: inline; margin-left: 0px; padding-left: 5px; padding-right: 5px; }
.initialbar .initialbarall { margin-right: 5px }
[id^=form_autocomplete_input] { width: 400px; }
[id^=enrolusersbutton] .btn { margin: 0 !important; }
[id=participantsform] .mdl-right { text-align: left !important; }
}

@-moz-document url-prefix("https://uv.ulb.ac.be/mod/forum/view.php") {
.lastpost a { margin-right: 5px; }
.lastpost br { display: none }
}

@-moz-document url-prefix("https://uv.ulb.ac.be/mod/forum/discuss.php") {
#forummenu { text-align: left; }
#forummenu select, #forummenu input { display: inline-block; float: none }
#forummenu input { margin-top: 10px; margin-left: 10px; }
.discussioncontrol.movediscussion { float: none; }
.discussioncontrol.pindiscussion { text-align: left !important; margin-top: 10px !important; }
.discussioncontrol.exporttoportfolio.noavailable { display: none !important; }
}
