css:
@-moz-document domain("aplikace.skolaonline.cz") {
body footer {
    background-color: dimgray;
    
}

body footer .contact {
    color: #f0f0f0;
}

body footer .dev_info td, body footer .dev_info td a {
    color: #f0f0f0;
}

body .left_frame_area_header {
	color: #0a026b !important;
	background-color: #e0e0e0;
	border: 1px solid #d3c6f3;
}

body header .row:nth-child(1) .menu .ornamental_image {
    background-image: none;
}

body header .row:nth-child(1) .logo a {
    background-image: url('https://mongo.alastify.cz/img/skolaonline-logo.png');
}

body header .row:nth-child(1) {
    background-color: white;
}

body header .row:nth-child(1) .account {
    background-color: white;
}

body header .row:nth-child(1) .account .info .logout {
    background-image: none;
}

body header .row:nth-child(1) .account {
    border-left-color: #a0a0a0;
}

body header .row:nth-child(1) .account .info {
    border-color: #a0a0a0;
}

body header .row:nth-child(2) {
	background: #505050;
}

body header .row:nth-child(2) nav div span ul li a {
    background-color: #505050;
}

body header .row:nth-child(2) nav div span ul li a:hover {
    background-color: #f0f0f0 !important;
    color: black;
}

body .content_frame_area_header {
    color: #505050;
}

body header .row:nth-child(2) .MenuButtons {
    display: none !important;
}

body .left_frame_area_header {
    color: #202020 !important;
}

#ctl00_main_lblZivotniSituace {
    display: none;
}

#ctl00_main_pnlContent {
	display: none;
}

body header .row:nth-child(1) .account .info .logout {
    color: white;
}

.LeftPartZivotniSituace {
    display: None;   
}
}
