css:
@-moz-document domain(spotify.com) {
	.main-view-container__content{
	background:linear-gradient(to bottom, rgb(35,35,35), rgb(40,40,40));
    min-height: calc(100vh - 91px);
}
.navBar{
	background: linear-gradient(to bottom, rgb(20,20,20), rgb(30,30,30));
    border-right: 1px solid #1E1E1E;
    border-right: 1px solid rgba(0,0,0,0.2);
}
.tracklist-row{
    padding:0;
    margin:3px;
    line-height:80%;
    height:38px;
}
.tracklist-row:hover{
    background: linear-gradient(to left, rgba(0,0,0,0.4), rgba(0,0,0,0.2));
    -webkit-border-radius: 8px 8px 8px 8px;
	border-radius: 8px 8px 8px 8px;
}
.tracklist-name{
    padding:0;
    margin:0;
	font-size:0.9em;
}
.second-line{
    padding:0;
    margin:0;
	font-size:0.8em;
}

.tracklist-col-duration{
	font-size:0.8em;
    line-height:38px;
}
.tracklist-top-align{
    padding:0;
    margin:0;
}
.spoticon-track-16{
    line-height:20px;
}
.tracklist-play-pause{
    line-height:35px;
}
.more{
    line-height:35px;
}
.now-playing-bar-container{
	border-top: 1px solid #181818;
    border-top: 1px solid rgba(0,0,0,0.4);
    -moz-box-shadow: 0 -10px 20px -10px rgba(0,0,0,0.5);
    -webkit-box-shadow: 0 -10px 20px -10px rgba(0,0,0,0.5);
	box-shadow: 0 -10px 20px -10px rgba(0,0,0,0.5);
    background:rgb(40,40,40);
}
.col-xl-4{
	width:15%;
}
.col-xl-8{
    width:85%;
}
.cover-art-image{
    -webkit-border-radius: 5px 5px 5px 5px;
	border-radius: 5px 5px 5px 5px;
}
.cover-art{
    -webkit-border-radius: 5px 5px 5px 5px;
	border-radius: 5px 5px 5px 5px;
}
}
