/* всплывающая плашка результата действий влево вверх */
.top_result_baloon:not([style*="width:470px;"]){position:fixed;top:54px;left:10px;box-shadow:0px 2px 15px var(--color2);color:#CCC;opacity:.8}

/* всплывающая плашка сохранения и перемещения картинки вниз поближе к кнопке "сохранить" */
.top_result_baloon[style*="width:470px;"]{box-shadow:0px 0px 1px 0px var(--color3);color:#999;position:fixed;bottom:7%}
.top_result_header{color:#AAA}
.top_result_baloon[style*="width:470px;"] a{color:var(--color2)}
    
/* фильтр вк-шных картинок */
