/* фикс календаря */ 
.cal_table>thead{border:0 solid var(--color3);background:transparent!important;border-width:1px 1px 0}
.cal_table>tbody{border:0 solid var(--color1);border-width:0 1px 1px;background-color:var(--color1)}
.cal_table .day{background-color:#2b2b2b}
.cal_table .day:hover{background:var(--color3)!important;color:#ccc!important}
.cal_table .next_month_day, .cal_table .prev_month_day{background-color:#1f1f1f}
.medadd_c_timer .cal_table .day.sel{background-color:var(--color1)!important}
.medadd_c_timer .cal_table .day.sel:hover{color:#fff!important}
.cal_table .month .year{color:var(--color2)}
.cal_table .today{box-shadow:inset 0px 0px 0px 1px var(--color6);color:var(--color1)!important}
.cal_clear{border-top:none;background:#1f1f1f}
.im_cal_clear_lnk{color:#aaa}
.cal_clear:hover{background:#111}
.cal_clear:hover .im_cal_clear_lnk{color:var(--color1)}

/* клипы */
[dir] .ShortVideoPage .page_block_h2,[dir] .ShortVideoPost,[dir] .ShortVideoPage__container--empty{box-shadow:0 1px 0 0 var(--color3),0 0 0 1px var(--color3)}
[dir] .ShortVideoPost,[dir] .ShortVideoPage__container--empty{background:rgba(0,0,0,.75)}
[dir] .ShortVideoPost .ShortVideoDescriptionText__moreToggle{color:var(--color2)}
[dir] .ShortVideoPost .ShortVideoDescriptionText__moreToggle:hover{color:var(--color1)}
[dir] .ShortVideoStub__title{color:var(--color2);transition:color .5s}
[dir] .ShortVideoStub__text{color:#999;transition:color .5s}
[dir] .ShortVideoStub:hover .ShortVideoStub__title{color:var(--color1)}
[dir] .ShortVideoStub:hover .ShortVideoStub__text{color:#fff}
[dir] .ShortVideoStub__link--iphone,[dir] .ShortVideoStub__link--android,[dir] .ShortVideoStub__qrLink{filter:invert(1)hue-rotate(180deg)var(--filt2)saturate(.1)brightness(.7)}
[dir] .ShortVideoStub__link--iphone:hover,[dir] .ShortVideoStub__link--android:hover,[dir] .ShortVideoStub__qrLink:hover{filter:invert(1)hue-rotate(180deg)var(--filt2)saturate(.7)}
[dir] .ShortVideoStub__qrLink{border:1px solid var(--color2)}
[dir] .ShortVideoStub__qrLink:hover{border:1px solid #5181b8}

[dir] .VerticalVideoLayerInfo div.submit_post_field,[dir] .VerticalVideoLayerInfo div.submit_post_field[contenteditable="true"],[dir] .VerticalVideoLayerInfo div.submit_post_field ~ .placeholder .ph_input, [dir] .VerticalVideoLayerInfo div.submit_post_field[contenteditable="true"] ~ .placeholder .ph_input{padding:3px 0 0 10px !important}
[dir] .VerticalVideoLayerInfo .ms_items_more_wrap.ms_items_more_wrap_vector .ms_item_more_label{margin-top:0px}
[dir] .VerticalVideoLayerInfo .wall_module .reply_form{height:28px!important}
[dir] .VerticalVideoLayerInfo .VerticalVideoLayerInfo__commentsReply > div:not(.reply_box_open) .reply_form,[dir] .VerticalVideoLayerInfo .VerticalVideoLayerInfo__commentsReply > div:not(.reply_box_open) .reply_field{height:28px!important}
[dir] .VerticalVideoLayerInfo .ui_scroll_outer{background:var(--color3)}
[dir] .VerticalVideoLayerInfo .flat_button.addpost_button{color:#aaa}
[dir] .VerticalVideoLayerInfo .flat_button.addpost_button:hover{color:#000}
[dir] .VerticalVideoLayerInfo .flat_button.addpost_button.reply_send_disabled{color:#666!important}
[dir] .VerticalVideoLayerInfo .flat_button.addpost_button.reply_send_disabled:hover{color:#444!important}
[dir] .VerticalVideoLayerInfo .like_button_count{color:var(--color2)!important}
[dir] .VerticalVideoLayerInfo .like_wrap .ui_actions_menu_more{color:#4e6e93}
[dir] .VerticalVideoLayerInfo .active .like_button_count,[dir] .VerticalVideoLayerInfo__authorName{color:var(--color1)!important}
[dir] .VerticalVideoLayerInfo .VideoLayerInfo__date,[dir] .VerticalVideoLayerInfo .VerticalVideoLayerInfo__date,[dir] .VerticalVideoLayerInfo{color:#999}
[dir] .VerticalVideoLayerInfo .audio_row .audio_row__title{color:#ccc}
[dir] .VerticalVideoLayerInfo .audio_row .audio_row__performers a{color:var(--color1)!important}

[dir="ltr"] .VerticalVideoLayerInfo .ui_scroll_default_theme.ui_scroll_emoji_theme > .ui_scroll_overflow > .ui_scroll_shadow_bottom,[dir="ltr"] .VerticalVideoLayerInfo .ui_scroll_default_theme.ui_scroll_emoji_theme > .ui_scroll_overflow > .ui_scroll_shadow_top,[dir="ltr"] .VerticalVideoLayerInfo .emoji_cats_title_helper::after{background:linear-gradient(180deg,#fff 0%,#ffffff00 100%)}
[dir] .VerticalVideoLayerInfo .emoji_tt_wrap{background:rgba(0,0,0,.75)!important}
[dir] .VerticalVideoLayerInfo .ui_scroll_outer{background:none!important}
}
