@-moz-document url-prefix("https://sa.duncraigshs.wa.edu.au/") {

/* Main */
#schoolLogo {
	display: none;    
}
.notifications__actions___1UX7r {
	display: none;    
}
.notifications__item___2ErJN>.notifications__dismiss___zveKV {
	display: none;
}
.notifications__notifications___3mmLY.notifications__shown___1kPoT>button {
	right: 8px!important;
}
.notifications__notifications___3mmLY.notifications__hasItems___gXxzx>button {
    right: 8px!important;
}
#menu .nav>.back>.arrow {
    color: #000;
}
#menu li {
    padding-top: 6px;
	padding-bottom: 6px;
    border-left: solid rgba(0, 0, 0, 0)!important;
}
#title {
	background-color: #fff!important;
}
.nav {
	background-color: #fff!important;
}
#title>span {
    color: #000;
}
#menu .nav>.back>.backLabel {
    color: #000;
}
.notifications__notifications___3mmLY>button>.notifications__bubble___1EkSQ {
    text-shadow: none;
}
#main>.settings>.theme>ul>li.dark-night>.preview {
    background-color: #1b315e;
}
@keyframes notifications__notifications-glow___3UZCz {
    0% {
    	background: #f00;
        color: #fff;
	}
    50% {
		background: #fdd;
        color: #fff;
    }
	100% {
    	background: #f00;
        color: #fff;
	}
}
.notifications__notifications___3mmLY>button>.notifications__bubble___1EkSQ {
	animation-direction: normal;
    animation-timing-function: ease-in-out;
}
#menu ul {
    top: 0;
}
.nav {
    background: none!important;
}
#menuToggle {
    background: #fff;
}
#menuToggle>img {
    filter: invert(100%);
}
.menuShown #menuToggle {
    background: #fff;
}
#main>.settings>.password, #main>.settings>.apps {
    display: none;
}
.modaliser.visible {
    filter: blur(1px);
}
#main>.settings>.theme>ul>.none>.label {
    font-style: italic;
}
#main>.settings>.theme>ul>.yellow>.label {
    font-style: italic;
}
#main>.settings>.theme>ul>.dark-night>.label {
    font-style: italic;
}
#main>.settings>.theme>ul>.fuzz>.label {
    font-style: italic;
}
#main>.settings>.theme>ul>.luminesce>.label {
    font-style: italic;
}
#userActions>.feedbackButton>img {
    visibility: hidden;
}
#userActions>.feedbackButton {
    background: url(https://sa.duncraigshs.wa.edu.au/js/icons/16/welcome.png) center no-repeat;
}
.uiSlidePane>.pane>.header>.alpha, .uiSlidePane>.pane>.header>h1 {
    display: none;
}
.uiSlidePane>.pane>.content>.feedback {
    visibility: hidden;
    margin-top: 0;
}
.uiSlidePane>.pane>.content {
	background: url(https://lh4.googleusercontent.com/B0hsXiSjI-PDx4DmB4owFxGKYQ5U0tTKOnJL58NKlMb79Rpqd9Z2pH4I17JrITzqfYgad6p60xAG9vV26yXfbYb66cwZ5xsaOIL9R5n1LVFlx2-etmg=w773) center no-repeat;
}
    
/* Bumble Bee */
[data-theme=yellow] #title {
	background-color: #fce94f!important;
}
[data-theme=yellow] #userActions {
	background-color: #d9c62c;
}
[data-theme=yellow] #userActions>button {
	background-color: #c9b61c;
    border: 1px solid #d9c62c;
    box-shadow: none;
}
[data-theme=yellow] .nav {
	background-color: #fce94f!important;
}
[data-theme=yellow] #userActions>div>div {
	color: #000;
}

/* Dark Night */
[data-theme=dark-night] #title {
	background-color: #1b315e!important;
}
[data-theme=dark-night] #menu ul {
	background-color: #1b315e;
}
[data-theme=dark-night] #userActions {
	background-color: #1f2a48;
}
[data-theme=dark-night] #userActions>div>div {
	color: #fff;
}
[data-theme=dark-night] #userActions>button {
	background-color: #1b315e;
    border: 1px solid #1f2a48;
    box-shadow: none;
}
[data-theme=dark-night] .nav {
	background-color: #1b315e!important;
}
[data-theme=dark-night] #title>span {
    color: #fff;
}
[data-theme=dark-night] #menu .nav>.back>.arrow {
    color: #fff;
}
[data-theme=dark-night] #menu .nav>.back>.backLabel {
    color: #fff;
}

/* Fuzz */
[data-theme=fuzz] .nav {
    background: none!important;
}
[data-theme=fuzz] #menu ul {
    animation-duration: 60s;
}
#main>.settings>.theme>ul>li.fuzz>.preview {
    animation: theme-fuzz infinite 25s ease-in-out;
    background-image: url(https://sa.duncraigshs.wa.edu.au/images/backgrounds/fuzz-1.png),url(https://sa.duncraigshs.wa.edu.au/images/backgrounds/fuzz-2.png),url(https://sa.duncraigshs.wa.edu.au/images/backgrounds/fuzz-3.png);
}

/* Luminesce */
[data-theme=luminesce] #title {
    animation: theme-luminesce infinite 49s ease-in-out;
}
[data-theme=luminesce] .nav {
    animation: theme-luminesce infinite 49s ease-in-out;
}
[data-theme=luminesce] .nav {
    animation: theme-luminesce infinite 49s ease-in-out;
}
[data-theme=luminesce] #menu ul, #main>.settings>.theme>ul>li.luminesce>.preview {
    animation: theme-luminesce infinite 49s ease-in-out;
}
[data-theme=luminesce] #userActions {
	animation: theme-luminesce infinite 49s ease-in-out;
}
[data-theme=luminesce] #userActions>button {
	animation: theme-luminesce infinite 49s ease-in-out;
    filter: brightness(0.9);
    border: none;
    box-shadow: none;
}
[data-theme=luminesce] #userActions>div>div {
	color: #fff;
}
[data-theme=luminesce] #title>span {
    color: #fff;
}
[data-theme=luminesce] #menu .nav>.back>.arrow {
    color: #fff;
}
[data-theme=luminesce] #menu .nav>.back>.backLabel {
    color: #fff;
}
@keyframes chroma {
    0% {
    	background-image: linear-gradient(to bottom right, #204a87,#5c3566,#c00,#ce5c00,#c4a000,#4e9a06);
    	border-image: linear-gradient(to bottom right, #204a87,#5c3566,#c00,#ce5c00,#c4a000,#4e9a06);
	}
	16.67% {
    	background-image: linear-gradient(to bottom right, #4e9a06,#204a87,#5c3566,#c00,#ce5c00,#c4a000);
    	border-image: linear-gradient(to bottom right, #4e9a06,#204a87,#5c3566,#c00,#ce5c00,#c4a000);
	}
	33.33% {
    	background-image: linear-gradient(to bottom right, #c4a000,#4e9a06,#204a87,#5c3566,#c00,#ce5c00);
    	border-image: linear-gradient(to bottom right, #c4a000,#4e9a06,#204a87,#5c3566,#c00,#ce5c00);
	}
	50% {
    	background-image: linear-gradient(to bottom right, #ce5c00,#c4a000,#4e9a06,#204a87,#5c3566,#c00);
    	border-image: linear-gradient(to bottom right, #ce5c00,#c4a000,#4e9a06,#204a87,#5c3566,#c00);
	}
	66.67% {
    	background-image: linear-gradient(to bottom right, #c00,#ce5c00,#c4a000,#4e9a06,#204a87,#5c3566);
    	border-image: linear-gradient(to bottom right, #c00,#ce5c00,#c4a000,#4e9a06,#204a87,#5c3566);
	}
	83.33% {
    	background-image: linear-gradient(to bottom right, #5c3566,#c00,#ce5c00,#c4a000,#4e9a06,#204a87);
    	border-image: linear-gradient(to bottom right, #5c3566,#c00,#ce5c00,#c4a000,#4e9a06,#204a87);
	}
	100% {
    	background-image: linear-gradient(to bottom right, #204a87,#5c3566,#c00,#ce5c00,#c4a000,#4e9a06);
    	border-image: linear-gradient(to bottom right, #204a87,#5c3566,#c00,#ce5c00,#c4a000,#4e9a06);
	}
}

}
