@-moz-document url-prefix(http://androdome.com) {
.wlink {
    color: #00f586;
    text-decoration: none;
}
.topbar {
    border: #000000 1px solid;
    background-image: url(https://t5.rbxcdn.com/c9749ac59c201199fc5ec1610dcf7d52);
    height: 140;
    text-align: left;
    vertical-align: bottom;
}
a {
    text-decoration: none;
    color: #2eb31f;
}
.sectionbody {
    font-size: 12px;
    font-weight: lighter;
    background-color: #00ff28;
}
.style {
    background-color: #23f307;
    padding: 10px;
}
.imgcontainer {
    background-color: #06f90b;
