    border-bottom: 1px solid black;
    text-align: center;
}
.showcasehead {
    background-color: #ff0000;
    border-bottom: 1px solid black;
}
.title {
    background-color: #ff0000;
    border-bottom: black 1px solid;
    width: 100%;
    letter-spacing: 0.1em;
    font: bold 0.8em/normal Verdana, sans-serif;
}
.sectionhead {
    font-size: 12px;
    font-weight: bold;
    border-bottom: black 1px solid;
    width: 100%;
    background-color: #ff0000;
}
.online {
    color: #0ae879;
}
.offline {
    color: #fd0909;
}
.tablehead {
    font-size: 20px;
