    border-bottom: black 1px solid;
    width: 100%;
    background-color: #ff0000;
    color: #2ff50d;
}
.tablebody {
    font-size: 12px;
    font-weight: lighter;
    font-family: Verdana;
    padding: 10px;
    background-color: #16e21a;
}
.textc {
    border: 1px dashed #555555;
    background-color: #2efb05;
    width: 40%;
    padding: 10px;
    float: right;
}
.playbar {
    background-color: #046afb;
    font-family: Verdana;
    border: 1px dashed green;
    text-align: center;
    padding: 3px;
}
.tablehead {
    font-size: 20px;
    font-weight: bold;
    border-bottom: black 1px solid;
    width: 100%;
    background-color: #ff0000;
    color: #2ff50d;
}
.tabcontent {
    background-color: #4dff00;
    border: 1px solid #555555;
    border-top: none;
    color: #333333;
}
.btn {
    background-color: #00ff6f;
    border: 1px solid #777777;
    color: #777777;
    font-family: Verdana;
    font-size: 12px;
}
.favbar {
    background-color: #0426ff;
    font-family: Verdana;
    border: 1px dashed black;
    border-top: none;
    text-align: center;
    padding: 3px;
}
.comments {
    font-family: Verdana;
    font-size: 10px;
    text-align: justify;
    margin: 10px;
    border: 1px dashed #333333;
    background-color: #00ff28;
}
#topbar {
    border: #000000 1px solid;
    background-image: url('https://cdn.discordapp.com/attachments/505132001758806045/512440341304049664/sssss.png');
    height: 140;
    text-align: left;
    vertical-align: bottom;
}
#absolute {
    width: 450px;
    /* display: block; */
    /* top: 1px; */
}
.bluebar {
    background-color: #fb0202;
    color: white;
    font-size: 25px;
}



element.style {
    width: 100%;
    border: solid 1px black;
    background-color: #ff0606;
    padding: 5px;
    font-family: Verdana;
    font-weight: light;
}


img.fullbackground {
    margin: 0 auto;
    position: absolute;
    z-index: 0;
    top: 0;
    left: 0;
    margin: 0;
    padding: 0;
    width: 0;
}
body {
background-image: url(https://d2v9y0dukr6mq2.cloudfront.net/video/thumbnail/JrbItQz/christmas-candy-canes-loopable-background_e1htdoexl__F0000.png)
}
.selectbtn {
    background-color: white;
    border: 1px solid #000000;
    font-family: Verdana;
    width: 90%;
    padding: 2px;
    font-size: 16px;
    margin: 10px;
    background-color: #ff0000;
    color: white;
}
#announcement {
    background-color: #ff0000;
    color: #00ff0a;
    width: 100%;
    border: black 1px solid;
    font-size: 20px;
    text-align: center;
}
