css:
@-moz-document domain("www.androidauthority.com") {
/*backgrounds & general some text color*/
/*dark-dark*/
body, .hot-products-menu-content, .tablepress tbody TR TD.column-1, .tablepress tbody TR TD.column-2, .tablepress tbody TR TD.column-3, .tablepress tbody tr.even td:not(.column-1), #dgit-form-email {
    background-color: #292929 !important;
    color: #d5d5d5;
    background-image: none;
    background: #292929 !important;
}
/*light-dark*/
#tt_hot_products_menu, .loop-info-wrapper, #footer, .copyright, .procon-box-wrapper, .procon-box .section-title, .ratings, .tablepress TR TH, .tablepress tbody tr.odd td:not(.column-1), .wp-caption, #dgit-daily-form, .dgit-info-container-inner {
    background-color: #2f2f2f !important;
    color: #d5d5d5;
    background-image: none;
}
/*Gradients*/
/*general use gradient*/
#sticky-bar, .aa-sticky-search-wrapper, .aa_button, .aa_cat_name_block, .load-more, #sticky-inner.visible, label[for="dgit-form-submit"], .go-to-dgit-button {
    background: linear-gradient(to right,#05c9aa 0,#64bc06 100%);
}
/*comments show more gradient*/
.show-disqus-thread {
    background: linear-gradient(to top, #292929 ,#08e73c8a , #3d6d0a0d );
    color:#d5d5d5;
}

/*text color*/
/*general*/
#section-menu > ul > li > a, h1.main-title, ul, li, p, a:hover, h2, h3, h4, .authorinfo a.author-name, .billboard-wrapper h1.main-title, .billboard-wrapper .post-byline, .device_name, .device_manufacturer, .ratings .section-title, .dgit-form-description, .privacy-policy-desc, .excerpt {
    color: #d5d5d5 !important;
}
/*Titles*/
.loop-panel h2.article-title a {
    background-color: transparent !important;
    color: #d5d5d5;
    background-image: none;
}
/*Green Text*/
cite {
    color: #39d266;
}

.aa_cat_name_block {
    border-radius: 10px;
    padding: 3px;
}
}
